                    <span id="value-bx" class="value-display"></span>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-by">y direction (BY)</label>
                <div class="slider-container">
                    <input type="range" id="slider-by" min="0" max="2" step="0.1" value="0">
                    <span id="value-by" class="value-display"></span>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-bz">z direction (BZ)</label>
                <div class="slider-container">
                    <input type="range" id="slider-bz" min="0" max="2" step="0.1" value="0">
                    <span id="value-bz" class="value-display"></span>
                </div>
            </div>
        </div>
        <div id="plot-container"></div>
