    <script>
        window.onload = function() {
            // Get references to all interactive DOM elements
            const sliders = {
                wso: document.getElementById('slider-wso'),
                n: document.getElementById('slider-n'),
                bx: document.getElementById('slider-bx'),
                by: document.getElementById('slider-by'),
                bz: document.getElementById('slider-bz')
            };

            const valueDisplays = {
                wso: document.getElementById('value-wso'),
                n: document.getElementById('value-n'),
                bx: document.getElementById('value-bx'),
                by: document.getElementById('value-by'),
                bz: document.getElementById('value-bz')
            };

            // Attach event listeners to all sliders to trigger plot updates
            for (const key in sliders) {
                sliders[key].addEventListener('input', updatePlot);
            }

            // Initial plot generation on page load
            updatePlot();

            function generateTitle(n, wso, BX, BY, BZ) {
                const parts = [`n = ${n}`];
                const b_parts = [];

                if (BX !== 0) b_parts.push(`BX = ${BX.toFixed(1)}`);
                if (BY !== 0) b_parts.push(`BY = ${BY.toFixed(1)}`);
                if (BZ !== 0) b_parts.push(`BZ = ${BZ.toFixed(1)}`);

                if (b_parts.length > 0) {
                    parts.push(...b_parts);
                } else {
                    parts.push(`BX = ${BX.toFixed(1)}`);
                }

                parts.push(`wso = ${wso.toFixed(1)}`);

                return parts.join(', ');
            }

            function updatePlot() {
                // 1. Read current values from sliders
                const wso = parseFloat(sliders.wso.value);
                const n = parseInt(sliders.n.value);
                const BX = parseFloat(sliders.bx.value);
                const BY = parseFloat(sliders.by.value);
                const BZ = parseFloat(sliders.bz.value);

                // Update the text content of the span elements next to sliders
                valueDisplays.wso.textContent = wso.toFixed(1);
                valueDisplays.n.textContent = n;
                valueDisplays.bx.textContent = BX.toFixed(1);
                valueDisplays.by.textContent = BY.toFixed(1);
                valueDisplays.bz.textContent = BZ.toFixed(1);

                // 2. Calculate energy bands
                const k_vals = [];
                const E_plus = [];
                const E_minus = [];
                const k_min = -5;
                const k_max = 5;
                const k_step = 0.1;

                for (let k = k_min; k <= k_max; k += k_step) {
                    k_vals.push(k);

                    const common_term = (2 * n + 1) + Math.pow(k, 2);
                    const sqrt_inner_term = Math.pow(BZ + wso * k, 2) + Math.pow(BX, 2) + Math.pow(BY, 2);
                    const sqrt_term = Math.sqrt(sqrt_inner_term);

                    E_plus.push(common_term + sqrt_term);
                    E_minus.push(common_term - sqrt_term);
                }

                // 3. Prepare data traces for Plotly
                const data = [];
                const isDegenerate = (wso === 0 && BX === 0 && BY === 0 && BZ === 0);

                if (isDegenerate) {
                    // If no interactions, plot a single degenerate band
                    data.push({
                        x: k_vals,
                        y: E_plus,
                        mode: 'lines',
                        line: { color: 'red', dash: 'solid', width: 2 }
                    });
                } else {
                    // Otherwise, plot both spin-split bands
                    data.push({
                        x: k_vals,
                        y: E_plus,
                        mode: 'lines',
                        line: { color: 'red', dash: 'solid', width: 2 },
                        name: 'E+'
                    });
                    data.push({
                        x: k_vals,
                        y: E_minus,
                        mode: 'lines',
                        line: { color: 'red', dash: 'dash', width: 2 },
                        name: 'E-'
                    });
                }

                // 4. Define plot layout, including the dynamic title
                const layout = {
                    title: generateTitle(n, wso, BX, BY, BZ),
                    xaxis: {
                        title: 'normalized k length (lwky)',
                        range: [-5, 5],
                        zeroline: true,
                        zerolinewidth: 1,
                        zerolinecolor: '#999'
                    },
                    yaxis: {
                        title: '2 E / &#x210F;&omega;',
                        range: [0, 40],
                        zeroline: true,
                        zerolinewidth: 1,
                        zerolinecolor: '#999'
                    },
                    showlegend: false,
                    margin: { t: 50, b: 50, l: 70, r: 20 },
                    font: {
                        family: 'Arial, sans-serif'
                    }
                };

                // 5. Update the plot
                Plotly.react('plot-container', data, layout);
            }
        };
    </script>
</body>
</html>
