                <h4>precipitation</h4>
                <div class="slider-group">
                    <label for="slider-precip-1">hour 1</label>
                    <input type="range" id="slider-precip-1" min="0" max="10" step="1" value="1">
                    <span id="value-precip-1">1</span>
                </div>
                <div class="slider-group">
                    <label for="slider-precip-2">hour 2</label>
                    <input type="range" id="slider-precip-2" min="0" max="10" step="1" value="0">
                    <span id="value-precip-2">0</span>
                </div>
                <div class="slider-group">
                    <label for="slider-precip-3">hour 3</label>
                    <input type="range" id="slider-precip-3" min="0" max="10" step="1" value="0">
                    <span id="value-precip-3">0</span>
                </div>

                <h4>UH streamflow rates</h4>
                <div class="slider-group">
                    <label for="slider-uh-1">hour 1</label>
                    <input type="range" id="slider-uh-1" min="0" max="10" step="1" value="1">
                    <span id="value-uh-1">1</span>
                </div>
                <div class="slider-group">
                    <label for="slider-uh-2">hour 2</label>
                    <input type="range" id="slider-uh-2" min="0" max="10" step="1" value="5">
                    <span id="value-uh-2">5</span>
                </div>
                <div class="slider-group">
                    <label for="slider-uh-3">hour 3</label>
                    <input type="range" id="slider-uh-3" min="0" max="10" step="1" value="3">
                    <span id="value-uh-3">3</span>
                </div>
                <div class="slider-group">
                    <label for="slider-uh-4">hour 4</label>
                    <input type="range" id="slider-uh-4" min="0" max="10" step="1" value="1">
                    <span id="value-uh-4">1</span>
                </div>
            </div>
        </div>
        <div id="plots-container">
            <div id="plot-canvas-1" class="plot-canvas"></div>
            <div id="plot-canvas-2" class="plot-canvas"></div>
            <div id="plot-canvas-3" class="plot-canvas"></div>
            <div id="plot-canvas-4" class="plot-canvas"></div>
            <div id="plot-canvas-5" class="plot-canvas"></div>
            <div id="plot-canvas-6" class="plot-canvas"></div>
        </div>
    </div>

    <script>
        // Global variables for data and sketches
        let P, U, C1, C2, C3, Q;
        const sketches = [];

        document.addEventListener('DOMContentLoaded', () => {
            setupEventListeners();
            createSketches();
            update(); // Initial calculation and draw
        });

        function setupEventListeners() {
            // Slider listeners
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', (event) => {
                    const id = event.target.id;
                    const value = event.target.value;
                    document.getElementById(id.replace('slider', 'value')).textContent = value;
                    update();
                });
            });

            // Toggle button listener
            const toggleButton = document.getElementById('toggle-controls');
            const slidersArea = document.getElementById('sliders-area');
            const header = document.querySelector('.controls-header');

            const toggleAction = () => {
                 if (slidersArea.style.display === 'none') {
                    slidersArea.style.display = 'block';
                    toggleButton.textContent = '-';
                } else {
                    slidersArea.style.display = 'none';
                    toggleButton.textContent = '+';
                }
            };

            toggleButton.addEventListener('click', toggleAction);
            header.addEventListener('click', (e) => {
                if(e.target !== toggleButton) {
                   toggleAction();
                }
            });
        }

        function update() {
            // 1. Read values from sliders
            P = [
                parseInt(document.getElementById('slider-precip-1').value),
                parseInt(document.getElementById('slider-precip-2').value),
                parseInt(document.getElementById('slider-precip-3').value)
            ];
            U = [
                parseInt(document.getElementById('slider-uh-1').value),
                parseInt(document.getElementById('slider-uh-2').value),
                parseInt(document.getElementById('slider-uh-3').value),
                parseInt(document.getElementById('slider-uh-4').value)
            ];

            // 2. Calculate component hydrographs
            C1 = U.map(u => u * P[0]);
            C2 = U.map(u => u * P[1]);
            C3 = U.map(u => u * P[2]);

            // 3. Calculate final composite hydrograph (convolution)
            Q = Array(P.length + U.length - 1).fill(0);
            for (let i = 0; i < P.length; i++) {
                for (let j = 0; j < U.length; j++) {
                    Q[i + j] += P[i] * U[j];
                }
            }

            // 4. Redraw all p5 sketches
            sketches.forEach(sketch => sketch.redraw());
        }

        function createSketches() {
            const plotConfigs = [
                { id: 'plot-canvas-1', getData: () => P,  xLabel: 't (hr)', yLabel: 'P (in)',      xMax: 5, yMax: 10,  yTickStep: 2,  xTickStep: 1, timeOffset: 0 },
                { id: 'plot-canvas-2', getData: () => U,  xLabel: 't (hr)', yLabel: 'Q (cfs/in)',  xMax: 5, yMax: 10,  yTickStep: 2,  xTickStep: 1, timeOffset: 0 },
                { id: 'plot-canvas-3', getData: () => C1, xLabel: 't (hr)', yLabel: 'Q (cfs)',     xMax: 8, yMax: 100, yTickStep: 20, xTickStep: 2, timeOffset: 0 },
                { id: 'plot-canvas-4', getData: () => C2, xLabel: 't (hr)', yLabel: 'Q (cfs)',     xMax: 8, yMax: 100, yTickStep: 20, xTickStep: 2, timeOffset: 1 },
                { id: 'plot-canvas-5', getData: () => C3, xLabel: 't (hr)', yLabel: 'Q (cfs)',     xMax: 8, yMax: 100, yTickStep: 20, xTickStep: 2, timeOffset: 2 },
                { id: 'plot-canvas-6', getData: () => Q,  xLabel: 't (hr)', yLabel: 'Q (cfs)',     xMax: 8, yMax: 100, yTickStep: 20, xTickStep: 2, timeOffset: 0 }
            ];

            plotConfigs.forEach(config => {
                const sketchFunc = p => {
                    p.setup = () => {
                        const container = document.getElementById(config.id);
                        const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                        canvas.parent(config.id);
                        p.noLoop();
                    };

                    p.draw = () => {
                        const data = config.getData();
                        drawPlot(p, data, config);
                    };
                };
                sketches.push(new p5(sketchFunc));
            });
        }

        function drawPlot(p, data, config) {
            const { xLabel, yLabel, xMax, yMax, yTickStep, xTickStep, timeOffset } = config;

            p.background(255);
            const margin = { top: 20, right: 35, bottom: 45, left: 50 };
            const plotWidth = p.width - margin.left - margin.right;
            const plotHeight = p.height - margin.top - margin.bottom;

            const mapX = val => p.map(val, 0, xMax, margin.left, margin.left + plotWidth);
            const mapY = val => p.map(val, 0, yMax, margin.top + plotHeight, margin.top);

            // Axes
            p.stroke(0);
            p.strokeWeight(1);
            p.line(margin.left, margin.top, margin.left, margin.top + plotHeight); // Y-axis
            p.line(margin.left, margin.top + plotHeight, margin.left + plotWidth, margin.top + plotHeight); // X-axis

            // Ticks and Labels
            p.noStroke();
            p.fill(0);
            p.textSize(10);

            // Y-axis ticks (left)
            for (let i = 0; i <= yMax; i += yTickStep) {
                if (i > yMax) continue;
                const yPos = mapY(i);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(i, margin.left - 5, yPos);
                p.stroke(0);
                p.line(margin.left - 3, yPos, margin.left, yPos);
                p.noStroke();
            }

            // Y-axis ticks (right)
            for (let i = 0; i <= yMax; i += yTickStep) {
                if (i > yMax) continue;
                const yPos = mapY(i);
                p.textAlign(p.LEFT, p.CENTER);
                p.text(i, margin.left + plotWidth + 5, yPos);
                p.stroke(0);
                p.line(margin.left + plotWidth, yPos, margin.left + plotWidth + 3, yPos);
                p.noStroke();
            }

            // X-axis ticks
            for (let i = 0; i <= xMax; i += xTickStep) {
                const xPos = mapX(i);
                p.textAlign(p.CENTER, p.TOP);
                p.text(i, xPos, margin.top + plotHeight + 5);
                p.stroke(0);
                p.line(xPos, margin.top + plotHeight, xPos, margin.top + plotHeight + 3);
                p.noStroke();
            }

            // Axis Labels
            p.fill(0);
            p.textSize(12);
            p.push();
            p.translate(margin.left - 38, margin.top + plotHeight / 2);
            p.rotate(-p.HALF_PI);
            p.textAlign(p.CENTER, p.CENTER);
            p.text(yLabel, 0, 0);
            p.pop();

            p.textAlign(p.CENTER, p.CENTER);
            p.text(xLabel, margin.left + plotWidth / 2, margin.top + plotHeight + 30);

            // Step Chart Drawing
            if (!data || data.length === 0) return;

            p.fill(212, 230, 241); // #d4e6f1
            p.stroke(46, 134, 193); // #2e86c1
            p.strokeWeight(2);

            p.beginShape();
            p.vertex(mapX(timeOffset), mapY(0));
            for (let i = 0; i < data.length; i++) {
                const t = i + timeOffset;
                const y = data[i];
                p.vertex(mapX(t), mapY(y));
                p.vertex(mapX(t + 1), mapY(y));
            }
            p.vertex(mapX(data.length + timeOffset), mapY(0));
            p.endShape(p.CLOSE);
        }

    </script>
</body>
</html>
