    });

    const updatePayoffTable = () => {
        cells['11'].textContent = `${payoffs.a11}, ${payoffs.b11}`;
        cells['12'].textContent = `${payoffs.a12}, ${payoffs.b12}`;
        cells['21'].textContent = `${payoffs.a21}, ${payoffs.b21}`;
        cells['22'].textContent = `${payoffs.a22}, ${payoffs.b22}`;
    };

    const updateEquilibriaDisplay = (vertices) => {
        const displayDiv = document.getElementById('equilibria-display');
        if (vertices.length === 0) {
            displayDiv.innerHTML = '\\( \\emptyset \\)';
        } else {
            const pointsStr = vertices.map(p => `(${toFraction(p.x)}, ${toFraction(p.y)})`).join(', ');
            displayDiv.innerHTML = `\\( \\{ ${pointsStr} \\} \\)`;
        }
        if (window.MathJax) {
            MathJax.typesetPromise([displayDiv]).catch(err => console.error('MathJax error:', err));
        }
    };

    // Greatest Common Divisor
    const gcd = (a, b) => b ? gcd(b, a % b) : a;

    // Convert decimal to fraction string
    const toFraction = (decimal, tolerance = 1e-6) => {
        if (Math.abs(decimal - Math.round(decimal)) < tolerance) {
            return `${Math.round(decimal)}`;
        }
        const sign = decimal < 0 ? "-" : "";
        decimal = Math.abs(decimal);
        let h1 = 1, h2 = 0, k1 = 0, k2 = 1;
        let b = decimal;
        do {
            let a = Math.floor(b);
            let aux = h1; h1 = a * h1 + h2; h2 = aux;
            aux = k1; k1 = a * k1 + k2; k2 = aux;
            b = 1 / (b - a);
        } while (Math.abs(decimal - h1 / k1) > decimal * tolerance && k1 < 1000);

        if (k1 > 1000) {
             for (let d = 1; d <= 100; d++) {
                let n = Math.round(decimal * d);
                if (Math.abs(decimal - n / d) < 0.01) {
                    const common = gcd(n, d);
                    return `${sign}${n/common}/${d/common}`;
                }
            }
            return decimal.toFixed(2);
        }

        return `${sign}${h1}/${k1}`;
    };


    // --- p5.js Sketch ---
    const sketch = (p) => {
        const canvasSize = 450;
        const margin = 50;
        const plotSize = canvasSize - 2 * margin;

        const toScreenX = (x) => margin + x * plotSize;
        const toScreenY = (y) => margin + (1 - y) * plotSize;

        p.setup = () => {
            p.createCanvas(canvasSize, canvasSize).parent('canvas-container');
            p.noLoop();
            fullUpdate();
        };

        p.draw = () => {
            p.background(255);
            drawAxes();

            const { a11, a12, a21, a22, b11, b12, b21, b22 } = payoffs;
            const epsilon = 1e-9;

            // Player 1 (Blue) Best Response
            const br1_path = [];
            const den1 = a11 - a12 - a21 + a22;
            if (Math.abs(den1) < epsilon) { // Dominant strategy
                if (a12 - a22 > 0) br1_path.push({ p1: { x: 1, y: 0 }, p2: { x: 1, y: 1 } });
                else br1_path.push({ p1: { x: 0, y: 0 }, p2: { x: 0, y: 1 } });
            } else {
                const y1_indiff = (a22 - a12) / den1;
                if (y1_indiff > epsilon && y1_indiff < 1 - epsilon) {
                    br1_path.push({ p1: { x: 0, y: y1_indiff }, p2: { x: 1, y: y1_indiff } });
                    if (den1 > 0) {
                        br1_path.push({ p1: { x: 0, y: 0 }, p2: { x: 0, y: y1_indiff } });
                        br1_path.push({ p1: { x: 1, y: y1_indiff }, p2: { x: 1, y: 1 } });
                    } else { // den1 < 0
                        br1_path.push({ p1: { x: 0, y: y1_indiff }, p2: { x: 0, y: 1 } });
                        br1_path.push({ p1: { x: 1, y: 0 }, p2: { x: 1, y: y1_indiff } });
                    }
                } else { // Dominant strategy in mixed extension
                    const pref_at_half = 0.5 * den1 + a12 - a22;
                    if (pref_at_half > 0) br1_path.push({ p1: { x: 1, y: 0 }, p2: { x: 1, y: 1 } });
                    else br1_path.push({ p1: { x: 0, y: 0 }, p2: { x: 0, y: 1 } });
                }
            }

            // Player 2 (Green) Best Response
            const br2_path = [];
            const den2 = b11 - b12 - b21 + b22;
            if (Math.abs(den2) < epsilon) { // Dominant strategy
                if (b21 - b22 > 0) br2_path.push({ p1: { x: 0, y: 1 }, p2: { x: 1, y: 1 } });
                else br2_path.push({ p1: { x: 0, y: 0 }, p2: { x: 1, y: 0 } });
            } else {
                const x1_indiff = (b22 - b21) / den2;
                if (x1_indiff > epsilon && x1_indiff < 1 - epsilon) {
                    br2_path.push({ p1: { x: x1_indiff, y: 0 }, p2: { x: x1_indiff, y: 1 } });
                    if (den2 > 0) {
                        br2_path.push({ p1: { x: 0, y: 0 }, p2: { x: x1_indiff, y: 0 } });
                        br2_path.push({ p1: { x: x1_indiff, y: 1 }, p2: { x: 1, y: 1 } });
                    } else { // den2 < 0
                        br2_path.push({ p1: { x: 0, y: 1 }, p2: { x: x1_indiff, y: 1 } });
                        br2_path.push({ p1: { x: x1_indiff, y: 0 }, p2: { x: 1, y: 0 } });
                    }
                } else { // Dominant strategy in mixed extension
                    const pref_at_half = 0.5 * den2 + b21 - b22;
                    if (pref_at_half > 0) br2_path.push({ p1: { x: 0, y: 1 }, p2: { x: 1, y: 1 } });
                    else br2_path.push({ p1: { x: 0, y: 0 }, p2: { x: 1, y: 0 } });
                }
            }

            drawPath(br1_path, p.color(0, 0, 255), 2);
            drawPath(br2_path, p.color(0, 255, 0), 2);

            const intersections = findIntersections(br1_path, br2_path, epsilon);
            drawPath(intersections, p.color(255, 0, 0), 4);

            const vertices = getUniqueVertices(intersections, epsilon);
            vertices.forEach(v => {
                p.fill(255, 0, 0);
                p.noStroke();
                p.circle(toScreenX(v.x), toScreenY(v.y), 8);
            });

            updateEquilibriaDisplay(vertices);
        };

        const drawAxes = () => {
            p.push();
            p.stroke(0);
            p.strokeWeight(1);

            // Axes lines
            p.line(toScreenX(0), toScreenY(0), toScreenX(1), toScreenY(0)); // x-axis
            p.line(toScreenX(0), toScreenY(0), toScreenX(0), toScreenY(1)); // y-axis

            // Labels
            p.fill(0);
            p.noStroke();
            p.textAlign(p.CENTER, p.TOP);
            p.text('x₁', toScreenX(1), toScreenY(0) + 5);
            p.textAlign(p.RIGHT, p.CENTER);
            p.text('y₁', toScreenX(0) - 5, toScreenY(1));

            // Ticks
            p.textAlign(p.CENTER, p.TOP);
            for (let i = 0; i <= 5; i++) {
                const val = i * 0.2;
                const x = toScreenX(val);
                p.line(x, toScreenY(0), x, toScreenY(0) + 5);
                p.text(val.toFixed(1), x, toScreenY(0) + 8);
            }
            p.textAlign(p.RIGHT, p.CENTER);
            for (let i = 0; i <= 5; i++) {
                const val = i * 0.2;
                const y = toScreenY(val);
                p.line(toScreenX(0), y, toScreenX(0) - 5, y);
                 if (val > 0) p.text(val.toFixed(1), toScreenX(0) - 8, y);
            }
             p.text("0", toScreenX(0) - 8, toScreenY(0) + 8);
            p.pop();
        };

        const drawPath = (path, color, weight) => {
            p.push();
            p.stroke(color);
            p.strokeWeight(weight);
            path.forEach(seg => {
                p.line(toScreenX(seg.p1.x), toScreenY(seg.p1.y), toScreenX(seg.p2.x), toScreenY(seg.p2.y));
            });
            p.pop();
        };

        // --- Intersection Logic ---
        const findIntersections = (path1, path2, epsilon) => {
            const intersections = [];
            for (const seg1 of path1) {
                for (const seg2 of path2) {
                    const intersect = getSegmentIntersection(seg1, seg2, epsilon);
                    if (intersect) intersections.push(intersect);
                }
            }
            return intersections;
        };

        const getSegmentIntersection = (s1, s2, epsilon) => {
            const s1_is_vert = Math.abs(s1.p1.x - s1.p2.x) < epsilon;
            const s2_is_vert = Math.abs(s2.p1.x - s2.p2.x) < epsilon;

            // Case 1: Vertical vs Horizontal
            if (s1_is_vert && !s2_is_vert) {
                const [vert, horiz] = [s1, s2];
                const ix = vert.p1.x;
                const iy = horiz.p1.y;
                if (ix >= Math.min(horiz.p1.x, horiz.p2.x) - epsilon &&
                    ix <= Math.max(horiz.p1.x, horiz.p2.x) + epsilon &&
                    iy >= Math.min(vert.p1.y, vert.p2.y) - epsilon &&
                    iy <= Math.max(vert.p1.y, vert.p2.y) + epsilon) {
                    return { p1: { x: ix, y: iy }, p2: { x: ix, y: iy } };
                }
            }
            // Case 1b: Horizontal vs Vertical
            if (!s1_is_vert && s2_is_vert) {
                const [horiz, vert] = [s1, s2];
                const ix = vert.p1.x;
                const iy = horiz.p1.y;
                if (ix >= Math.min(horiz.p1.x, horiz.p2.x) - epsilon &&
                    ix <= Math.max(horiz.p1.x, horiz.p2.x) + epsilon &&
                    iy >= Math.min(vert.p1.y, vert.p2.y) - epsilon &&
                    iy <= Math.max(vert.p1.y, vert.p2.y) + epsilon) {
                    return { p1: { x: ix, y: iy }, p2: { x: ix, y: iy } };
                }
            }
            // Case 2: Vertical vs Vertical (overlap)
            if (s1_is_vert && s2_is_vert) {
                if (Math.abs(s1.p1.x - s2.p1.x) < epsilon) {
                    const y_min1 = Math.min(s1.p1.y, s1.p2.y);
                    const y_max1 = Math.max(s1.p1.y, s1.p2.y);
                    const y_min2 = Math.min(s2.p1.y, s2.p2.y);
                    const y_max2 = Math.max(s2.p1.y, s2.p2.y);
                    const overlap_min = Math.max(y_min1, y_min2);
                    const overlap_max = Math.min(y_max1, y_max2);
                    if (overlap_min <= overlap_max + epsilon) {
                        return { p1: { x: s1.p1.x, y: overlap_min }, p2: { x: s1.p1.x, y: overlap_max } };
                    }
                }
            }
            // Case 3: Horizontal vs Horizontal (overlap)
            if (!s1_is_vert && !s2_is_vert) {
                if (Math.abs(s1.p1.y - s2.p1.y) < epsilon) {
                    const x_min1 = Math.min(s1.p1.x, s1.p2.x);
                    const x_max1 = Math.max(s1.p1.x, s1.p2.x);
                    const x_min2 = Math.min(s2.p1.x, s2.p2.x);
                    const x_max2 = Math.max(s2.p1.x, s2.p2.x);
                    const overlap_min = Math.max(x_min1, x_min2);
                    const overlap_max = Math.min(x_max1, x_max2);
                    if (overlap_min <= overlap_max + epsilon) {
                        return { p1: { x: overlap_min, y: s1.p1.y }, p2: { x: overlap_max, y: s1.p1.y } };
                    }
                }
            }
            return null;
        };

        const getUniqueVertices = (segments, epsilon) => {
            const vertexSet = new Set();
            const vertices = [];
            segments.forEach(seg => {
                [seg.p1, seg.p2].forEach(p => {
                    const key = `${p.x.toFixed(6)},${p.y.toFixed(6)}`;
                    if (!vertexSet.has(key)) {
                        vertexSet.add(key);
                        vertices.push(p);
                    }
                });
            });
            // Sort for consistent display order
            vertices.sort((a,b) => a.x - b.x || a.y - b.y);
            return vertices;
        };
    };

    let myp5 = new p5(sketch);

    function fullUpdate() {
        updatePayoffTable();
        if (myp5) {
            myp5.redraw();
        }
    }
});
</script>

</body>
</html>
