        }
        #btn-reset:active {
            background: #ddd;
        }
        #canvas-container {
            border: 1px solid #ddd;
            border-radius: 4px;
            overflow: hidden; /* Ensures canvas corners are rounded */
        }
    </style>
</head>
<body>

    <div id="demo-container">
        <div id="controls-container">
            <div id="slider-group">
                <label for="slider-t">choose quadratic</label>
                <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
                <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
                <button id="btn-reset" title="Reset Demo">&#x21BB;</button>
            </div>
            <div id="checkbox-group">
                <input type="checkbox" id="checkbox-family">
                <label for="checkbox-family">family</label>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // --- P5.JS SKETCH ---

        // DOM element references
        let tSlider, tInput, familyCheckbox, resetButton;

        // Canvas and coordinate system properties
        const canvasWidth = 600;
        const canvasHeight = 500;
        const mathRangeX = [-6, 6];
        const mathRangeY = [-5, 5];
        let scaleFactor;
        let origin;

        // State variables
        let locators = [];
        let cubicCoeffs = { a: 0, b: 0, c: 0, d: 0 };
        let quadraticCoeffs = { p: 0, q: 0, r: 0 };
        let draggingLocator = null;

        const initialLocators = [
            { x: -4, y: 1 },
            { x: -2, y: 1.8 },
            { x: 1, y: -1 },
            { x: 4, y: -1 }
        ];

        // --- MATH HELPERS ---

        // Solves for the coefficients [a, b, c, d] of a cubic y = ax^3 + bx^2 + cx + d
        // that passes through four points, using Cramer's rule.
        function solveCubicCoefficients(points) {
            const M = points.map(p => [p.x ** 3, p.x ** 2, p.x, 1]);
            const y = points.map(p => p.y);

            const detM = determinant(M);
            if (Math.abs(detM) < 1e-9) {
                console.warn("Points are collinear or matrix is singular. Cannot solve.");
                return { a: 0, b: 0, c: 0, d: 0 };
            }

            const coeffs = { a: 0, b: 0, c: 0, d: 0 };
            const keys = ['a', 'b', 'c', 'd'];

            for (let j = 0; j < 4; j++) {
                const M_j = M.map(row => [...row]);
                for (let i = 0; i < 4; i++) {
                    M_j[i][j] = y[i];
                }
                coeffs[keys[j]] = determinant(M_j) / detM;
            }
            return coeffs;
        }

        // Calculates the determinant of a 4x4 matrix.
        function determinant(m) {
            if (m.length !== 4 || m[0].length !== 4) return 0;
            return (
                m[0][0] * (m[1][1] * (m[2][2] * m[3][3] - m[2][3] * m[3][2]) - m[1][2] * (m[2][1] * m[3][3] - m[2][3] * m[3][1]) + m[1][3] * (m[2][1] * m[3][2] - m[2][2] * m[3][1])) -
                m[0][1] * (m[1][0] * (m[2][2] * m[3][3] - m[2][3] * m[3][2]) - m[1][2] * (m[2][0] * m[3][3] - m[2][3] * m[3][0]) + m[1][3] * (m[2][0] * m[3][2] - m[2][2] * m[3][0])) +
                m[0][2] * (m[1][0] * (m[2][1] * m[3][3] - m[2][3] * m[3][1]) - m[1][1] * (m[2][0] * m[3][3] - m[2][3] * m[3][0]) + m[1][3] * (m[2][0] * m[3][1] - m[2][1] * m[3][0])) -
                m[0][3] * (m[1][0] * (m[2][1] * m[3][2] - m[2][2] * m[3][1]) - m[1][1] * (m[2][0] * m[3][2] - m[2][2] * m[3][0]) + m[1][2] * (m[2][0] * m[3][1] - m[2][1] * m[3][0]))
            );
        }

        // Calculates coefficients for the osculating quadratic at point t.
        function getOsculatingQuadraticCoeffs(t, cubic) {
            const { a, b, c, d } = cubic;
            const p = 3 * a * t + b;
            const q = -3 * a * t * t + c;
            const r = a * t * t * t + d;
            return { p, q, r };
        }

        // --- P5.JS SETUP & DRAW ---

        function setup() {
            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            noLoop(); // Redraw only on interaction

            // Coordinate system setup
            scaleFactor = canvasWidth / (mathRangeX[1] - mathRangeX[0]);
            origin = {
                x: -mathRangeX[0] * scaleFactor,
                y: mathRangeY[1] * scaleFactor
            };

            // Get DOM elements
            tSlider = select('#slider-t');
            tInput = select('#input-t');
            familyCheckbox = select('#checkbox-family');
            resetButton = select('#btn-reset');

            // Attach event listeners
            tSlider.input(handleSliderChange);
            tInput.input(handleInputChange);
            familyCheckbox.changed(updateAndRedraw);
            resetButton.mousePressed(resetDemo);

            // Initial setup
            resetDemo();
        }

        function draw() {
            background(255);

            // Set up coordinate system for drawing
            translate(origin.x, origin.y);
            scale(1, -1); // Flip Y-axis

            drawAxes();

            if (familyCheckbox.checked()) {
                drawFamilyOfQuadratics();
                // Draw the selected quadratic on top, bold
                drawCurve(x => quadraticCoeffs.p * x * x + quadraticCoeffs.q * x + quadraticCoeffs.r, color('#f57c00'), 3);
            } else {
                // Draw the cubic curve
                drawCurve(x => cubicCoeffs.a * x ** 3 + cubicCoeffs.b * x ** 2 + cubicCoeffs.c * x + cubicCoeffs.d, color('#3f51b5'), 3);
                // Draw the single osculating quadratic
                drawCurve(x => quadraticCoeffs.p * x * x + quadraticCoeffs.q * x + quadraticCoeffs.r, color('#f57c00'), 3);
            }

            drawLocators();
        }

        // --- DRAWING HELPERS ---

        function drawAxes() {
            stroke(220);
            strokeWeight(1);

            // X-axis and Y-axis
            line(mathToPixelX(mathRangeX[0]), 0, mathToPixelX(mathRangeX[1]), 0);
            line(0, mathToPixelY(mathRangeY[0]), 0, mathToPixelY(mathRangeY[1]));

            // Ticks and labels
            textAlign(CENTER, CENTER);
            for (let x = Math.ceil(mathRangeX[0]); x <= Math.floor(mathRangeX[1]); x++) {
                if (x === 0) continue;
                stroke(220);
                line(mathToPixelX(x), -5, mathToPixelX(x), 5);
                noStroke();
                fill(150);
                push();
                scale(1, -1);
                text(x, mathToPixelX(x), 15);
                pop();
            }
            for (let y = Math.ceil(mathRangeY[0]); y <= Math.floor(mathRangeY[1]); y++) {
                if (y === 0) continue;
                stroke(220);
                line(-5, mathToPixelY(y), 5, mathToPixelY(y));
                noStroke();
                fill(150);
                push();
                scale(1, -1);
                text(y, -15, -mathToPixelY(y));
                pop();
            }
        }

        function drawCurve(func, strokeColor, weight) {
            noFill();
            stroke(strokeColor);
            strokeWeight(weight);
            beginShape();
            for (let px = 0; px <= width; px++) {
                const mathX = pixelToMathX(px);
                const mathY = func(mathX);
                if (isFinite(mathY)) {
                    vertex(mathToPixelX(mathX), mathToPixelY(mathY));
                }
            }
            endShape();
        }

        function drawLocators() {
            for (const loc of locators) {
                const px = mathToPixelX(loc.x);
                const py = mathToPixelY(loc.y);

                strokeWeight(2);
                stroke(0);
                fill(255);
                ellipse(px, py, 12, 12);
                line(px - 4, py, px + 4, py);
                line(px, py - 4, px, py + 4);
            }
        }

        function drawFamilyOfQuadratics() {
            const familyColors = [
                color(141, 211, 199, 100), color(255, 255, 179, 100), color(190, 186, 218, 100),
                color(251, 128, 114, 100), color(128, 177, 211, 100), color(253, 180, 98, 100),
                color(179, 222, 105, 100), color(252, 205, 229, 100), color(217, 217, 217, 100),
                color(188, 128, 189, 100), color(204, 235, 197, 100), color(255, 237, 111, 100)
            ];
            const steps = 20;
            const tRange = tSlider.elt.max - tSlider.elt.min;

            for (let i = 0; i <= steps; i++) {
                const t = parseFloat(tSlider.elt.min) + (i / steps) * tRange;
                const qCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);

                const qFunc = x => qCoeffs.p * x * x + qCoeffs.q * x + qCoeffs.r;

                noStroke();
                fill(familyColors[i % familyColors.length]);

                beginShape();

                // Determine fill boundary based on parabola direction
                const yBoundary = (qCoeffs.p >= 0) ? mathToPixelY(mathRangeY[1]) : mathToPixelY(mathRangeY[0]);

                vertex(mathToPixelX(mathRangeX[0]), yBoundary);
                for (let px = 0; px <= width; px++) {
                    const mathX = pixelToMathX(px);
                    const mathY = qFunc(mathX);
                    vertex(mathToPixelX(mathX), mathToPixelY(mathY));
                }
                vertex(mathToPixelX(mathRangeX[1]), yBoundary);

                endShape(CLOSE);
            }
        }

        // --- INTERACTION HANDLERS ---

        function resetDemo() {
            tSlider.value(0.5);
            tInput.value(0.5);
            familyCheckbox.checked(false);
            locators = initialLocators.map(p => ({...p})); // Deep copy
            updateCalculations();
            redraw();
        }

        function handleSliderChange() {
            tInput.value(tSlider.value());
            updateCalculations();
            redraw();
        }

        function handleInputChange() {
            tSlider.value(tInput.value());
            updateCalculations();
            redraw();
        }

        function updateAndRedraw() {
            updateCalculations();
            redraw();
        }

        function updateCalculations() {
            cubicCoeffs = solveCubicCoefficients(locators);
            const t = tSlider.value();
            quadraticCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
        }

        function mousePressed() {
            // Check if mouse is over any locator
            for (let i = 0; i < locators.length; i++) {
                const loc = locators[i];
                // Convert mouse position to be relative to transformed canvas
                const mouseMathX = pixelToMathX(mouseX);
                const mouseMathY = pixelToMathY(mouseY, true); // Correct for y-flip
                const d = dist(mouseMathX, mouseMathY, loc.x, loc.y);

                if (d * scaleFactor < 10) { // Hitbox of 10px radius
                    draggingLocator = i;
                    return; // Stop after finding one
                }
            }
        }

        function mouseDragged() {
            if (draggingLocator !== null) {
                const loc = locators[draggingLocator];
                loc.x = constrain(pixelToMathX(mouseX), mathRangeX[0], mathRangeX[1]);
                loc.y = constrain(pixelToMathY(mouseY, true), mathRangeY[0], mathRangeY[1]);
                updateCalculations();
                redraw();
            }
        }

        function mouseReleased() {
            draggingLocator = null;
        }

        // --- COORDINATE CONVERSION UTILS ---

        function mathToPixelX(x) { return x * scaleFactor; }
        function mathToPixelY(y) { return y * scaleFactor; }

        function pixelToMathX(px) { return (px - origin.x) / scaleFactor; }
        function pixelToMathY(py, isMouse = false) {
            // Mouse Y is not flipped by the scale transform, but geometry is
            if (isMouse) {
                return (origin.y - py) / scaleFactor;
            }
            return (py - origin.y) / -scaleFactor;
        }

    </script>
</body>
</html>
