<script>
document.addEventListener('DOMContentLoaded', () => {
    // --- DOM Elements ---
    const sliderV0 = document.getElementById('slider-v0');
    const sliderT = document.getElementById('slider-T');
    const radioKelvin = document.getElementById('radio-kelvin');
    const radioCelsius = document.getElementById('radio-celsius');
    const plotContainer = document.getElementById('plot-container');
    const pistonContainer = document.getElementById('piston-container');

    // --- Constants ---
    const T0_K = 273.15; // Reference temperature in Kelvin (0°C)
    const MIN_T_C = -273;
    const MAX_T_C = 727;
    const MIN_T_K = 0;
    const MAX_T_K = 1000;

    // --- State Variables ---
    let state = {
        v0: parseFloat(sliderV0.value),
        temp: parseFloat(sliderT.value),
        unit: 'C',
        k: 0, // Proportionality constant (V/T)
        tempK: 0,
        currentV: 0
    };

    // Global variables for p5 sketch to access
    let p5_vars = {
        currentVolume: 0,
        tempK: 0,
        maxVolume: 0
    };

    // --- Color Mapping ---
    const colorScale = [
        [0.0, 'rgb(128, 0, 128)'], // Purple at 0K
        [0.5, 'rgb(255, 255, 0)'], // Yellow at 500K
        [1.0, 'rgb(255, 0, 0)']    // Red at 1000K
    ];

    function getTempColor(tempK) {
        const t = Math.max(0, Math.min(1, tempK / MAX_T_K));
        if (t <= 0.5) {
            const ratio = t * 2;
            const r = 128 + (255 - 128) * ratio;
            const g = 0 + (255 - 0) * ratio;
            const b = 128 - 128 * ratio;
            return `rgb(${Math.round(r)}, ${Math.round(g)}, ${Math.round(b)})`;
        } else {
            const ratio = (t - 0.5) * 2;
            const r = 255;
            const g = 255 - 255 * ratio;
            const b = 0;
            return `rgb(${Math.round(r)}, ${Math.round(g)}, ${Math.round(b)})`;
        }
    }

    // --- Calculation Logic ---
    function updateCalculations() {
        state.v0 = parseFloat(sliderV0.value);
        state.temp = parseFloat(sliderT.value);
        state.unit = radioKelvin.checked ? 'K' : 'C';

        state.k = state.v0 / T0_K;

        if (state.unit === 'C') {
            state.tempK = state.temp + T0_K;
        } else {
            state.tempK = state.temp;
        }
        state.tempK = Math.max(0, state.tempK); // Temperature cannot go below 0K

        state.currentV = state.k * state.tempK;

        // Update variables for p5
        p5_vars.currentVolume = state.currentV;
        p5_vars.tempK = state.tempK;
        // Max volume is at max temp for the current v0
        p5_vars.maxVolume = (state.v0 / T0_K) * MAX_T_K; 
    }

    // --- Visualization Update ---
    function updatePlot() {
        // --- Line Trace ---
        const lineX = [];
        const lineY = [];
        const lineColors = [];
        const nPoints = 100;

        let minT_plot = state.unit === 'K' ? MIN_T_K : MIN_T_C;
        let maxT_plot = state.unit === 'K' ? MAX_T_K : MAX_T_C;

        for (let i = 0; i <= nPoints; i++) {
            const tPlot = minT_plot + (i / nPoints) * (maxT_plot - minT_plot);
            let tK = state.unit === 'K' ? tPlot : tPlot + T0_K;
            if (tK < 0) tK = 0;

            lineX.push(tPlot);
            lineY.push(state.k * tK);
            lineColors.push(tK);
        }

        const lineTrace = {
            x: lineX,
            y: lineY,
            mode: 'lines',
            line: {
                width: 3
            },
            marker: {
                color: lineColors,
                colorscale: colorScale,
                showscale: false
            },
            type: 'scatter',
            hoverinfo: 'none'
        };

        // --- Marker Trace ---
        const markerTrace = {
            x: [state.temp],
            y: [state.currentV],
            mode: 'markers',
            marker: {
                color: getTempColor(state.tempK),
                size: 12,
                line: {
                    color: 'black',
                    width: 1
                }
            },
            type: 'scatter',
            hoverinfo: 'x+y'
        };

        const data = [lineTrace, markerTrace];

        // --- Layout ---
        const layout = {
            xaxis: {
                title: `temperature ${state.unit === 'K' ? 'K' : '°C'}`,
                range: [minT_plot, maxT_plot],
                zerolinecolor: '#969696',
                zerolinewidth: 2,
            },
            yaxis: {
                title: 'volume',
                range: [0, 2000],
                zeroline: false
            },
            showlegend: false,
            margin: { l: 60, r: 20, b: 50, t: 20 },
        };

        Plotly.react(plotContainer, data, layout, {responsive: true});
    }

    function handleUnitChange() {
        const newUnit = this.value;
        const oldUnit = state.unit;
        let currentTemp = parseFloat(sliderT.value);

        if (newUnit === oldUnit) return;

        if (newUnit === 'K' && oldUnit === 'C') { // C -> K
            currentTemp += T0_K;
            sliderT.min = MIN_T_K;
            sliderT.max = MAX_T_K;
        } else if (newUnit === 'C' && oldUnit === 'K') { // K -> C
            currentTemp -= T0_K;
            sliderT.min = MIN_T_C;
            sliderT.max = MAX_T_C;
        }

        sliderT.value = Math.round(currentTemp);
        state.unit = newUnit;

        updateAll();
    }

    // --- Main Update Function ---
    function updateAll() {
        updateCalculations();
        updatePlot();
        // p5 sketch updates automatically via its draw loop
    }

    // --- Event Listeners ---
    sliderV0.addEventListener('input', updateAll);
    sliderT.addEventListener('input', updateAll);
    radioKelvin.addEventListener('change', handleUnitChange);
    radioCelsius.addEventListener('change', handleUnitChange);

    // --- p5.js Sketch ---
    const sketch = (p) => {
        let colorLow, colorMid, colorHigh;

        p.setup = () => {
            const canvas = p.createCanvas(pistonContainer.offsetWidth, pistonContainer.offsetHeight);
            canvas.parent(pistonContainer);
            colorLow = p.color(128, 0, 128);   // Purple
            colorMid = p.color(255, 255, 0);   // Yellow
            colorHigh = p.color(255, 0, 0);    // Red
        };

        p.draw = () => {
            p.background(255);

            const w = p.width;
            const h = p.height;
            const wallThickness = w * 0.1;
            const cylinderWidth = w - 2 * wallThickness;
            const baseHeight = h * 0.05;

            // --- Gas Color ---
            let gasColor;
            const tempRatio = Math.max(0, Math.min(1, p5_vars.tempK / MAX_T_K));
            if (tempRatio <= 0.5) {
                gasColor = p.lerpColor(colorLow, colorMid, tempRatio * 2);
            } else {
                gasColor = p.lerpColor(colorMid, colorHigh, (tempRatio - 0.5) * 2);
            }

            // --- Cylinder ---
            p.fill(60, 100, 160);
            p.noStroke();
            // Walls
            p.rect(0, 0, wallThickness, h);
            p.rect(w - wallThickness, 0, wallThickness, h);
            // Base
            p.rect(0, h - baseHeight, w, baseHeight);

            // --- Gas and Piston ---
            const minVolume = 0;
            const maxVolume = p5_vars.maxVolume;
            const pistonHeight = h * 0.08;

            const gasDrawHeight = p.map(p5_vars.currentVolume, minVolume, maxVolume, 0, h - baseHeight - pistonHeight * 1.5);
            const pistonTopY = h - baseHeight - gasDrawHeight - pistonHeight;

            // Gas
            if (gasDrawHeight > 0) {
                p.fill(gasColor);
                p.rect(wallThickness, h - baseHeight - gasDrawHeight, cylinderWidth, gasDrawHeight);
            }

            // Piston
            p.fill(120);
            p.rect(wallThickness, pistonTopY, cylinderWidth, pistonHeight);
            p.fill(100);
            const pistonHandleWidth = cylinderWidth * 0.3;
            const pistonHandleHeight = pistonHeight * 1.5;
            p.rect(wallThickness + (cylinderWidth - pistonHandleWidth) / 2, pistonTopY - pistonHandleHeight, pistonHandleWidth, pistonHandleHeight);

            // --- Pressure Arrows ---
            p.stroke(255, 120, 0);
            p.strokeWeight(2);
            p.fill(255, 120, 0);
            for(let i = 0; i < 6; i++) {
                let arrowX = wallThickness + (cylinderWidth / 7) * (i + 1);
                let arrowTop = pistonTopY - pistonHandleHeight * 1.5;
                let arrowBottom = pistonTopY - pistonHandleHeight * 0.5;
                p.line(arrowX, arrowTop, arrowX, arrowBottom);
                p.triangle(arrowX, arrowBottom, arrowX - 4, arrowBottom - 6, arrowX + 4, arrowBottom - 6);
            }
        };

        p.windowResized = () => {
             p.resizeCanvas(pistonContainer.offsetWidth, pistonContainer.offsetHeight);
        };
    };

    // --- Initialization ---
    new p5(sketch, 'piston-container');
    updateAll();
});
</script>

</body>
</html>
