            <label for="slider-a">A</label>
            <input type="range" id="slider-a" min="0" max="360" value="306" step="0.5">
        </div>
        <div>
            <label for="slider-b">B</label>
            <input type="range" id="slider-b" min="0" max="360" value="54" step="0.5">
        </div>
        <div>
            <label for="slider-c">C</label>
            <input type="range" id="slider-c" min="0" max="360" value="180" step="0.5">
        </div>
    </div>

    <script>
        // DOM Elements
        const sliderA = document.getElementById('slider-a');
        const sliderB = document.getElementById('slider-b');
        const sliderC = document.getElementById('slider-c');
        const radioButtons = document.querySelectorAll('input[name="vertex-select"]');

        const infoSpans = {
            arcLabel1: document.getElementById('arc-label-1'),
            arcValue1: document.getElementById('arc-value-1'),
            vertexLabel1: document.getElementById('vertex-label-1'),
            vertexValue1: document.getElementById('vertex-value-1'),
            arcLabel2: document.getElementById('arc-label-2'),
            arcValue2: document.getElementById('arc-value-2'),
            vertexLabel2: document.getElementById('vertex-label-2'),
            vertexValue2: document.getElementById('vertex-value-2'),
            arcLabel3: document.getElementById('arc-label-3'),
            arcValue3: document.getElementById('arc-value-3'),
            vertexLabel3: document.getElementById('vertex-label-3'),
            vertexValue3: document.getElementById('vertex-value-3'),
            arcTotal: document.getElementById('arc-total'),
            vertexTotal: document.getElementById('vertex-total'),
        };

        // State
        let angles = { A: 0, B: 0, C: 0 };
        let selectedVertex = 'C';
        let draggingVertex = null;

        let p = {}; // p5 instance

        function setup(p5) {
            p = p5;
            p.angleMode(p.DEGREES);
            const canvas = p.createCanvas(500, 450);
            canvas.parent('canvas-container');
            canvas.mousePressed(() => mousePressed(p));
            canvas.mouseReleased(() => mouseReleased(p));
            canvas.mouseDragged(() => mouseDragged(p));

            // Attach listeners
            sliderA.addEventListener('input', updateState);
            sliderB.addEventListener('input', updateState);
            sliderC.addEventListener('input', updateState);
            radioButtons.forEach(radio => radio.addEventListener('change', updateState));

            // Initial update
            updateState();
        }

        function updateState() {
            angles.A = parseFloat(sliderA.value);
            angles.B = parseFloat(sliderB.value);
            angles.C = parseFloat(sliderC.value);

            const selectedRadio = document.querySelector('input[name="vertex-select"]:checked');
            selectedVertex = selectedRadio ? selectedRadio.value : 'None';

            updateInfoPanel();
            p.redraw();
        }

        function updateInfoPanel() {
            const arcAB = (angles.B - angles.A + 360) % 360;
            const arcBC = (angles.C - angles.B + 360) % 360;
            const arcCA = (angles.A - angles.C + 360) % 360;

            const vertexA = arcBC / 2;
            const vertexB = arcCA / 2;
            const vertexC = arcAB / 2;

            const data = [
                { arcLabel: 'Arc BC', arcValue: arcBC, vertexLabel: 'Vertex A', vertexValue: vertexA, key: 'A' },
                { arcLabel: 'Arc CA', arcValue: arcCA, vertexLabel: 'Vertex B', vertexValue: vertexB, key: 'B' },
                { arcLabel: 'Arc AB', arcValue: arcAB, vertexLabel: 'Vertex C', vertexValue: vertexC, key: 'C' }
            ];

            const clearRow = (i) => {
                infoSpans[`arcLabel${i}`].innerText = '';
                infoSpans[`arcValue${i}`].innerText = '';
                infoSpans[`vertexLabel${i}`].innerText = '';
                infoSpans[`vertexValue${i}`].innerText = '';
            };

            const fillRow = (i, d) => {
                infoSpans[`arcLabel${i}`].innerText = d.arcLabel;
                infoSpans[`arcValue${i}`].innerText = `${d.arcValue.toFixed(1)}°`;
                infoSpans[`vertexLabel${i}`].innerText = d.vertexLabel;
                infoSpans[`vertexValue${i}`].innerText = `${d.vertexValue.toFixed(1)}°`;
            };

            if (selectedVertex === 'None') {
                fillRow(1, data.find(d => d.key === 'C')); // AB -> C
                fillRow(2, data.find(d => d.key === 'A')); // BC -> A
                fillRow(3, data.find(d => d.key === 'B')); // CA -> B
                infoSpans.arcTotal.innerText = '360.0°';
                infoSpans.vertexTotal.innerText = '180.0°';
            } else {
                const selectedData = data.find(d => d.key === selectedVertex);
                fillRow(1, selectedData);
                clearRow(2);
                clearRow(3);
                infoSpans.arcTotal.innerText = '?';
                infoSpans.vertexTotal.innerText = '?';
            }
        }

        function draw(p5) {
            p = p5;
            p.background(255);

            const centerX = p.width / 2;
            const centerY = p.height / 2 - 20;
            const radius = 130;
            const diameter = radius * 2;

            const getCoords = (angle) => ({
                x: centerX + radius * p.cos(angle),
                y: centerY + radius * p.sin(angle)
            });

            const pA = getCoords(angles.A);
            const pB = getCoords(angles.B);
            const pC = getCoords(angles.C);

            // 1. Background Sectors
            p.noStroke();
            // Arc BC (light blue)
            p.fill('#e0e8f8');
            p.arc(centerX, centerY, diameter + 20, diameter + 20, angles.B, angles.C);
            // Arc CA (light yellow)
            p.fill('#f5f5d5');
            p.arc(centerX, centerY, diameter + 20, diameter + 20, angles.C, angles.A);
            // Arc AB (light green)
            p.fill('#e0f0e0');
            p.arc(centerX, centerY, diameter + 20, diameter + 20, angles.A, angles.B);


            // 2. Main Circle
            p.fill('#aaccde');
            p.stroke('#666666');
            p.strokeWeight(2);
            p.ellipse(centerX, centerY, diameter, diameter);

            // 3. Triangle
            p.fill('rgba(173, 216, 230, 0.5)');
            p.stroke('#666666');
            p.strokeWeight(2);
            p.triangle(pA.x, pA.y, pB.x, pB.y, pC.x, pC.y);

            // 4. Center point
            p.fill('#666');
            p.noStroke();
            p.ellipse(centerX, centerY, 8, 8);

            // 5. Highlighted Angles
            if (selectedVertex !== 'None') {
                let startAngle, endAngle, vertex, vertexPos, color, arc, vertexAngle;

                if (selectedVertex === 'A') {
                    startAngle = angles.B; endAngle = angles.C; vertexPos = pA;
                    color = '#FF0000'; // Red for vertex
                    arc = (endAngle - startAngle + 360) % 360; vertexAngle = arc / 2;
                } else if (selectedVertex === 'B') {
                    startAngle = angles.C; endAngle = angles.A; vertexPos = pB;
                    color = '#FF0000'; // Red for vertex
                    arc = (endAngle - startAngle + 360) % 360; vertexAngle = arc / 2;
                } else { // C
                    startAngle = angles.A; endAngle = angles.B; vertexPos = pC;
                    color = '#FF0000'; // Red for vertex
                    arc = (endAngle - startAngle + 360) % 360; vertexAngle = arc / 2;
                }

                // Central angle θ
                p.noFill();
                p.stroke('#008000'); // Green
                p.strokeWeight(3);
                p.arc(centerX, centerY, radius * 1.5, radius * 1.5, startAngle, endAngle);
                drawArrow(p, getCoords(endAngle - 5), getCoords(endAngle), '#008000', 1.5 * radius);

                // Central angle small arc
                p.arc(centerX, centerY, 40, 40, startAngle, endAngle);

                // Central angle label
                p.fill('#008000');
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                const midAngle = startAngle + arc / 2;
                const labelRad = radius * 1.05;
                p.text(`θ = ${arc.toFixed(0)}°`, centerX + labelRad * p.cos(midAngle), centerY + labelRad * p.sin(midAngle));

                // Inscribed angle θ/2
                p.noFill();
                p.stroke(color); // Red
                p.strokeWeight(3);

                if (selectedVertex === 'A') p.arc(vertexPos.x, vertexPos.y, 80, 80, p.atan2(pC.y - pA.y, pC.x - pA.x), p.atan2(pB.y - pA.y, pB.x - pA.x));
                if (selectedVertex === 'B') p.arc(vertexPos.x, vertexPos.y, 80, 80, p.atan2(pA.y - pB.y, pA.x - pB.x), p.atan2(pC.y - pB.y, pC.x - pB.x));
                if (selectedVertex === 'C') p.arc(vertexPos.x, vertexPos.y, 80, 80, p.atan2(pB.y - pC.y, pB.x - pC.x), p.atan2(pA.y - pC.y, pA.x - pC.x));

                // Inscribed angle label
                p.fill(color);
                p.noStroke();
                const labelAngle = midAngle + 180;
                const labelPos = {
                    x: vertexPos.x + 80 * p.cos(labelAngle),
                    y: vertexPos.y + 80 * p.sin(labelAngle)
                };
                p.push();
                p.translate(labelPos.x, labelPos.y);
                p.text(`θ/2 = ${vertexAngle.toFixed(1)}°`, 0, 0);
                p.pop();
            }

            // 6. Vertex labels
            p.fill('#00008B');
            p.noStroke();
            p.textSize(24);
            p.textAlign(p.CENTER, p.CENTER);
            p.text('A', centerX + (radius + 20) * p.cos(angles.A), centerY + (radius + 20) * p.sin(angles.A));
            p.text('B', centerX + (radius + 20) * p.cos(angles.B), centerY + (radius + 20) * p.sin(angles.B));
            p.text('C', centerX + (radius + 20) * p.cos(angles.C), centerY + (radius + 20) * p.sin(angles.C));
        }

        function drawArrow(p, p1, p2, color, r) {
            const centerX = p.width / 2;
            const centerY = p.height / 2 - 20;
            let angle = p.atan2(p2.y - centerY, p2.x - centerX);
            p.push();
            p.stroke(color);
            p.fill(color);
            p.translate(centerX + r/2 * p.cos(angle), centerY + r/2 * p.sin(angle));
            p.rotate(angle);
            p.triangle(0, 0, -8, 4, -8, -4);
            p.pop();
        }

        // --- Mouse Interaction ---
        function mousePressed(p) {
            const centerX = p.width / 2;
            const centerY = p.height / 2 - 20;
            const radius = 130;
            const getCoords = (angle) => ({
                x: centerX + radius * p.cos(angle),
                y: centerY + radius * p.sin(angle)
            });

            const pA = getCoords(angles.A);
            const pB = getCoords(angles.B);
            const pC = getCoords(angles.C);

            const d_to_A = p.dist(p.mouseX, p.mouseY, pA.x, pA.y);
            const d_to_B = p.dist(p.mouseX, p.mouseY, pB.x, pB.y);
            const d_to_C = p.dist(p.mouseX, p.mouseY, pC.x, pC.y);

            const hitRadius = 15;
            if (d_to_A < hitRadius) draggingVertex = 'A';
            else if (d_to_B < hitRadius) draggingVertex = 'B';
            else if (d_to_C < hitRadius) draggingVertex = 'C';
            else draggingVertex = null;
        }

        function mouseDragged(p) {
            if (draggingVertex) {
                const centerX = p.width / 2;
                const centerY = p.height / 2 - 20;
                let newAngle = p.atan2(p.mouseY - centerY, p.mouseX - centerX);
                if (newAngle < 0) newAngle += 360;

                if (draggingVertex === 'A') sliderA.value = newAngle;
                else if (draggingVertex === 'B') sliderB.value = newAngle;
                else if (draggingVertex === 'C') sliderC.value = newAngle;

                updateState();
            }
        }

        function mouseReleased(p) {
            draggingVertex = null;
        }

        // Initialize p5
        new p5(p => {
            p.setup = () => setup(p);
            p.draw = () => draw(p);
            p.noLoop();
        });

    </script>
</body>
