        phi: document.getElementById('slider-phi'),
        beta: document.getElementById('slider-beta'),
        gamma: document.getElementById('slider-gamma'),
        xi: document.getElementById('slider-xi'),
    };
    const values = {
        tau: document.getElementById('value-tau'),
        phi: document.getElementById('value-phi'),
        beta: document.getElementById('value-beta'),
        gamma: document.getElementById('value-gamma'),
        xi: document.getElementById('value-xi'),
    };
    const buttons = {
        dec_tau: document.getElementById('btn-dec-tau'), inc_tau: document.getElementById('btn-inc-tau'),
        dec_phi: document.getElementById('btn-dec-phi'), inc_phi: document.getElementById('btn-inc-phi'),
        dec_beta: document.getElementById('btn-dec-beta'), inc_beta: document.getElementById('btn-inc-beta'),
        dec_gamma: document.getElementById('btn-dec-gamma'), inc_gamma: document.getElementById('btn-inc-gamma'),
        dec_xi: document.getElementById('btn-dec-xi'), inc_xi: document.getElementById('btn-inc-xi'),
    };

    const CONC_PLOT_DIV = 'plot-concentration';
    const TEMP_PLOT_DIV = 'plot-temperature';

    // --- STATE MANAGEMENT ---
    let state = {
        tau: parseFloat(sliders.tau.value),
        phi: parseFloat(sliders.phi.value),
        beta: parseFloat(sliders.beta.value),
        gamma: parseFloat(sliders.gamma.value),
        xi: parseFloat(sliders.xi.value),
    };
    let c_profile = [];
    let theta_profile = [];

    // --- NUMERICAL MODEL CONSTANTS ---
    const N = 100; // Number of spatial grid points
    const d_xi = 1.0 / (N - 1);
    const xi_grid = Array.from({ length: N }, (_, i) => i * d_xi);
    const d_xi_sq = d_xi * d_xi;

    // --- NUMERICAL SOLVER ---
    function solvePDEs(params) {
        const { tau, phi, beta, gamma } = params;
        const n_internal = N - 1; // We solve for internal points, ξ=1 is fixed

        // State vector y = [c_0, ..., c_{N-2}, θ_0, ..., θ_{N-2}]
        let y = new Array(2 * n_internal).fill(0);
        // Initial conditions: c=0, θ=1
        for (let i = 0; i < n_internal; i++) {
            y[i] = 0.0; // c(ξ, 0) = 0
            y[i + n_internal] = 1.0; // θ(ξ, 0) = 1
        }

        // The ODE system dy/dt = f(t, y)
        function pde_rhs(y_current) {
            const dydt = new Array(2 * n_internal).fill(0);
            const c_full = new Array(N);
            const theta_full = new Array(N);

            // Populate full arrays with internal points and boundary conditions
            for (let i = 0; i < n_internal; i++) {
                c_full[i] = y_current[i];
                theta_full[i] = y_current[i + n_internal];
            }
            c_full[N - 1] = 1.0;     // c(1, τ) = 1
            theta_full[N - 1] = 1.0; // θ(1, τ) = 1

            for (let i = 0; i < n_internal; i++) {
                // Laplacian (∂²u/∂ξ²) calculation using finite differences
                let laplacian_c, laplacian_theta;
                if (i === 0) { // Center point (ξ=0), using symmetry ∂u/∂ξ=0 -> u[-1] = u[1]
                    laplacian_c = 2 * (c_full[1] - c_full[0]) / d_xi_sq;
                    laplacian_theta = 2 * (theta_full[1] - theta_full[0]) / d_xi_sq;
                } else { // Internal points
                    laplacian_c = (c_full[i + 1] - 2 * c_full[i] + c_full[i - 1]) / d_xi_sq;
                    laplacian_theta = (theta_full[i + 1] - 2 * theta_full[i] + theta_full[i - 1]) / d_xi_sq;
                }

                // Reaction term, avoiding division by zero if θ is small
                const reaction_term = (theta_full[i] > 1e-6)
                    ? phi * phi * c_full[i] * Math.exp(gamma * (1.0 - 1.0 / theta_full[i]))
                    : 0;

                // PDE equations
                dydt[i] = laplacian_c - reaction_term;
                dydt[i + n_internal] = laplacian_theta + beta * reaction_term;
            }
            return dydt;
        }

        // RK4 Integrator
        const dt = 0.001; // Time step for integration
        const num_steps = Math.ceil(tau / dt);

        for (let i = 0; i < num_steps; i++) {
            const k1 = pde_rhs(y);
            const y_k2 = y.map((val, j) => val + 0.5 * dt * k1[j]);
            const k2 = pde_rhs(y_k2);
            const y_k3 = y.map((val, j) => val + 0.5 * dt * k2[j]);
            const k3 = pde_rhs(y_k3);
            const y_k4 = y.map((val, j) => val + dt * k3[j]);
            const k4 = pde_rhs(y_k4);

            y = y.map((val, j) => val + (dt / 6.0) * (k1[j] + 2 * k2[j] + 2 * k3[j] + k4[j]));
        }

        // Extract final profiles and add boundary values
        const final_c = new Array(N);
        const final_theta = new Array(N);
        for (let i = 0; i < n_internal; i++) {
            final_c[i] = y[i];
            final_theta[i] = y[i + n_internal];
        }
        final_c[N - 1] = 1.0;
        final_theta[N - 1] = 1.0;

        return { c_profile: final_c, theta_profile: final_theta };
    }

    // --- PLOTTING ---
    function initializePlots() {
        const commonLayout = {
            xaxis: { title: 'ξ', range: [0, 1] },
            margin: { l: 50, r: 20, b: 40, t: 20 },
            showlegend: false,
        };

        const concLayout = { ...commonLayout, yaxis: { title: 'c', range: [0, 1.1] } };
        const tempLayout = { ...commonLayout, yaxis: { title: 'θ', autorange: true } };

        const initialTrace = { x: [], y: [], type: 'scatter', mode: 'lines' };
        const markerTrace = { x: [], y: [], type: 'scatter', mode: 'lines', line: { color: '#333333', width: 1.5 } };

        Plotly.newPlot(CONC_PLOT_DIV, [initialTrace, markerTrace], concLayout, { responsive: true, displaylogo: false });
        Plotly.newPlot(TEMP_PLOT_DIV, [initialTrace, markerTrace], tempLayout, { responsive: true, displaylogo: false });
    }

    function getMarkerAndAnnotationData() {
        if (c_profile.length === 0 || theta_profile.length === 0) return {};

        // Find interpolated values at the current ξ
        const xi_val = state.xi;
        const index = Math.min(N - 2, Math.floor(xi_val / d_xi));
        const frac = (xi_val - xi_grid[index]) / d_xi;

        const c_val = c_profile[index] + frac * (c_profile[index + 1] - c_profile[index]);
        const theta_val = theta_profile[index] + frac * (theta_profile[index + 1] - theta_profile[index]);

        // Data for concentration plot
        const c_marker = {
            x: [xi_val, xi_val, 0],
            y: [0, c_val, c_val],
        };
        const c_annotation = {
            text: `τ = ${state.tau.toFixed(2)}<br>ξ = ${xi_val.toFixed(3)}<br>c = ${c_val.toFixed(5)}`,
            align: 'left',
            showarrow: false,
            x: 0.95, y: 0.95,
            xref: 'paper', yref: 'paper',
            xanchor: 'right', yanchor: 'top',
            bgcolor: 'rgba(255,255,255,0.7)',
        };

        // Data for temperature plot
        const temp_marker = {
            x: [xi_val, xi_val, 0],
            y: [1.0, theta_val, theta_val], // Start from baseline temp of 1.0
        };
        const temp_annotation = {
            text: `τ = ${state.tau.toFixed(2)}<br>ξ = ${xi_val.toFixed(3)}<br>θ = ${theta_val.toFixed(5)}`,
            align: 'left',
            showarrow: false,
            x: 0.95, y: 0.95,
            xref: 'paper', yref: 'paper',
            xanchor: 'right', yanchor: 'top',
            bgcolor: 'rgba(255,255,255,0.7)',
        };

        return { c_marker, c_annotation, temp_marker, temp_annotation };
    }

    function redrawPlots(fullSimulation) {
        if (fullSimulation) {
            const { c_profile: new_c, theta_profile: new_theta } = solvePDEs(state);
            c_profile = new_c;
            theta_profile = new_theta;

            const { c_marker, c_annotation, temp_marker, temp_annotation } = getMarkerAndAnnotationData();

            Plotly.react(CONC_PLOT_DIV,
                [{ x: xi_grid, y: c_profile }, { x: c_marker.x, y: c_marker.y }],
                { annotations: [c_annotation] }
            );

            // Need to get the new y-axis range for the temperature plot marker
            const min_temp = Math.min(...theta_profile, 1.0);
            const temp_marker_adjusted = {
                x: [state.xi, state.xi, 0],
                y: [min_temp, temp_annotation.text.split('=')[3], temp_annotation.text.split('=')[3]],
            };

            Plotly.react(TEMP_PLOT_DIV,
                [{ x: xi_grid, y: theta_profile }, { x: temp_marker.x, y: temp_marker.y }],
                { annotations: [temp_annotation], 'yaxis.autorange': true }
            );

        } else { // Only update markers and annotations
            const { c_marker, c_annotation, temp_marker, temp_annotation } = getMarkerAndAnnotationData();

            // Use restyle for traces, relayout for annotations/layout
            Plotly.restyle(CONC_PLOT_DIV, { x: [c_marker.x], y: [c_marker.y] }, [1]);
            Plotly.relayout(CONC_PLOT_DIV, { annotations: [c_annotation] });

            Plotly.restyle(TEMP_PLOT_DIV, { x: [temp_marker.x], y: [temp_marker.y] }, [1]);
            Plotly.relayout(TEMP_PLOT_DIV, { annotations: [temp_annotation] });
        }
    }

    // --- EVENT LISTENERS ---
    function setupEventListeners() {
        // Sliders
        sliders.tau.addEventListener('input', (e) => {
            state.tau = parseFloat(e.target.value);
            values.tau.textContent = state.tau.toFixed(2);
            redrawPlots(true);
        });
        sliders.phi.addEventListener('input', (e) => {
            state.phi = parseFloat(e.target.value);
            values.phi.textContent = state.phi.toFixed(1);
            redrawPlots(true);
        });
        sliders.beta.addEventListener('input', (e) => {
            state.beta = parseFloat(e.target.value);
            values.beta.textContent = state.beta.toFixed(2);
            redrawPlots(true);
        });
        sliders.gamma.addEventListener('input', (e) => {
            state.gamma = parseFloat(e.target.value);
            values.gamma.textContent = state.gamma.toFixed(0);
            redrawPlots(true);
        });
        sliders.xi.addEventListener('input', (e) => {
            state.xi = parseFloat(e.target.value);
            values.xi.textContent = state.xi.toFixed(3);
            redrawPlots(false); // No new simulation needed
        });

        // Buttons
        Object.keys(buttons).forEach(key => {
            buttons[key].addEventListener('click', () => {
                const [action, param] = key.split('_');
                const slider = sliders[param];
                const step = parseFloat(slider.step);
                const currentValue = parseFloat(slider.value);
                const multiplier = (action === 'inc') ? 1 : -1;

                let newValue = currentValue + (multiplier * step);
                // Clamp to min/max
                newValue = Math.max(parseFloat(slider.min), Math.min(parseFloat(slider.max), newValue));

                slider.value = newValue;
                // Programmatically trigger the input event to run updates
                slider.dispatchEvent(new Event('input', { bubbles: true }));
            });
        });
    }

    // --- INITIALIZATION ---
    function init() {
        // Set initial text values
        values.tau.textContent = state.tau.toFixed(2);
        values.phi.textContent = state.phi.toFixed(1);
        values.beta.textContent = state.beta.toFixed(2);
        values.gamma.textContent = state.gamma.toFixed(0);
        values.xi.textContent = state.xi.toFixed(3);

        initializePlots();
        setupEventListeners();
        redrawPlots(true); // Run initial simulation and draw
    }

    init();
});
</script>

</body>
</html>
