                    x: [richards_y.map((_, i) => t_values[i]), law45_t],
                    y: [richards_y, law45_y]
                }, [1, 2]);

                Plotly.relayout('plot-container', {
                    'yaxis.range': [0, b_max * 1.05]
                });
            }

            // --- Event Listeners ---
            for (let key in sliders) {
                sliders[key].addEventListener('input', update);
            }

            // --- Initial Call ---
            initializePlot();
            update();
        });
    </script>
