    <script>
        // --- DOM Elements ---
        const controls = {
            basePolygon: document.querySelector('input[name="base-polygon"]:checked'),
            units: document.getElementById('slider-units'),
            angle: document.getElementById('slider-angle'),
            conical: document.getElementById('slider-conical'),
            rotateX: document.getElementById('slider-rotate-x'),
            rotateZ: document.getElementById('slider-rotate-z'),
        };

        const buttons = {
            units: document.getElementById('btn-units-plus'),
            angle: document.getElementById('btn-angle-plus'),
            conical: document.getElementById('btn-conical-plus'),
            rotateX: document.getElementById('btn-rotate-x-plus'),
            rotateZ: document.getElementById('btn-rotate-z-plus'),
        };

        const canvasContainer = document.getElementById('canvas-container');
        const canvas = document.getElementById('main-canvas');

        // --- Three.js Scene Setup ---
        let scene, camera, renderer, pineConeGroup;

        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Camera
            const fov = 75;
            const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            const near = 0.1;
            const far = 1000;
            camera = new THREE.PerspectiveCamera(fov, aspect, near, far);
            camera.position.z = 150;

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(50, 50, 100);
            scene.add(directionalLight);

            // Main group for all segments
            pineConeGroup = new THREE.Group();
            scene.add(pineConeGroup);

            // Event Listeners
            setupEventListeners();

            // Initial Draw
            redraw();

            // Handle window resize
            window.addEventListener('resize', onWindowResize, false);
        }

        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            renderer.render(scene, camera);
        }

        // --- Interaction Logic ---
        function setupEventListeners() {
            // Radio buttons
            document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
                radio.addEventListener('change', () => {
                    controls.basePolygon = document.querySelector('input[name="base-polygon"]:checked');
                    redraw();
                });
            });

            // Sliders
            Object.values(controls).forEach(slider => {
                if (slider && slider.type === 'range') {
                    slider.addEventListener('input', redraw);
                }
            });

            // Plus Buttons
            Object.entries(buttons).forEach(([key, button]) => {
                button.addEventListener('click', () => {
                    const slider = controls[key];
                    if (slider) {
                        const newValue = parseFloat(slider.value) + parseFloat(slider.step);
                        if (newValue <= parseFloat(slider.max)) {
                            slider.value = newValue;
                            // Manually trigger input event to update everything
                            slider.dispatchEvent(new Event('input'));
                        }
                    }
                });
            });
        }

        // --- Core Redraw Function ---
        function redraw() {
            // 1. Clear previous geometry
            while (pineConeGroup.children.length > 0) {
                pineConeGroup.remove(pineConeGroup.children[0]);
            }

            // 2. Read current control values
            const numSides = parseInt(controls.basePolygon.value);
            const numUnits = parseInt(controls.units.value);
            const angularAdvance = parseFloat(controls.angle.value);
            const conicalFactor = parseFloat(controls.conical.value);
            const rotX = parseFloat(controls.rotateX.value);
            const rotZ = parseFloat(controls.rotateZ.value);

            // 3. Create base geometry for a single segment
            const shape = new THREE.Shape();
            const polygonRadius = 10;
            for (let i = 0; i <= numSides; i++) {
                const angle = (i / numSides) * Math.PI * 2;
                const x = polygonRadius * Math.cos(angle);
                const y = polygonRadius * Math.sin(angle);
                if (i === 0) {
                    shape.moveTo(x, y);
                } else {
                    shape.lineTo(x, y);
                }
            }
            const geometry = new THREE.ShapeGeometry(shape);
            geometry.center();

            // 4. Loop to generate and place segments
            const angularAdvanceRad = angularAdvance * (Math.PI / 180);

            for (let i = 0; i < numUnits; i++) {
                // a. Calculate transformations
                const angle = i * angularAdvanceRad;
                const radius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);

                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = conicalFactor * radius;

                // b. Create mesh with unique material
                const material = new THREE.MeshStandardMaterial({
                    roughness: 0.8,
                    metalness: 0.1
                });
                const hue = (angle / (2 * Math.PI)) % 1.0;
                material.color.setHSL(hue, 0.8, 0.6);

                const mesh = new THREE.Mesh(geometry, material);

                // c. Apply transformations
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angle + Math.PI / 2; // Align with spiral tangent

                // d. Add to group
                pineConeGroup.add(mesh);
            }

            // 5. Apply master rotations
            pineConeGroup.rotation.x = rotX * (Math.PI / 180);
            pineConeGroup.rotation.z = rotZ * (Math.PI / 180);

            // 6. Render the scene
            renderer.render(scene, camera);
        }

        // --- Start ---
        init();

    </script>
