<script type="module">
    import * as THREE from 'three';
    import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

    let scene, camera, renderer, controls;
    let atomGroup, planeGroup;

    // --- DOM Elements ---
    const btnSc = document.getElementById('btn-sc');
    const btnBcc = document.getElementById('btn-bcc');
    const btnFcc = document.getElementById('btn-fcc');
    const inputH = document.getElementById('input-h');
    const inputK = document.getElementById('input-k');
    const inputL = document.getElementById('input-l');
    const sliderOpacity = document.getElementById('slider-opacity');
    const canvasContainer = document.getElementById('canvas-container');

    // --- State ---
    let currentLattice = 'SC';
    let millerIndices = { h: 1, k: 1, l: 1 };
    let planeOpacity = 0.8;

    function init() {
        // --- Scene Setup ---
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        // --- Camera ---
        const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
        camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
        camera.position.set(2, 2, 2.5);
        camera.lookAt(0, 0, 0);

        // --- Renderer ---
        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        canvasContainer.appendChild(renderer.domElement);

        // --- Lighting ---
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);
        const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        dirLight1.position.set(5, 5, 5);
        scene.add(dirLight1);
        const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
        dirLight2.position.set(-5, -5, -5);
        scene.add(dirLight2);

        // --- Controls ---
        controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // --- Permanent Scene Objects ---
        // Unit Cell Wireframe
        const boxGeom = new THREE.BoxGeometry(1, 1, 1);
        const edges = new THREE.EdgesGeometry(boxGeom);
        const line = new THREE.LineSegments(edges, new THREE.LineBasicMaterial({ color: 0x444444 }));
        scene.add(line);

        // Coordinate Axes
        const axesHelper = new THREE.Group();
        axesHelper.add(new THREE.ArrowHelper(new THREE.Vector3(1, 0, 0), new THREE.Vector3(0, 0, 0), 1.2, 0xff0000));
        axesHelper.add(new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), new THREE.Vector3(0, 0, 0), 1.2, 0x00ff00));
        axesHelper.add(new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), 1.2, 0x0000ff));
        scene.add(axesHelper);

        // --- Dynamic Object Groups ---
        atomGroup = new THREE.Group();
        planeGroup = new THREE.Group();
        scene.add(atomGroup);
        scene.add(planeGroup);

        // --- Event Listeners ---
        btnSc.addEventListener('click', () => setLatticeType('SC'));
        btnBcc.addEventListener('click', () => setLatticeType('BCC'));
        btnFcc.addEventListener('click', () => setLatticeType('FCC'));

        [inputH, inputK, inputL].forEach(input => {
            input.addEventListener('input', () => {
                millerIndices.h = parseInt(inputH.value) || 0;
                millerIndices.k = parseInt(inputK.value) || 0;
                millerIndices.l = parseInt(inputL.value) || 0;
                updateScene();
            });
        });

        sliderOpacity.addEventListener('input', (e) => {
            planeOpacity = parseFloat(e.target.value);
            planeGroup.children.forEach(planeMesh => {
                planeMesh.material.opacity = planeOpacity;
            });
        });

        window.addEventListener('resize', onWindowResize, false);

        // --- Initial State ---
        setLatticeType(currentLattice); // This calls updateScene
        animate();
    }

    function setLatticeType(type) {
        currentLattice = type;
        [btnSc, btnBcc, btnFcc].forEach(btn => btn.classList.remove('active'));
        if (type === 'SC') btnSc.classList.add('active');
        if (type === 'BCC') btnBcc.classList.add('active');
        if (type === 'FCC') btnFcc.classList.add('active');
        updateScene();
    }

    function onWindowResize() {
        if (!canvasContainer || !renderer) return;
        camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
        camera.updateProjectionMatrix();
        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
    }

    function animate() {
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
    }

    function updateScene() {
        // Clear previous dynamic objects
        atomGroup.clear();
        planeGroup.clear();

        // Draw new objects based on state
        drawAtoms();
        drawPlanes();
    }

    function drawAtoms() {
        const atomGeometry = new THREE.SphereGeometry(0.08, 32, 16);
        const atomMaterial = new THREE.MeshPhongMaterial({
            color: 0x223344,
            specular: 0x507D7D,
            shininess: 50
        });

        let positions = [];
        const p = 0.5;
        const corners = [
            [p, p, p], [p, p, -p], [p, -p, p], [p, -p, -p],
            [-p, p, p], [-p, p, -p], [-p, -p, p], [-p, -p, -p]
        ];

        positions.push(...corners); // SC has corners

        if (currentLattice === 'BCC') {
            positions.push([0, 0, 0]); // Body center
        } else if (currentLattice === 'FCC') {
            const faces = [
                [p, 0, 0], [-p, 0, 0], [0, p, 0], 
                [0, -p, 0], [0, 0, p], [0, 0, -p]
            ];
            positions.push(...faces); // Face centers
        }

        positions.forEach(pos => {
            const atom = new THREE.Mesh(atomGeometry, atomMaterial);
            atom.position.set(pos[0], pos[1], pos[2]);
            atomGroup.add(atom);
        });
    }

    // --- Plane Drawing Logic ---
    function gcd(a, b) {
        return b === 0 ? a : gcd(b, a % b);
    }

    function gcdArray(arr) {
        let result = Math.abs(arr[0]);
        for (let i = 1; i < arr.length; i++) {
            result = gcd(result, Math.abs(arr[i]));
        }
        return result;
    }

    function drawPlanes() {
        let { h, k, l } = millerIndices;
        if (h === 0 && k === 0 && l === 0) return;

        const commonDivisor = gcdArray([h, k, l]);
        if (commonDivisor > 0) {
            h /= commonDivisor;
            k /= commonDivisor;
            l /= commonDivisor;
        }

        const normal = new THREE.Vector3(h, k, l);
        const cubeSize = 1.0;
        const halfSize = cubeSize / 2;

        // Edges of the cube [-0.5, 0.5]^3
        const edges = [
            // Bottom face
            [new THREE.Vector3(-halfSize, -halfSize, -halfSize), new THREE.Vector3(halfSize, -halfSize, -halfSize)],
            [new THREE.Vector3(halfSize, -halfSize, -halfSize), new THREE.Vector3(halfSize, -halfSize, halfSize)],
            [new THREE.Vector3(halfSize, -halfSize, halfSize), new THREE.Vector3(-halfSize, -halfSize, halfSize)],
            [new THREE.Vector3(-halfSize, -halfSize, halfSize), new THREE.Vector3(-halfSize, -halfSize, -halfSize)],
            // Top face
            [new THREE.Vector3(-halfSize, halfSize, -halfSize), new THREE.Vector3(halfSize, halfSize, -halfSize)],
            [new THREE.Vector3(halfSize, halfSize, -halfSize), new THREE.Vector3(halfSize, halfSize, halfSize)],
            [new THREE.Vector3(halfSize, halfSize, halfSize), new THREE.Vector3(-halfSize, halfSize, halfSize)],
            [new THREE.Vector3(-halfSize, halfSize, halfSize), new THREE.Vector3(-halfSize, halfSize, -halfSize)],
            // Vertical edges
            [new THREE.Vector3(-halfSize, -halfSize, -halfSize), new THREE.Vector3(-halfSize, halfSize, -halfSize)],
            [new THREE.Vector3(halfSize, -halfSize, -halfSize), new THREE.Vector3(halfSize, halfSize, -halfSize)],
            [new THREE.Vector3(halfSize, -halfSize, halfSize), new THREE.Vector3(halfSize, halfSize, halfSize)],
            [new THREE.Vector3(-halfSize, -halfSize, halfSize), new THREE.Vector3(-halfSize, halfSize, halfSize)],
        ];

        // Determine range of 'n' for hx + ky + lz = n
        const maxAbsVal = 0.5 * (Math.abs(h) + Math.abs(k) + Math.abs(l));
        const n_min = Math.ceil(-maxAbsVal - 1e-9);
        const n_max = Math.floor(maxAbsVal + 1e-9);

        for (let n = n_min; n <= n_max; n++) {
            let intersectionPoints = [];
            const planeConstant = n;

            edges.forEach(([p1, p2]) => {
                const lineDir = new THREE.Vector3().subVectors(p2, p1);
                const denominator = normal.dot(lineDir);

                if (Math.abs(denominator) > 1e-6) { // Not parallel
                    const t = (planeConstant - normal.dot(p1)) / denominator;
                    if (t >= -1e-6 && t <= 1 + 1e-6) { // Intersection is on the segment
                        const intersectPt = p1.clone().add(lineDir.multiplyScalar(t));

                        // Avoid duplicate points
                        let isDuplicate = false;
                        for(const pt of intersectionPoints) {
                            if (pt.distanceTo(intersectPt) < 1e-6) {
                                isDuplicate = true;
                                break;
                            }
                        }
                        if (!isDuplicate) {
                            intersectionPoints.push(intersectPt);
                        }
                    }
                }
            });

            if (intersectionPoints.length >= 3) {
                // Sort points to form a polygon
                const centroid = new THREE.Vector3();
                intersectionPoints.forEach(p => centroid.add(p));
                centroid.divideScalar(intersectionPoints.length);

                const firstPointVec = new THREE.Vector3().subVectors(intersectionPoints[0], centroid);

                intersectionPoints.sort((a, b) => {
                    const vecA = new THREE.Vector3().subVectors(a, centroid);
                    const vecB = new THREE.Vector3().subVectors(b, centroid);
                    const crossA = new THREE.Vector3().crossVectors(firstPointVec, vecA);
                    const crossB = new THREE.Vector3().crossVectors(firstPointVec, vecB);
                    const angleA = Math.atan2(normal.dot(crossA), firstPointVec.dot(vecA));
                    const angleB = Math.atan2(normal.dot(crossB), firstPointVec.dot(vecB));
                    return angleA - angleB;
                });

                // Triangulate the polygon (fan triangulation)
                const vertices = [];
                for (let i = 1; i < intersectionPoints.length - 1; i++) {
                    vertices.push(intersectionPoints[0].x, intersectionPoints[0].y, intersectionPoints[0].z);
                    vertices.push(intersectionPoints[i].x, intersectionPoints[i].y, intersectionPoints[i].z);
                    vertices.push(intersectionPoints[i + 1].x, intersectionPoints[i + 1].y, intersectionPoints[i + 1].z);
                }

                const geometry = new THREE.BufferGeometry();
                geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
                geometry.computeVertexNormals();

                const material = new THREE.MeshBasicMaterial({
                    color: 0x0000ff,
                    side: THREE.DoubleSide,
                    transparent: true,
                    opacity: planeOpacity
                });
                const mesh = new THREE.Mesh(geometry, material);
                planeGroup.add(mesh);
            }
        }
    }

    init();

</script>

