    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // --- DATA (as specified, can be populated with pre-calculated values) ---
        const THOMSON_POINTS = {}; // Empty, will trigger random generation

        // --- DOM ELEMENTS ---
        const sliderPoints = document.getElementById('slider-points');
        const labelPoints = document.getElementById('label-points');
        const btn3d = document.getElementById('btn-3d');
        const btn2d = document.getElementById('btn-2d');
        const canvas3d = document.getElementById('canvas-3d');
        const div2dPlot = document.getElementById('div-2d-plot');
        const canvas2d = document.getElementById('canvas-2d');
        const div2dText = document.getElementById('div-2d-text');
        const vizContainer = document.getElementById('visualization-container');

        // --- STATE VARIABLES ---
        let currentMode = '3d';
        let currentPointsCoords = [];
        let pointMeshes = [];

        // --- 3D SCENE VARIABLES ---
        let scene, camera, renderer, controls, mainSphere, pointsGroup;

        // --- INITIALIZATION ---
        function init() {
            // Setup 3D Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            const { width, height } = vizContainer.getBoundingClientRect();
            camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
            camera.position.z = 2.5;

            renderer = new THREE.WebGLRenderer({ canvas: canvas3d, antialias: true });
            renderer.setSize(width, height);

            // Lighting
            scene.add(new THREE.AmbientLight(0xcccccc));
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.7);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Main semi-transparent sphere
            const sphereGeom = new THREE.SphereGeometry(1, 64, 64);
            const sphereMat = new THREE.MeshLambertMaterial({
                color: 0xFFFFE0,
                transparent: true,
                opacity: 0.6
            });
            mainSphere = new THREE.Mesh(sphereGeom, sphereMat);
            scene.add(mainSphere);

            // Group to hold all points
            pointsGroup = new THREE.Group();
            scene.add(pointsGroup);

            // Event Listeners
            sliderPoints.addEventListener('input', handleSliderChange);
            btn2d.addEventListener('click', () => switchView('2d'));
            btn3d.addEventListener('click', () => switchView('3d'));
            window.addEventListener('resize', onWindowResize);

            // Initial visualization
            updateVisualization();

            // Start animation loop
            animate();
        }

        // --- EVENT HANDLERS ---
        function handleSliderChange(event) {
            const numPoints = event.target.value;
            labelPoints.textContent = `+ ${numPoints}`;
            updateVisualization();
        }

        function switchView(mode) {
            if (currentMode === mode) return;
            currentMode = mode;

            if (mode === '3d') {
                btn3d.classList.add('active');
                btn2d.classList.remove('active');
                canvas3d.style.display = 'block';
                div2dPlot.style.display = 'none';
            } else { // mode === '2d'
                btn2d.classList.add('active');
                btn3d.classList.remove('active');
                canvas3d.style.display = 'none';
                div2dPlot.style.display = 'flex';
                update2DView(); // Re-draw 2D plot when switching to it
            }
        }

        function onWindowResize() {
            const { width, height } = vizContainer.getBoundingClientRect();
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        // --- CORE LOGIC ---
        function updateVisualization() {
            const numPoints = parseInt(sliderPoints.value, 10);
            currentPointsCoords = getPoints(numPoints);

            if (currentMode === '3d') {
                update3DView();
            } else {
                update2DView();
            }
        }

        function getPoints(n) {
            if (THOMSON_POINTS[n]) {
                return THOMSON_POINTS[n];
            }
            return generateRandomPoints(n);
        }

        function generateRandomPoints(n) {
            const points = [];
            for (let i = 0; i < n; i++) {
                const u = Math.random();
                const v = Math.random();
                const theta = 2 * Math.PI * u;
                const phi = Math.acos(2 * v - 1);
                const x = Math.sin(phi) * Math.cos(theta);
                const y = Math.sin(phi) * Math.sin(theta);
                const z = Math.cos(phi);
                points.push([x, y, z]);
            }
            return points;
        }

        // --- 3D VIEW LOGIC ---
        function update3DView() {
            // Clear previous points
            while (pointsGroup.children.length) {
                pointsGroup.remove(pointsGroup.children[0]);
            }
            pointMeshes = [];

            const solidGeom = new THREE.SphereGeometry(0.025, 16, 16);
            const ringGeom = new THREE.RingGeometry(0.02, 0.025, 32);
            const pointMat = new THREE.MeshBasicMaterial({ color: 0x0000ff });
            const ringMat = new THREE.MeshBasicMaterial({ color: 0x0000ff, side: THREE.DoubleSide });

            currentPointsCoords.forEach(coords => {
                const [x, y, z] = coords;

                const solid = new THREE.Mesh(solidGeom, pointMat);
                solid.position.set(x, y, z);
                pointsGroup.add(solid);

                const ring = new THREE.Mesh(ringGeom, ringMat);
                ring.position.set(x, y, z);
                pointsGroup.add(ring);

                pointMeshes.push({ solid, ring });
            });
        }

        // --- 2D VIEW LOGIC ---
        function update2DView() {
            const ctx = canvas2d.getContext('2d');
            const { width, height } = canvas2d;

            // Clear canvas
            ctx.clearRect(0, 0, width, height);

            // Draw points
            ctx.fillStyle = '#0000ff';
            currentPointsCoords.forEach(coords => {
                const [x, y, z] = coords;

                const theta = Math.atan2(y, x); // -PI to PI
                const phi = Math.acos(z);      // 0 to PI

                const canvasX = ((theta + Math.PI) / (2 * Math.PI)) * width;
                const canvasY = (phi / Math.PI) * height;

                ctx.beginPath();
                ctx.arc(canvasX, canvasY, 3.5, 0, 2 * Math.PI);
                ctx.fill();
            });

            // Calculate and display distances
            calculateAndDisplayDistances();
        }

        function calculateAndDisplayDistances() {
            if (currentPointsCoords.length < 2) {
                div2dText.innerHTML = '';
                return;
            }

            let minDistance = Infinity;
            for (let i = 0; i < currentPointsCoords.length; i++) {
                for (let j = i + 1; j < currentPointsCoords.length; j++) {
                    const p1 = currentPointsCoords[i];
                    const p2 = currentPointsCoords[j];
                    const distSq = (p1[0] - p2[0])**2 + (p1[1] - p2[1])**2 + (p1[2] - p2[2])**2;
                    const dist = Math.sqrt(distSq);
                    if (dist < minDistance) {
                        minDistance = dist;
                    }
                }
            }

            div2dText.innerHTML = `minimum distance between points = ${minDistance.toFixed(5)}<br>maximum distance between points = 2.`;
        }

        // --- ANIMATION LOOP ---
        const cameraWorldPos = new THREE.Vector3();
        const pointWorldPos = new THREE.Vector3();

        function animate() {
            requestAnimationFrame(animate);

            if (currentMode === '3d') {
                controls.update();

                camera.getWorldPosition(cameraWorldPos);

                pointMeshes.forEach(meshPair => {
                    const { solid, ring } = meshPair;

                    // Get the world position of the point
                    solid.getWorldPosition(pointWorldPos);

                    // Vector from sphere center (origin) to the point
                    const vectorToPoint = pointWorldPos.clone();
                    // Vector from sphere center (origin) to the camera
                    const vectorToCamera = cameraWorldPos.clone();

                    // Check if point is on the hemisphere facing the camera
                    const isFrontFacing = vectorToPoint.dot(vectorToCamera) > 0;

                    solid.visible = isFrontFacing;
                    ring.visible = !isFrontFacing;

                    if (!isFrontFacing) {
                        // Make the ring face the camera
                        ring.lookAt(camera.position);
                    }
                });

                renderer.render(scene, camera);
            }
        }

        // --- START THE APP ---
        document.addEventListener('DOMContentLoaded', init);

    </script>
</body>
</html>
