<body>

<div id="main-container">
    <div id="control-panel">
        <div class="control-group">
            <label for="slider-angle">angle relative to ecliptic</label>
            <input type="range" id="slider-angle" min="0" max="60" value="23.45" step="0.01">
            <span id="value-angle" class="value-display">23.45</span>
        </div>
        <div class="control-group">
            <label for="slider-longitude">longitude of periapsis</label>
            <input type="range" id="slider-longitude" min="0" max="360" value="102.95" step="0.01">
            <span id="value-longitude" class="value-display">102.95</span>
        </div>
        <div class="control-group">
            <label for="slider-eccentricity">orbit eccentricity</label>
            <input type="range" id="slider-eccentricity" min="0" max="0.99" value="0.016722" step="0.000001">
            <span id="value-eccentricity" class="value-display">0.016722</span>
        </div>
    </div>
    <div id="visualization-area">
        <div id="orbit-canvas-container"></div>
        <div id="plot-canvas-container"></div>
    </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', function() {

    // --- DOM Element References ---
    const sliderAngle = document.getElementById('slider-angle');
    const sliderLongitude = document.getElementById('slider-longitude');
    const sliderEccentricity = document.getElementById('slider-eccentricity');

    const valueAngle = document.getElementById('value-angle');
    const valueLongitude = document.getElementById('value-longitude');
    const valueEccentricity = document.getElementById('value-eccentricity');

    let orbitSketch;
    let plotSketch;

    // --- State Update and Redraw Logic ---
    function updateValuesAndRedraw() {
        // Update span displays
        valueAngle.textContent = parseFloat(sliderAngle.value).toFixed(2);
        valueLongitude.textContent = parseFloat(sliderLongitude.value).toFixed(2);
        valueEccentricity.textContent = parseFloat(sliderEccentricity.value).toFixed(6);

        // Redraw canvases
        if (orbitSketch) orbitSketch.redraw();
        if (plotSketch) plotSketch.redraw();
    }

    // --- p5.js Sketch for Orbit Visualization ---
    const orbitSketchP5 = (p) => {
        p.setup = () => {
            const container = document.getElementById('orbit-canvas-container');
            const canvas = p.createCanvas(container.offsetWidth, 100);
            canvas.parent('orbit-canvas-container');
            p.noLoop(); // Only redraw on slider change
            p.angleMode(p.DEGREES);
        };

        p.draw = () => {
            const e = parseFloat(sliderEccentricity.value);
            const omega = parseFloat(sliderLongitude.value);

            p.background('#f0f0f0');
            p.translate(p.width / 2, p.height / 2);
            p.rotate(omega);

            const a = p.width * 0.25; // Semi-major axis
            const b = a * p.sqrt(1 - e * e); // Semi-minor axis

            p.stroke('#a0522d');
            p.strokeWeight(2.5);
            p.noFill();
            p.ellipse(0, 0, a * 2, b * 2);
        };
    };

    // --- p5.js Sketch for Equation of Time Plot ---
    const plotSketchP5 = (p) => {
        let eotPoints = [];
        const margin = { top: 30, right: 20, bottom: 40, left: 60 };
        const resolution = 400; // Number of points to calculate

        p.setup = () => {
            const container = document.getElementById('plot-canvas-container');
            const canvas = p.createCanvas(container.offsetWidth, 400);
            canvas.parent('plot-canvas-container');
            p.noLoop();
        };

        p.draw = () => {
            calculateEOT();
            drawPlot();
        };

        function calculateEOT() {
            const obliquity = p.radians(parseFloat(sliderAngle.value));
            const eccentricity = parseFloat(sliderEccentricity.value);
            const periapsisLongitude = p.radians(parseFloat(sliderLongitude.value));

            const rawEOT = [];

            for (let i = 0; i <= resolution; i++) {
                const M = (p.TWO_PI * i) / resolution; // Mean Anomaly

                // 1. Solve Kepler's Equation for Eccentric Anomaly (E) using Newton's method
                let E = M; // Initial guess
                for (let j = 0; j < 8; j++) { // 8 iterations for precision
                    E = E - (E - eccentricity * p.sin(E) - M) / (1 - eccentricity * p.cos(E));
                }

                // 2. Calculate True Anomaly (nu)
                const nu = 2 * p.atan2(p.sqrt(1 + eccentricity) * p.sin(E / 2), p.sqrt(1 - eccentricity) * p.cos(E / 2));

                // 3. Calculate True Longitude (lambda)
                const lambda = nu + periapsisLongitude;

                // 4. Calculate Right Ascension (alpha)
                const alpha = p.atan2(p.cos(obliquity) * p.sin(lambda), p.cos(lambda));

                // 5. Calculate Equation of Time in radians
                const eot_rad = M - alpha;
                rawEOT.push({ M: M, val: eot_rad });
            }

            // 6. Phase Unwrapping
            eotPoints = [];
            let offset = 0;
            let prev_eot_rad = rawEOT[0].val;

            eotPoints.push({ M: rawEOT[0].M, val: rawEOT[0].val });

            for (let i = 1; i < rawEOT.length; i++) {
                const current_eot_rad = rawEOT[i].val;
                const diff = current_eot_rad - prev_eot_rad;
                if (diff > p.PI) {
                    offset -= p.TWO_PI;
                } else if (diff < -p.PI) {
                    offset += p.TWO_PI;
                }

                // 7. Convert to minutes
                const unwrapped_eot = current_eot_rad + offset;
                const eot_minutes = unwrapped_eot * (720 / p.PI);
                eotPoints.push({ M: rawEOT[i].M, val: eot_minutes });

                prev_eot_rad = current_eot_rad;
            }
        }

        function drawPlot() {
            p.background(255);
            const plotWidth = p.width - margin.left - margin.right;
            const plotHeight = p.height - margin.top - margin.bottom;

            // Determine Y-axis scale
            let minY = Infinity, maxY = -Infinity;
            for (const pt of eotPoints) {
                if (p.isFinite(pt.val)) {
                    if (pt.val < minY) minY = pt.val;
                    if (pt.val > maxY) maxY = pt.val;
                }
            }
            if (minY === Infinity) { // Handle case with no valid points
                minY = -15; maxY = 15;
            }

            const yRange = maxY - minY;
            const yPadding = yRange === 0 ? 10 : yRange * 0.1;
            const yAxisMin = p.floor((minY - yPadding) / 5) * 5;
            const yAxisMax = p.ceil((maxY + yPadding) / 5) * 5;

            // Draw Axes
            p.stroke(0);
            p.strokeWeight(1);
            p.line(margin.left, margin.top, margin.left, p.height - margin.bottom); // Y-axis
            p.line(margin.left, p.height - margin.bottom, p.width - margin.right, p.height - margin.bottom); // X-axis

            // Draw Gridlines and Labels
            p.textAlign(p.RIGHT, p.CENTER);
            p.noStroke();
            p.fill(0);
            p.textSize(12);
            const yTickCount = Math.min(10, Math.round((yAxisMax-yAxisMin)/5));
            if (yTickCount > 0) {
                 for (let i = 0; i <= yTickCount; i++) {
                    const val = yAxisMin + i * (yAxisMax - yAxisMin) / yTickCount;
                    const y = p.map(val, yAxisMin, yAxisMax, p.height - margin.bottom, margin.top);
                    p.text(Math.round(val), margin.left - 8, y);
                    p.stroke(220); // Light gray gridlines
                    p.strokeWeight(1);
                    p.line(margin.left, y, p.width - margin.right, y);
                }
            }


            p.textAlign(p.CENTER, p.TOP);
            p.noStroke();
            p.fill(0);
            for (let i = 0; i <= 6; i++) {
                const x = p.map(i, 0, p.TWO_PI, margin.left, p.width - margin.right);
                p.text(i, x, p.height - margin.bottom + 8);
            }

            // Draw axis line over gridlines at y=0
            const yZero = p.map(0, yAxisMin, yAxisMax, p.height - margin.bottom, margin.top);
            if (yZero >= margin.top && yZero <= p.height - margin.bottom) {
                p.stroke(100);
                p.strokeWeight(1);
                p.line(margin.left, yZero, p.width - margin.right, yZero);
            }

            // Draw Axis Titles
            p.push();
            p.translate(margin.left / 2 - 10, margin.top + plotHeight / 2);
            p.rotate(-p.HALF_PI);
            p.textAlign(p.CENTER, p.CENTER);
            p.noStroke();
            p.fill(0);
            p.text("minutes", 0, 0);
            p.pop();

            // Draw Curve
            p.stroke('#ff8c00');
            p.strokeWeight(2.5);
            p.noFill();
            p.beginShape();
            for (let i = 0; i < eotPoints.length; i++) {
                const pt = eotPoints[i];
                if (!p.isFinite(pt.val)) continue;

                const x = p.map(pt.M, 0, p.TWO_PI, margin.left, p.width - margin.right);
                const y = p.map(pt.val, yAxisMin, yAxisMax, p.height - margin.bottom, margin.top);

                // Detect and handle discontinuities
                if (i > 0) {
                    const prevPt = eotPoints[i-1];
                    const prevY = p.map(prevPt.val, yAxisMin, yAxisMax, p.height - margin.bottom, margin.top);
                    if (p.abs(y - prevY) > plotHeight * 0.8) {
                        p.endShape();
                        p.beginShape();
                    }
                }

                p.vertex(x, y);
            }
            p.endShape();
        }
    };

    // --- Initialization ---
    orbitSketch = new p5(orbitSketchP5);
    plotSketch = new p5(plotSketchP5);

    sliderAngle.addEventListener('input', updateValuesAndRedraw);
    sliderLongitude.addEventListener('input', updateValuesAndRedraw);
    sliderEccentricity.addEventListener('input', updateValuesAndRedraw);

    // Initial draw
    updateValuesAndRedraw();
});
</script>

</body>
</html>
