                </div>
            </div>

            <div class="control-group">
                <label for="slider-h_C">h<sub>C</sub></label>
                <div class="slider-container">
                    <input type="range" id="slider-h_C" min="0.1" max="5" step="0.1" value="1.9">
                    <span id="h_C-value">1.9</span>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-delta">&delta; = &alpha; - &beta;</label>
                <div class="slider-container">
                    <input type="range" id="slider-delta" min="-1.5" max="1.5" step="0.01" value="0.5">
                    <span id="delta-value">0.5</span>
                </div>
            </div>

            <div class="control-group">
                <label>steps</label>
                <div class="steps-group">
                    <input type="radio" id="step1" name="steps" value="1"><label for="step1">1</label>
                    <input type="radio" id="step2" name="steps" value="2"><label for="step2">2</label>
                    <input type="radio" id="step3" name="steps" value="3"><label for="step3">3</label>
                    <input type="radio" id="step4" name="steps" value="4"><label for="step4">4</label>
                    <input type="radio" id="step5" name="steps" value="5" checked><label for="step5">5</label>
                </div>
            </div>

            <div class="control-group">
                <div class="checkbox-container">
                    <input type="checkbox" id="check-verification" checked>
                    <label for="check-verification">verification</label>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-plot-range">plot range</label>
                <div class="slider-container">
                    <input type="range" id="slider-plot-range" min="0.5" max="2" step="0.01" value="1">
                    <span id="plot-range-value">1</span>
                </div>
            </div>
        </div>
        <div id="canvas-container" class="vis-area">
            <!-- p5.js canvas will be attached here -->
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // HTML Control Elements
            let sliderC, cValueSpan;
            let sliderHC, hCValueSpan;
            let sliderDelta, deltaValueSpan;
            let stepRadios;
            let checkVerification;
            let sliderPlotRange, plotRangeValueSpan;

            // Global scale factor for zooming
            let scaleFactor = 50;

            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                canvas.parent(canvasContainer);

                // Initialize control elements
                sliderC = p.select('#slider-c');
                cValueSpan = p.select('#c-value');
                sliderHC = p.select('#slider-h_C');
                hCValueSpan = p.select('#h_C-value');
                sliderDelta = p.select('#slider-delta');
                deltaValueSpan = p.select('#delta-value');
                stepRadios = p.selectAll('input[name="steps"]');
                checkVerification = p.select('#check-verification');
                sliderPlotRange = p.select('#slider-plot-range');
                plotRangeValueSpan = p.select('#plot-range-value');

                // Attach event listeners
                const allControls = [sliderC, sliderHC, sliderDelta, checkVerification, sliderPlotRange, ...stepRadios];
                allControls.forEach(control => control.input(redrawScene));

                // Update span values on input
                sliderC.input(() => cValueSpan.html(sliderC.value()));
                sliderHC.input(() => hCValueSpan.html(sliderHC.value()));
                sliderDelta.input(() => deltaValueSpan.html(sliderDelta.value()));
                sliderPlotRange.input(() => plotRangeValueSpan.html(p.float(sliderPlotRange.value()).toFixed(2)));

                p.noLoop(); // Use redraw on demand
                redrawScene(); // Initial draw
            };

            p.windowResized = () => {
                const canvasContainer = document.getElementById('canvas-container');
                p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                redrawScene();
            };

            function redrawScene() {
                p.redraw();
            }

            p.draw = () => {
                // 1. Clear and get inputs
                p.background('#E0E0E0');
                const c = p.float(sliderC.value());
                const hC = p.float(sliderHC.value());
                const delta = p.float(sliderDelta.value());
                const plotRange = p.float(sliderPlotRange.value());
                const showVerification = checkVerification.checked();
                let currentStep = 1;
                for (const radio of stepRadios) {
                    if (radio.elt.checked) {
                        currentStep = p.int(radio.value());
                    }
                }

                // 2. Setup coordinate system
                p.push();
                p.translate(p.width / 2, p.height * 0.7); // Origin at center-bottom
                const baseScale = 50;
                scaleFactor = baseScale * plotRange;
                p.scale(scaleFactor, -scaleFactor); // Scale and flip Y-axis

                // 3. Geometric calculations
                const F = p.createVector(0, 0);
                const C = p.createVector(0, hC);
                const D = p.createVector(c / 2, 0);
                const K = p.createVector(c, 0);

                // Solve for M
                let M, R, B, A, E;
                try {
                    const t = p.tan(delta);
                    const a_ = (2 * hC) / c;
                    const b_ = (c * c / 4 - hC * hC) / c;

                    const A_quad = t * (1 + a_ * a_);
                    const B_quad = t * (2 * a_ * b_ - (3 * a_ * c) / 2) - c / 2;
                    const C_quad = t * (b_ * b_ - (3 * b_ * c) / 2 + (c * c) / 2);

                    if (p.abs(A_quad) < 1e-9) { // Handle linear case (delta approaches 0)
                        const yM = -C_quad / B_quad;
                        const xM = a_ * yM + b_;
                        M = p.createVector(xM, yM);
                    } else {
                        const discriminant = B_quad * B_quad - 4 * A_quad * C_quad;
                        if (discriminant < 0) throw new Error("No real solution for M");

                        const sqrt_discriminant = p.sqrt(discriminant);
                        const yM1 = (-B_quad + sqrt_discriminant) / (2 * A_quad);
                        const yM2 = (-B_quad - sqrt_discriminant) / (2 * A_quad);

                        // Select the negative root as M is below the x-axis
                        const yM = yM1 < 0 ? yM1 : yM2;
                        const xM = a_ * yM + b_;
                        M = p.createVector(xM, yM);
                    }

                    R = M.dist(C);
                    const xB = 2 * M.x - c / 2;
                    B = p.createVector(xB, 0);
                    A = p.createVector(xB - c, 0);
                    E = p.createVector(c - xB, 0);

                } catch (error) {
                    console.error(error);
                    p.pop(); // End transform
                    p.fill(255, 0, 0);
                    p.noStroke();
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text("Invalid parameters: No solution found.", p.width / 2, p.height / 2);
                    return;
                }

                // 4. Rendering
                // Helper functions for drawing
                const drawPoint = (pt, label) => {
                    p.push();
                    p.strokeWeight(1 / scaleFactor);
                    p.fill(0);
                    p.ellipse(pt.x, pt.y, 6 / scaleFactor, 6 / scaleFactor);
                    p.pop();

                    p.push();
                    p.translate(pt.x, pt.y);
                    p.scale(1 / scaleFactor, -1 / scaleFactor);
                    p.fill(0);
                    p.noStroke();
                    p.textSize(14);
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(label, 0, 10);
                    p.pop();
                };

                const drawDashedLine = (p1, p2) => {
                    p.push();
                    p.stroke(0);
                    p.strokeWeight(1 / scaleFactor);
                    p.drawingContext.setLineDash([5 / scaleFactor, 5 / scaleFactor]);
                    p.line(p1.x, p1.y, p2.x, p2.y);
                    p.drawingContext.setLineDash([]);
                    p.pop();
                };

                // Cumulative drawing based on step
                if (currentStep >= 1) {
                    // Line λ
                    p.stroke(0);
                    p.strokeWeight(1 / scaleFactor);
                    p.line(-p.width, 0, p.width, 0);
                    drawLabel(p.createVector(-p.width / (2 * scaleFactor) * 0.8, 0), 'λ');

                    // Points F, C, D
                    drawPoint(F, 'F');
                    drawPoint(C, 'C');
                    drawPoint(D, 'D');

                    // Line FC (altitude)
                    p.stroke(0);
                    p.strokeWeight(1.5 / scaleFactor);
                    p.line(F.x, F.y, C.x, C.y);
                }

                if (currentStep >= 2) {
                    drawPoint(K, 'K');
                    drawDashedLine(C, D);
                    drawDashedLine(D, K);

                    drawPoint(M, 'M');
                    drawDashedLine(M, C);
                    drawDashedLine(M, D);
                    drawDashedLine(M, K);

                    // Arc κ
                    p.push();
                    p.noFill();
                    p.stroke(0);
                    p.strokeWeight(1.5 / scaleFactor);
                    // Draw a large arc segment
                    const startAngle = p.atan2(C.y - M.y, A.x - M.x) - 0.5;
                    const endAngle = p.atan2(C.y-M.y, K.x - M.x + R);
                    let safeEndAngle = p.PI + (p.PI - startAngle) + 0.5;
                    p.arc(M.x, M.y, 2 * R, 2 * R, p.PI - 0.5, p.TWO_PI + 0.5);
                    drawLabel(p.createVector(M.x - R*0.9, M.y + R*0.3), 'κ');
                    p.pop();
                }

                if (currentStep >= 3) {
                    drawPoint(B, 'B');
                }

                if (currentStep >= 4) {
                    drawPoint(A, 'A');
                }

                if (currentStep >= 5) {
                    p.push();
                    p.stroke(0);
                    p.strokeWeight(4 / scaleFactor);
                    p.line(A.x, A.y, B.x, B.y);
                    p.line(B.x, B.y, C.x, C.y);
                    p.line(C.x, C.y, A.x, A.y);
                    p.pop();
                }

                if (showVerification) {
                    drawPoint(E, 'E');
                    drawDashedLine(C, E);
                    drawDashedLine(K, B);
                }

                p.pop(); // End main transform
            };

            // Helper to draw labels that are not flipped or scaled
            function drawLabel(pt, label) {
                p.push();
                p.translate(pt.x, pt.y);
                p.scale(1 / scaleFactor, -1 / scaleFactor);
                p.fill(0);
                p.noStroke();
                p.textSize(16);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(label, -10, 0);
                p.pop();
            }
        };

        new p5(sketch, 'canvas-container');
    </script>
</body>
</html>
