<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Scientific Visualization Demo</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
            background-color: #f0f0f0;
            display: flex;
            justify-content: center;
            align-items: flex-start;
            margin: 0;
            padding: 20px;
        }
        #main-container {
            width: 100%;
            max-width: 850px;
            padding: 20px;
            border: 1px solid #dcdcdc;
            background-color: #fff;
            box-shadow: 0 4px 8px rgba(0,0,0,0.1);
            border-radius: 8px;
        }
        #control-panel {
            display: flex;
            align-items: center;
            gap: 15px;
            padding: 0 10px 15px 10px;
        }
        #slider-z {
            flex-grow: 1;
            max-width: 500px;
            cursor: pointer;
        }
        #label-z {
            font-family: monospace;
            font-size: 1.1em;
            min-width: 80px; /* Prevents layout shift on value change */
        }
        label[for="slider-z"] {
            font-weight: bold;
        }
        .plot-container {
            width: 100%;
            height: 280px; /* Give plots a fixed height */
        }
    </style>
</head>
<body>

    <div id="main-container">
        <div id="control-panel">
            <label for="slider-z">z (m)</label>
            <input type="range" id="slider-z" min="0" max="32" value="0" step="0.01">
            <span id="label-z">0.</span>
        </div>
        <div id="plots-container">
            <div id="plot-intensity" class="plot-container"></div>
            <div id="plot-population" class="plot-container"></div>
            <div id="plot-coherence" class="plot-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // --- 1. Get DOM Elements ---
            const sliderZ = document.getElementById('slider-z');
            const labelZ = document.getElementById('label-z');
            const plotDivs = {
                intensity: 'plot-intensity',
                population: 'plot-population',
                coherence: 'plot-coherence'
            };

            // --- 2. Constants and Data Generation ---
            const t_values = Array.from({ length: 301 }, (_, i) => i - 50);

            // Red curve functions (z=0, static)
            const I_red = t => (t < -15 || t > 170) ? 0 : (t > 15 ? 1 : 0.5 * (1 + Math.tanh(t / 4)));
            const P_red = t => (t < -15) ? 0 : 4.5 * (1 - Math.exp(-(t + 15) / 50));
            const C_red = t => (t < -15) ? 0 : 2.2 * (1 - Math.exp(-(t + 15) / 50));

            // Blue curve functions (dynamic, function of z)
            const I_blue = (t, z) => {
                const t_delay = z / 0.3;
                if (t > 170 + t_delay) return null;
                const t_prime = t - t_delay;
                const I_base = (t_prime < -15) ? 0 : (t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime / 4)));
                const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
                return I_base * envelope;
            };
            const P_blue = (t, z) => {
                const t_delay = z / 0.3;
                if (t > 170 + t_delay) return null;
                const t_prime = t - t_delay;
                const scaling_factor_p = 1 - 0.45 * (z / 32);
                return scaling_factor_p * ((t_prime < -15) ? 0 : 4.5 * (1 - Math.exp(-(t_prime + 15) / 50)));
            };
            const C_blue = (t, z) => {
                const t_delay = z / 0.3;
                if (t > 170 + t_delay) return null;
                const t_prime = t - t_delay;
                const scaling_factor_c = 1 - 0.45 * (z / 32);
                return scaling_factor_c * ((t_prime < -15) ? 0 : 2.2 * (1 - Math.exp(-(t_prime + 15) / 50)));
            };

            // Calculate initial data (z=0)
            const initialData = {
                intensity: t_values.map(I_red),
                population: t_values.map(P_red),
                coherence: t_values.map(C_red)
            };

            // --- 3. Plotly Configuration ---
            const createPlotConfig = (yData, layoutOptions) => {
                const traces = [
                    {
                        x: t_values,
                        y: yData,
                        name: 'z = 0 meters',
                        mode: 'lines',
                        line: { color: 'red', width: 2 }
                    },
                    {
                        x: t_values,
                        y: yData, // Initially same as red line
                        name: 'z = 0. meters',
                        mode: 'lines',
                        line: { color: 'blue', width: 2, dash: 'dash' }
                    }
                ];

                const baseLayout = {
                    xaxis: { title: "time (ns)", range: [-50, 250], zeroline: true },
                    yaxis: { zeroline: true },
                    margin: { l: 80, r: 20, t: 40, b: 50 },
                    showlegend: true,
                    legend: { x: 0.7, y: 0.95, xanchor: 'left', yanchor: 'top' },
                    shapes: [
                        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 1.5, dash: 'dash' } },
                        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 1.5, dash: 'dash' } }
                    ],
                    annotations: []
                };

                // Deep merge layout options
                const finalLayout = JSON.parse(JSON.stringify(baseLayout));
                Object.assign(finalLayout.yaxis, layoutOptions.yaxis);
                finalLayout.annotations = layoutOptions.annotations;

                return { traces, layout: finalLayout };
            };

            const plotConfigs = {
                intensity: createPlotConfig(initialData.intensity, {
                    yaxis: { title: 'intensity (arb)', range: [-0.1, 1.1] },
                    annotations: [
                        { x: -45, y: 0.9, text: '(a)', showarrow: false, font: {size: 14} },
                        { x: 5, y: 0.7, text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom'}
                    ]
                }),
                population: createPlotConfig(initialData.population, {
                    yaxis: { title: 'excited-state population × 10<sup>4</sup>', range: [-0.5, 5.5] },
                    annotations: [{ x: -45, y: 4.5, text: '(b)', showarrow: false, font: {size: 14} }]
                }),
                coherence: createPlotConfig(initialData.coherence, {
                    yaxis: { title: 'coherence × 10<sup>2</sup>', range: [-0.2, 2.4] },
                    annotations: [{ x: -45, y: 2.1, text: '(c)', showarrow: false, font: {size: 14} }]
                })
            };

            // --- 4. Plot Initialization ---
            function init() {
                Plotly.newPlot(plotDivs.intensity, plotConfigs.intensity.traces, plotConfigs.intensity.layout, {responsive: true});
                Plotly.newPlot(plotDivs.population, plotConfigs.population.traces, plotConfigs.population.layout, {responsive: true});
                Plotly.newPlot(plotDivs.coherence, plotConfigs.coherence.traces, plotConfigs.coherence.layout, {responsive: true});

                sliderZ.addEventListener('input', handleSliderChange);
            }

            // --- 5. Interaction Handler & Update Logic ---
            function updateLabel(z) {
                if (Number.isInteger(z)) {
                    labelZ.textContent = z + '.';
                } else {
                    // Match screenshot precision for some values, fallback to toFixed(5)
                    const specialCases = { 7.02: '7.02439', 16.39: '16.3902' };
                    const zRounded = z.toFixed(2);
                    labelZ.textContent = specialCases[zRounded] || z.toFixed(5);
                }
            }

            function updatePlots(z) {
                updateLabel(z);

                const t_delay = z / 0.3;

                // Data for update
                const newData = {
                    intensity: t_values.map(t => I_blue(t, z)),
                    population: t_values.map(t => P_blue(t, z)),
                    coherence: t_values.map(t => C_blue(t, z)),
                };

                // Update trace data and legend name
                const traceUpdate = {
                    name: [`z = ${z.toFixed(2)} meters`]
                };

                // Update shape position
                const layoutUpdate = {
                    'shapes[1].x0': t_delay,
                    'shapes[1].x1': t_delay
                };

                // Loop through plots to update them
                for (const key in plotDivs) {
                    traceUpdate.y = [newData[key]];
                    Plotly.restyle(plotDivs[key], traceUpdate, [1]);
                    Plotly.relayout(plotDivs[key], layoutUpdate);
                }
            }

            function handleSliderChange(event) {
                const z = parseFloat(event.target.value);
                updatePlots(z);
            }

            // --- 6. Run Initialization ---
            init();

        });
    </script>
</body>
</html>
