    <div id="canvas-container">
        <!-- p5.js canvas will be inserted here -->
    </div>

</div>

<script>
    // --- p5.js Sketch ---

    // State variables
    let eccentricity, rotationAngle, refFrame, envelopeType;

    // Point arrays for curves
    let epitrochoidPoints = [];
    let innerEnvelopePoints = [];
    let outerEnvelopePoints = [];
    const NUM_POINTS = 400;

    // DOM Elements
    let sliderEcc, labelEcc, sliderRot, labelRot;
    let refFrameRadios, envelopeTypeRadios;

    function setup() {
        let canvas = createCanvas(600, 600);
        canvas.parent('canvas-container');

        // Get DOM elements
        sliderEcc = document.getElementById('slider-eccentricity');
        labelEcc = document.getElementById('label-eccentricity');
        sliderRot = document.getElementById('slider-rotation');
        labelRot = document.getElementById('label-rotation');
        refFrameRadios = document.querySelectorAll('input[name="ref-frame"]');
        envelopeTypeRadios = document.querySelectorAll('input[name="envelope-type"]');

        // Attach event listeners
        sliderEcc.addEventListener('input', () => {
            updateState();
            calculateCurves();
        });
        sliderRot.addEventListener('input', updateState);
        refFrameRadios.forEach(radio => radio.addEventListener('change', updateState));
        envelopeTypeRadios.forEach(radio => radio.addEventListener('change', updateState));

        // Initial state update and curve calculation
        updateState();
        calculateCurves();
    }

    function updateState() {
        eccentricity = parseFloat(sliderEcc.value);
        rotationAngle = parseFloat(sliderRot.value);

        for (const radio of refFrameRadios) {
            if (radio.checked) {
                refFrame = radio.value;
                break;
            }
        }
        for (const radio of envelopeTypeRadios) {
            if (radio.checked) {
                envelopeType = radio.value;
                break;
            }
        }

        // Update labels
        labelEcc.textContent = eccentricity.toFixed(4);
        labelRot.textContent = rotationAngle.toFixed(4);
    }

    function calculateCurves() {
        epitrochoidPoints = [];
        innerEnvelopePoints = [];
        outerEnvelopePoints = [];

        const k = eccentricity;
        const R_rotor = 1.0; 

        for (let i = 0; i <= NUM_POINTS; i++) {
            const t = map(i, 0, NUM_POINTS, 0, TWO_PI);

            // Blue Curve (Epitrochoid/Housing) - A hypotrochoid which forms the housing
            const epi_x = cos(t) - k * cos(3 * t);
            const epi_y = sin(t) - k * sin(3 * t);
            epitrochoidPoints.push(createVector(epi_x, epi_y));

            // Orange Curve (Inner Envelope/Rotor)
            const inner_x = (2 / 3) * R_rotor * cos(t) + k * cos(2 * t);
            const inner_y = (2 / 3) * R_rotor * sin(t) - k * sin(2 * t);
            innerEnvelopePoints.push(createVector(inner_x, inner_y));

            // Orange Curve (Outer Envelope)
            const outer_x = (2 / 3) * R_rotor * cos(t) - k * cos(2 * t);
            const outer_y = (2 / 3) * R_rotor * sin(t) + k * sin(2 * t);
            outerEnvelopePoints.push(createVector(outer_x, outer_y));
        }
    }


    function draw() {
        background(255);

        // --- Dynamic Scaling ---
        let maxExtent = 1.2; // Default extent
        if (epitrochoidPoints.length > 0) {
            let housingMaxR = 0;
            let rotorMaxR = 0;

            epitrochoidPoints.forEach(p => housingMaxR = max(housingMaxR, p.mag()));

            if (envelopeType === 'inner' || envelopeType === 'both') {
                innerEnvelopePoints.forEach(p => rotorMaxR = max(rotorMaxR, p.mag()));
            }
            if (envelopeType === 'outer' || envelopeType === 'both') {
                outerEnvelopePoints.forEach(p => rotorMaxR = max(rotorMaxR, p.mag()));
            }

            // The total extent depends on the reference frame
            if (refFrame === 'envelope') {
                 maxExtent = max(rotorMaxR, housingMaxR + eccentricity);
            } else { // epitrochoid or fixed centers
                 maxExtent = max(housingMaxR, rotorMaxR + eccentricity);
            }
        }
        const dynamicScale = (width / 2) / (maxExtent * 1.1); // 10% padding

        // --- Drawing ---
        translate(width / 2, height / 2);

        drawAxes(dynamicScale, maxExtent);

        // Set Cartesian coordinates (y-axis up)
        scale(1, -1);
        scale(dynamicScale);


        // --- Kinematics ---
        const shaftAngle = rotationAngle;
        const rotorAngle = -shaftAngle / 3.0; // Rotor rotates at 1/3 speed in opposite direction
        const rotorCenterX = eccentricity * cos(shaftAngle);
        const rotorCenterY = eccentricity * sin(shaftAngle);

        // --- Reference Frame Logic ---
        push(); // Save global transform state

        if (refFrame === 'epitrochoid' || refFrame === 'fixed centers') {
            // Housing (blue) is stationary
            drawCurve(epitrochoidPoints, color(50, 100, 200), 2);

            // Rotor (orange) moves
            push();
            translate(rotorCenterX, rotorCenterY);
            rotate(rotorAngle);
            drawEnvelopes();
            pop();

        } else if (refFrame === 'envelope') {
            // Rotor (orange) is stationary
            drawEnvelopes();

            // Housing (blue) moves
            push();
            // Apply inverse transformation to housing
            rotate(-rotorAngle);
            translate(-rotorCenterX, -rotorCenterY);
            drawCurve(epitrochoidPoints, color(50, 100, 200), 2);
            pop();
        }

        pop(); // Restore global transform state
    }

    function drawEnvelopes() {
        if (envelopeType === 'inner' || envelopeType === 'both') {
            drawCurve(innerEnvelopePoints, color(230, 150, 0), 2);
        }
        if (envelopeType === 'outer' || envelopeType === 'both') {
            drawCurve(outerEnvelopePoints, color(230, 150, 0), 2);
        }
    }

    function drawCurve(points, strokeColor, weight) {
        if (points.length < 2) return;
        stroke(strokeColor);
        strokeWeight(weight / (width / 200)); // Adjust stroke weight for zoom
        noFill();
        beginShape();
        for (const p of points) {
            vertex(p.x, p.y);
        }
        endShape(CLOSE);
    }

    function drawAxes(currentScale, maxUnit) {
        stroke(150);
        strokeWeight(1);

        // Draw axis lines
        line(-width / 2, 0, width / 2, 0); // X-axis
        line(0, -height / 2, 0, height / 2); // Y-axis

        // Draw ticks and labels
        textAlign(CENTER, CENTER);
        textSize(10);
        noStroke();
        fill(100);

        const tickStep = pow(10, floor(log(maxUnit * 2) / log(10))) / 2;
        const tickLength = 5;

        // X-axis ticks
        for (let x = tickStep; x * currentScale < width / 2; x += tickStep) {
            stroke(150);
            line(x * currentScale, -tickLength, x * currentScale, tickLength);
            line(-x * currentScale, -tickLength, -x * currentScale, tickLength);
            noStroke();
            text(x.toFixed(1), x * currentScale, 15);
            text((-x).toFixed(1), -x * currentScale, 15);
        }

        // Y-axis ticks
        textAlign(RIGHT, CENTER);
        for (let y = tickStep; y * currentScale < height / 2; y += tickStep) {
            stroke(150);
            line(-tickLength, y * currentScale, tickLength, y * currentScale);
            line(-tickLength, -y * currentScale, tickLength, -y * currentScale);
            noStroke();
            text(y.toFixed(1), -10, -y * currentScale);
            text((-y).toFixed(1), -10, y * currentScale);
        }
    }

</script>

</body>
</html>
