<script>
    document.addEventListener('DOMContentLoaded', function() {
        // --- DOM ELEMENT REFERENCES ---
        const sliders = {
            phi: document.getElementById('slider-phi'),
            gammaSat: document.getElementById('slider-gamma-sat'),
            r: document.getElementById('slider-r'),
            rpe: document.getElementById('slider-rpe'),
            mw: document.getElementById('slider-mw'),
            phiAxis: document.getElementById('slider-phi-axis'),
            cAxis: document.getElementById('slider-c-axis'),
        };

        const displays = {
            phi: document.getElementById('phi-value-display'),
            gammaSat: document.getElementById('gamma-sat-value-display'),
            r: document.getElementById('r-value-display'),
            rpe: document.getElementById('rpe-value-display'),
            mw: document.getElementById('mw-value-display'),
            phiAxis: document.getElementById('phi-axis-value-display'),
            cAxis: document.getElementById('c-axis-value-display'),
            phimax: document.getElementById('phimax-display'),
            range: document.getElementById('range-display'),
        };

        const plotContainer = document.getElementById('plot-container');

        // --- CONSTANTS ---
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        // --- CORE FORMULAS ---
        const y1 = (phi, gamma_sat, r) => (3 * gamma_sat / r) * C_UNIT_CONVERSION * phi;
        const y2 = (phi, gamma_sat, r) => y1(phi, gamma_sat, r) / (1 - phi);
        const y3 = (phi, gamma_sat, r, mw, r_pe) => y1(phi, gamma_sat, r) + (K_DEPLETION * mw / (r_pe ** 3)) / phi;

        const calculatePhiMax = (gamma_sat, r, mw, r_pe) => {
            const A = (3 * gamma_sat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (r_pe ** 3);
            if (A <= 0 || B <= 0) return 0.0001; // Avoid division by zero or negative sqrt
            return Math.sqrt(B / A);
        };

        // --- STATE MANAGEMENT ---
        let state = {};

        function readAllState() {
            state.phi = parseFloat(sliders.phi.value);
            state.gammaSat = parseFloat(sliders.gammaSat.value);
            state.r = parseFloat(sliders.r.value);
            state.rpe = parseFloat(sliders.rpe.value);
            state.mw = parseFloat(sliders.mw.value);
            state.phiAxis = parseFloat(sliders.phiAxis.value);
            state.cAxis = parseFloat(sliders.cAxis.value);
        }

        // --- UPDATE FUNCTIONS ---

        function updateDisplays() {
            displays.phi.textContent = state.phi.toFixed(4);
            displays.gammaSat.textContent = state.gammaSat.toFixed(1);
            displays.r.textContent = state.r.toFixed(0);
            displays.rpe.textContent = state.rpe.toFixed(0);
            displays.mw.textContent = state.mw.toFixed(0);
            displays.phiAxis.textContent = state.phiAxis.toFixed(2);
            displays.cAxis.textContent = state.cAxis.toFixed(1);
        }

        function updatePlot() {
            readAllState();
            updateDisplays();

            // Update phi slider's max range
            sliders.phi.max = state.phiAxis;
            if (state.phi > state.phiAxis) {
                sliders.phi.value = state.phiAxis;
                state.phi = state.phiAxis;
                displays.phi.textContent = state.phi.toFixed(4);
            }

            const phi_max = calculatePhiMax(state.gammaSat, state.r, state.mw, state.rpe);

            // Generate data for lines
            const num_points = 200;
            const phi_array = Array.from({ length: num_points }, (_, i) => 
                (i / (num_points - 1)) * state.phiAxis
            ).filter(p => p > 1e-6); // Avoid phi=0 for y3

            const y1_data = phi_array.map(p => y1(p, state.gammaSat, state.r));
            const y2_data = phi_array.map(p => y2(p, state.gammaSat, state.r));
            const y3_data = phi_array.map(p => y3(p, state.gammaSat, state.r, state.mw, state.rpe));

            // Generate data for shaded region
            const phi_fill_array = phi_array.filter(p => p <= phi_max && p <= state.phiAxis);
            if (phi_fill_array.length > 0 && phi_fill_array[phi_fill_array.length-1] < phi_max && phi_max < state.phiAxis) {
                phi_fill_array.push(phi_max); // Ensure the fill goes exactly to phi_max
            }
            const y2_fill_data = phi_fill_array.map(p => y2(p, state.gammaSat, state.r));
            const y3_fill_data = phi_fill_array.map(p => y3(p, state.gammaSat, state.r, state.mw, state.rpe));

            const fill_trace_x = [...phi_fill_array, ...phi_fill_array.slice().reverse()];
            const fill_trace_y = [...y2_fill_data, ...y3_fill_data.slice().reverse()];

            // --- PLOTLY TRACES ---
            const trace_fill = {
                x: fill_trace_x,
                y: fill_trace_y,
                type: 'scatter',
                mode: 'lines',
                fill: 'toself',
                fillcolor: 'rgba(173, 216, 230, 0.5)',
                line: { width: 0 },
                hoverinfo: 'none',
                showlegend: false
            };

            const trace_y1 = {
                x: phi_array, y: y1_data, type: 'scatter', mode: 'lines', name: 'y1',
                line: { color: 'blue', width: 2.5 }
            };
            const trace_y2 = {
                x: phi_array, y: y2_data, type: 'scatter', mode: 'lines', name: 'y2',
                line: { color: 'red', width: 2.5 }
            };
            const trace_y3 = {
                x: phi_array, y: y3_data, type: 'scatter', mode: 'lines', name: 'y3',
                line: { color: 'green', width: 2.5 }
            };

            const marker_x = state.phi;
            const trace_markers = {
                x: [marker_x, marker_x, marker_x],
                y: [
                    y1(marker_x, state.gammaSat, state.r),
                    y2(marker_x, state.gammaSat, state.r),
                    y3(marker_x, state.gammaSat, state.r, state.mw, state.rpe)
                ],
                type: 'scatter', mode: 'markers', name: 'markers',
                marker: { color: ['blue', 'red', 'green'], size: 10, line: {color: 'white', width: 1} }
            };

            const data = [trace_fill, trace_y1, trace_y2, trace_y3, trace_markers];

            // --- PLOTLY LAYOUT ---
            const layout = {
                xaxis: { title: 'ϕ (volume fraction)', range: [0, state.phiAxis], zeroline: false },
                yaxis: { title: 'C (kg/m³)', range: [0, state.cAxis], zeroline: false },
                showlegend: false,
                margin: { l: 60, r: 20, b: 50, t: 20 },
                shapes: [{
                    type: 'line',
                    x0: state.phi, x1: state.phi,
                    y0: 0, y1: state.cAxis,
                    line: { color: 'grey', width: 1.5, dash: 'dash' }
                }]
            };

            Plotly.react(plotContainer, data, layout);

            // Update info displays
            displays.phimax.innerHTML = `\\(\\phi_{max} = ${phi_max.toFixed(4)}\\)`;
            MathJax.typesetPromise([displays.phimax]).catch(err => console.log(err));
            updateRangeDisplay();
        }

        function updateInteractiveElements() {
            state.phi = parseFloat(sliders.phi.value);
            displays.phi.textContent = state.phi.toFixed(4);

            const marker_y = [
                y1(state.phi, state.gammaSat, state.r),
                y2(state.phi, state.gammaSat, state.r),
                y3(state.phi, state.gammaSat, state.r, state.mw, state.rpe)
            ];

            // Update markers
            Plotly.restyle(plotContainer, {
                x: [[state.phi, state.phi, state.phi]],
                y: [marker_y]
            }, [4]); // Marker trace is the 5th one (index 4)

            // Update vertical line
            Plotly.relayout(plotContainer, {
                'shapes[0].x0': state.phi,
                'shapes[0].x1': state.phi
            });

            updateRangeDisplay();
        }

        function updateAxisLayout() {
            readAllState();
            updateDisplays();

            const layoutUpdate = {
                'yaxis.range': [0, state.cAxis],
                'shapes[0].y1': state.cAxis
            };

            Plotly.relayout(plotContainer, layoutUpdate);
        }

        function updateRangeDisplay() {
            const phi_val = state.phi;
            const lower_bound = y2(phi_val, state.gammaSat, state.r);
            const upper_bound = y3(phi_val, state.gammaSat, state.r, state.mw, state.rpe);

            if (isFinite(lower_bound) && isFinite(upper_bound)) {
                displays.range.textContent = `${lower_bound.toFixed(3)} < C_secondary emulsion < ${upper_bound.toFixed(3)}`;
            } else {
                displays.range.textContent = 'Stability range is out of bounds.';
            }
        }


        // --- EVENT LISTENERS ---
        sliders.phi.addEventListener('input', updateInteractiveElements);
        sliders.cAxis.addEventListener('input', updateAxisLayout);

        // Sliders that trigger a full replot
        [sliders.gammaSat, sliders.r, sliders.rpe, sliders.mw, sliders.phiAxis].forEach(slider => {
            slider.addEventListener('input', updatePlot);
        });

        // --- INITIALIZATION ---
        MathJax.startup.promise.then(() => {
            updatePlot(); // Initial plot draw
            MathJax.typesetPromise(); // Render all initial LaTeX
        });
    });
</script>

</body>
</html>
