<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-time">time relative to collision</label>
                <input type="range" id="slider-time" min="-30" max="30" value="-10" step="0.1">
                <span id="label-time"></span>
            </div>

            <h4>object 1</h4>
            <div class="control-group">
                <label for="slider-m1">mass</label>
                <input type="range" id="slider-m1" min="1" max="10" value="5" step="0.01">
                <span id="label-m1"></span>
                <label for="slider-v1">speed</label>
                <input type="range" id="slider-v1" min="1" max="20" value="10" step="0.1">
                <span id="label-v1"></span>
            </div>

            <h4>object 2</h4>
            <div class="control-group">
                <label for="slider-m2">mass</label>
                <input type="range" id="slider-m2" min="1" max="10" value="3" step="0.01">
                <span id="label-m2"></span>
                <label for="slider-v2">speed</label>
                <input type="range" id="slider-v2" min="1" max="20" value="5" step="0.1">
                <span id="label-v2"></span>
            </div>

            <div id="joystick-section">
                <label>observer velocity (m/s)</label>
                <div class="joystick-layout">
                    <div id="joystick-container">
                        <div id="joystick-knob"></div>
                    </div>
                    <span id="label-v0y"></span>
                    <span id="label-v0x"></span>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Global state object
        const state = {
            t: -10,
            m1: 5, v1_cm: 10,
            m2: 3, v2_cm: 0,
            v0x: 1.1, v0y: 1.1,
            v0_max: 5,
        };

        // Physics vectors and display metrics
        let u1_cm, u2_cm, v1_cm, v2_cm, v0;
        const metrics = { v0: 0, v1: 0, v2: 0, energy: 0 };

        // DOM Elements
        let sliderTime, labelTime, sliderM1, labelM1, sliderV1, labelV1,
            sliderM2, labelM2, sliderV2, labelV2, joystick, knob,
            labelV0x, labelV0y;

        // p5.js Sketch
        const sketch = (p) => {
            const scaleFactor = 10;

            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const w = Math.max(300, canvasContainer.clientWidth - 40);
                const h = Math.max(300, canvasContainer.clientHeight - 40);
                const canvas = p.createCanvas(w, h);
                canvas.parent('canvas-container');

                p.noLoop(); // We will manually call redraw
                initializeControls();
                updateSimulation();
            };

            p.windowResized = () => {
                 const canvasContainer = document.getElementById('canvas-container');
                 const w = Math.max(300, canvasContainer.clientWidth - 40);
                 const h = Math.max(300, canvasContainer.clientHeight - 40);
                 p.resizeCanvas(w, h);
                 updateJoystickPositionFromState();
                 p.redraw();
            }

            p.draw = () => {
                p.background(255);
                p.translate(p.width / 2, p.height / 2);

                if (!u1_cm) return; // Don't draw if physics not calculated yet

                drawTrajectories();
                drawParticles();
                drawObserverVector();
                drawMetricsText();
            };

            function drawTrajectories() {
                p.push();
                p.drawingContext.setLineDash([5, 5]);
                const T_far = 50;

                strokeAndLine(255, 0, 0, u1_cm, v1_cm);
                strokeAndLine(0, 0, 255, u2_cm, v2_cm);

                p.pop();

                function strokeAndLine(r, g, b, u_cm, v_cm) {
                    p.stroke(r, g, b, 150);
                    p.noFill();
                    const start_pos = p5.Vector.add(u_cm, v0).mult(-T_far).mult(scaleFactor);
                    const end_pos = p5.Vector.add(v_cm, v0).mult(T_far).mult(scaleFactor);
                    p.line(start_pos.x, start_pos.y, 0, 0);
                    p.line(0, 0, end_pos.x, end_pos.y);
                }
            }

            function drawParticles() {
                const t = state.t;
                let pos1_obs, pos2_obs;

                if (t < 0) {
                    pos1_obs = p5.Vector.add(u1_cm, v0).mult(t);
                    pos2_obs = p5.Vector.add(u2_cm, v0).mult(t);
                } else {
                    pos1_obs = p5.Vector.add(v1_cm, v0).mult(t);
                    pos2_obs = p5.Vector.add(v2_cm, v0).mult(t);
                }

                pos1_obs.mult(scaleFactor);
                pos2_obs.mult(scaleFactor);

                p.noStroke();
                const r1 = 8 * p.sqrt(state.m1);
                p.fill(255, 0, 0);
                p.circle(pos1_obs.x, pos1_obs.y, r1 * 2);

                const r2 = 8 * p.sqrt(state.m2);
                p.fill(0, 0, 255);
                p.circle(pos2_obs.x, pos2_obs.y, r2 * 2);
            }

            function drawObserverVector() {
                const vec_to_draw = p5.Vector.mult(v0, -1).mult(scaleFactor * 2.5);
                const base_pos = p.createVector(-p.width/2 + 80, p.height/2 - 200);

                drawArrow(base_pos, vec_to_draw, p.color(0));

                p.fill(0);
                p.noStroke();
                p.textAlign(p.LEFT, p.CENTER);
                p.textSize(14);
                p.text(`v₀ = ${metrics.v0.toFixed(5)} m/s`, base_pos.x, base_pos.y + vec_to_draw.y/2 + 30);
            }

            function drawArrow(base, vec, myColor) {
                p.push();
                p.stroke(myColor);
                p.strokeWeight(2);
                p.fill(myColor);
                p.translate(base.x, base.y);
                p.line(0, 0, vec.x, vec.y);
                p.rotate(vec.heading());
                let arrowSize = 7;
                p.translate(vec.mag() - arrowSize, 0);
                p.triangle(0, arrowSize / 2, 0, -arrowSize / 2, arrowSize, 0);
                p.pop();
            }

            function drawMetricsText() {
                 p.push();
                 // Position is relative to the center, so no matrix reset needed
                 const xPos = 100;
                 const yPos = 20;
                 p.fill(0);
                 p.noStroke();
                 p.textAlign(p.LEFT, p.TOP);
                 p.textSize(16);
                 p.text(`v₁ = ${metrics.v1.toFixed(5)} m/s`, xPos, yPos);
                 p.text(`v₂ = ${metrics.v2.toFixed(5)} m/s`, xPos, yPos + 25);
                 p.text(`energy: ${metrics.energy.toFixed(3)} joules`, xPos, yPos + 50);
                 p.pop();
            }
        };

        let p5_instance;

        function initializeControls() {
            sliderTime = document.getElementById('slider-time');
            labelTime = document.getElementById('label-time');
            sliderM1 = document.getElementById('slider-m1');
            labelM1 = document.getElementById('label-m1');
            sliderV1 = document.getElementById('slider-v1');
            labelV1 = document.getElementById('label-v1');
            sliderM2 = document.getElementById('slider-m2');
            labelM2 = document.getElementById('label-m2');
            sliderV2 = document.getElementById('slider-v2');
            labelV2 = document.getElementById('label-v2');
            joystick = document.getElementById('joystick-container');
            knob = document.getElementById('joystick-knob');
            labelV0x = document.getElementById('label-v0x');
            labelV0y = document.getElementById('label-v0y');

            sliderTime.addEventListener('input', handleTimeChange);
            sliderM1.addEventListener('input', handlePhysicsChange);
            sliderV1.addEventListener('input', handlePhysicsChange);
            sliderM2.addEventListener('input', handlePhysicsChange);
            sliderV2.addEventListener('input', handlePhysicsChange);

            setupJoystick();
        }

        function handleTimeChange(event) {
            state.t = parseFloat(event.target.value);
            updateLabels();
            if(p5_instance) p5_instance.redraw();
        }

        function handlePhysicsChange() {
            readControls();
            updateSimulation();
        }

        function readControls() {
            state.t = parseFloat(sliderTime.value);
            state.m1 = parseFloat(sliderM1.value);
            state.v1_cm = parseFloat(sliderV1.value);
            state.m2 = parseFloat(sliderM2.value);
        }

        function updateSimulation() {
            calculatePhysics();
            calculateMetrics();
            updateLabels();
            if(p5_instance) p5_instance.redraw();
        }

        function calculatePhysics() {
            const s1_cm = state.v1_cm;
            const s2_cm = (state.m1 / state.m2) * s1_cm;
            state.v2_cm = s2_cm;

            u1_cm = p5_instance.createVector(s1_cm, 0);
            u2_cm = p5_instance.createVector(-s2_cm, 0);

            const angle = p5_instance.radians(-45);
            v1_cm = u1_cm.copy().rotate(angle);
            v2_cm = u2_cm.copy().rotate(angle);

            v0 = p5_instance.createVector(state.v0x, state.v0y);
        }

        function calculateMetrics() {
            const u1_obs = p5.Vector.add(u1_cm, v0);
            const u2_obs = p5.Vector.add(u2_cm, v0);

            metrics.v0 = v0.mag();
            metrics.v1 = u1_obs.mag();
            metrics.v2 = u2_obs.mag();
            metrics.energy = 0.5 * state.m1 * p5_instance.pow(metrics.v1, 2) + 0.5 * state.m2 * p5_instance.pow(metrics.v2, 2);
        }

        function updateLabels() {
            labelTime.textContent = `${parseFloat(sliderTime.value).toFixed(1)} s`;
            labelM1.textContent = `${state.m1.toFixed(2)} kg`;
            labelV1.textContent = `${state.v1_cm.toFixed(1)} m/s`;
            labelM2.textContent = `${state.m2.toFixed(2)} kg`;

            const v2_max = parseFloat(sliderV2.max);
            const v2_clamped = Math.min(state.v2_cm, v2_max);
            sliderV2.value = v2_clamped;
            labelV2.textContent = `${state.v2_cm.toFixed(1)} m/s`;

            labelV0x.textContent = `${state.v0x.toFixed(2)}`;
            labelV0y.textContent = `${state.v0y.toFixed(2)}`;
        }

        function updateJoystickPositionFromState() {
            const rect = joystick.getBoundingClientRect();
            if (rect.width === 0) return;
            const x = ((state.v0x + state.v0_max) / (2 * state.v0_max)) * rect.width;
            const y = ((-state.v0y + state.v0_max) / (2 * state.v0_max)) * rect.height; // Invert Y for screen coords
            knob.style.left = `${x}px`;
            knob.style.top = `${y}px`;
        }

        function setupJoystick() {
            let isDragging = false;

            function updateJoystick(e) {
                const rect = joystick.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;

                let clampedX = Math.max(0, Math.min(rect.width, x));
                let clampedY = Math.max(0, Math.min(rect.height, y));

                state.v0x = (clampedX / rect.width) * 2 * state.v0_max - state.v0_max;
                state.v0y = -((clampedY / rect.height) * 2 * state.v0_max - state.v0_max); // Invert Y

                knob.style.left = `${clampedX}px`;
                knob.style.top = `${clampedY}px`;

                handlePhysicsChange();
            }

            function onMouseDown(e) {
                e.preventDefault();
                isDragging = true;
                knob.style.cursor = 'grabbing';
                document.addEventListener('mousemove', onMouseMove);
                document.addEventListener('mouseup', onMouseUp);
            }

            function onMouseMove(e) {
                if (!isDragging) return;
                updateJoystick(e);
            }

            function onMouseUp() {
                isDragging = false;
                knob.style.cursor = 'grab';
                document.removeEventListener('mousemove', onMouseMove);
                document.removeEventListener('mouseup', onMouseUp);
            }

            joystick.addEventListener('mousedown', onMouseDown);
            updateJoystickPositionFromState();
        }

        document.addEventListener('DOMContentLoaded', () => {
             p5_instance = new p5(sketch);
        });

    </script>
</body>
</html>
