<body>
  <div class="container">
    <!-- Control Panel Section -->
    <div class="control-panel">
      <div class="slider-row">
        <label for="shift-slider">shift</label>
        <input id="shift-slider" type="range" min="0" max="25" step="1" value="12"/>
      </div>
      <div class="text-row">
        <input id="text-input" type="text" list="phrase-list" value="abcd" placeholder="enter a message to encrypt"/>
        <datalist id="phrase-list">
          <option value="abcd"></option>
          <option value="test phrase"></option>
          <option value="to be or not to be"></option>
          <option value="the quick brown fox jumped over the lazy dog"></option>
        </datalist>
      </div>
    </div>

    <!-- Display Area Section -->
    <div class="display-area">
      <!-- Visualization Section -->
      <div class="alphabet">
        <div id="plaintext-alphabet" class="alphabet-row"></div>
        <div id="ciphertext-alphabet" class="alphabet-row"></div>
      </div>

      <!-- Key Section -->
      <div class="key-section">
        <p id="encryption-key-display">encryption key: 12</p>
        <p id="decryption-key-display">decryption key: 14</p>
      </div>

      <!-- Text Output Section -->
      <div class="output-section">
        <p id="plaintext-output"><span class="label-plain">plaintext :</span> abcd</p>
        <p id="ciphertext-output"><span class="label-cipher">ciphertext:</span> MNOP</p>
      </div>
    </div>
  </div>

  <script>
    const ALPHABET = 'abcdefghijklmnopqrstuvwxyz';

    function renderPlainAlphabet(){
      const container = document.getElementById('plaintext-alphabet');
      container.innerHTML = '';
      for(let i=0;i<26;i++){
        const span = document.createElement('span');
        span.textContent = ALPHABET[i];
        container.appendChild(span);
      }
    }

    function renderCipherAlphabet(shift){
      const container = document.getElementById('ciphertext-alphabet');
      container.innerHTML = '';
      for(let i=0;i<26;i++){
        const span = document.createElement('span');
        span.textContent = ALPHABET[(shift + i) % 26].toUpperCase();
        container.appendChild(span);
      }
    }

    function encrypt(text, shift){
      let out = '';
      for(const ch of text){
        const idx = ALPHABET.indexOf(ch.toLowerCase());
        if(idx !== -1){
          out += ALPHABET[(idx + shift) % 26].toUpperCase();
        }else{
          out += ch;
        }
      }
      return out;
    }

    function escapeHtml(str){
      return str.replace(/[&<>"']/g, m => (
        { '&':'&amp;', '<':'&lt;', '>':'&gt;', '"':'&quot;', "'":'&#039;' }[m]
      ));
    }

    function updateCipher(){
      const shiftValue = parseInt(document.getElementById('shift-slider').value, 10) || 0;
      const msg = document.getElementById('text-input').value;

      // Visualization
      renderPlainAlphabet();
      renderCipherAlphabet(shiftValue);

      // Keys
      document.getElementById('encryption-key-display').textContent = 'encryption key: ' + shiftValue;
      const decKey = (26 - shiftValue) % 26;
      document.getElementById('decryption-key-display').textContent = 'decryption key: ' + decKey;

      // Text Output
      document.getElementById('plaintext-output').innerHTML =
        '<span class="label-plain">plaintext :</span> ' + escapeHtml(msg);
      const cipher = encrypt(msg, shiftValue);
      document.getElementById('ciphertext-output').innerHTML =
        '<span class="label-cipher">ciphertext:</span> ' + escapeHtml(cipher);
    }

    // Initialize
    window.addEventListener('DOMContentLoaded', () => {
      // Ensure defaults per plan
      document.getElementById('shift-slider').value = 12;
      document.getElementById('text-input').value = 'abcd';

      // Listeners
      document.getElementById('shift-slider').addEventListener('input', updateCipher);
      document.getElementById('text-input').addEventListener('input', updateCipher);

      // First render
      updateCipher();
    });
  </script>
</body>
</html>
