<body>
<div class="app" id="main-container">
  <!-- Control Panel -->
  <div class="control-panel" id="control-panel">
    <div class="row">
      <div class="label">coordination</div>
      <div class="button-group" role="group" aria-label="coordination buttons">
        <button id="btn-threefold" class="active">threefold</button>
        <button id="btn-fourfold">fourfold</button>
        <button id="btn-sixfold">sixfold</button>
        <button id="btn-eightfold">eightfold</button>
      </div>
      <div></div>
    </div>

    <div class="row">
      <label for="slider-anion-radius" class="label">relative anion radius</label>
      <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1" />
      <span id="value-anion-radius" class="value-box">0.100</span>
    </div>

    <div class="row">
      <label for="slider-cation-radius" class="label">relative cation radius</label>
      <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1" />
      <span id="value-cation-radius" class="value-box">0.100</span>
    </div>

    <div class="row">
      <label for="slider-opacity" class="label">opacity of anions</label>
      <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8" />
      <div></div>
    </div>
  </div>

  <!-- Main Display Container -->
  <div class="display-container">
    <div id="canvas-container"></div>
    <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
  </div>
</div>

<!-- 3D and logic -->
<script type="module">
  import * as THREE from 'https://unpkg.com/three@0.160.0/build/three.module.js';
  import { OrbitControls } from 'https://unpkg.com/three@0.160.0/examples/jsm/controls/OrbitControls.js';

  // DOM elements
  const btnThree = document.getElementById('btn-threefold');
  const btnFour = document.getElementById('btn-fourfold');
  const btnSix = document.getElementById('btn-sixfold');
  const btnEight = document.getElementById('btn-eightfold');

  const sliderAnion = document.getElementById('slider-anion-radius');
  const sliderCation = document.getElementById('slider-cation-radius');
  const sliderOpacity = document.getElementById('slider-opacity');

  const valueAnion = document.getElementById('value-anion-radius');
  const valueCation = document.getElementById('value-cation-radius');

  const ratioText = document.getElementById('ratio-text');
  const canvasContainer = document.getElementById('canvas-container');

  // State
  let coordination = 'threefold'; // default
  let anionRadius = parseFloat(sliderAnion.value);
  let cationRadius = parseFloat(sliderCation.value);
  let anionOpacity = parseFloat(sliderOpacity.value);
  const centerDistance = 1.5; // distance of anion centers from origin

  // Three.js setup
  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xffffff);

  const renderer = new THREE.WebGLRenderer({ antialias: true });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
  canvasContainer.appendChild(renderer.domElement);

  const camera = new THREE.PerspectiveCamera(45, 1, 0.01, 1000);
  const controls = new OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.dampingFactor = 0.06;
  controls.minDistance = 0.5;
  controls.maxDistance = 20;

  // Lighting
  const ambient = new THREE.AmbientLight(0xffffff, 0.6);
  scene.add(ambient);
  const dirLight = new THREE.DirectionalLight(0xffffff, 0.9);
  dirLight.position.set(2, 3, 4);
  scene.add(dirLight);

  // Materials and meshes
  const gradientTexture = createIridescentTexture();
  const cationMaterial = new THREE.MeshPhongMaterial({
    color: 0xffffff,
    shininess: 80,
    specular: 0x666666,
    map: gradientTexture
  });

  const cationGeometry = new THREE.SphereGeometry(1, 64, 64);
  const cationMesh = new THREE.Mesh(cationGeometry, cationMaterial);
  cationMesh.scale.setScalar(cationRadius);
  scene.add(cationMesh);

  const anionGeometry = new THREE.SphereGeometry(1, 48, 48);
  const anionMaterial = new THREE.MeshPhongMaterial({
    color: 0xe0d8e0,
    transparent: true,
    opacity: anionOpacity,
    shininess: 50
  });

  let anionsGroup = new THREE.Group();
  let bboxLines = null; // wireframe bounding box (for 4,6,8)

  // Initial build
  buildStructure(coordination);
  updateRatioText();
  resizeRenderer();
  animate();

  // Event listeners: buttons
  btnThree.addEventListener('click', () => setCoordination('threefold'));
  btnFour.addEventListener('click', () => setCoordination('fourfold'));
  btnSix.addEventListener('click', () => setCoordination('sixfold'));
  btnEight.addEventListener('click', () => setCoordination('eightfold'));

  // Event listeners: sliders
  sliderAnion.addEventListener('input', () => {
    anionRadius = parseFloat(sliderAnion.value);
    valueAnion.textContent = anionRadius.toFixed(3);
    // Update anion sizes
    anionsGroup.children.forEach(m => m.scale.setScalar(anionRadius));
    updateRatioText();
  });

  sliderCation.addEventListener('input', () => {
    cationRadius = parseFloat(sliderCation.value);
    valueCation.textContent = cationRadius.toFixed(3);
    cationMesh.scale.setScalar(cationRadius);
    updateRatioText();
  });

  sliderOpacity.addEventListener('input', () => {
    anionOpacity = parseFloat(sliderOpacity.value);
    anionsGroup.traverse(obj => {
      if (obj.isMesh) {
        obj.material.opacity = anionOpacity;
        obj.material.transparent = true;
        obj.material.needsUpdate = true;
      }
    });
  });

  window.addEventListener('resize', resizeRenderer);

  // Functions

  function setCoordination(type) {
    if (coordination === type) return;
    coordination = type;
    setActiveButton(type);
    buildStructure(type);
  }

  function setActiveButton(type) {
    const mapping = {
      threefold: btnThree,
      fourfold: btnFour,
      sixfold: btnSix,
      eightfold: btnEight
    };
    [btnThree, btnFour, btnSix, btnEight].forEach(b => b.classList.remove('active'));
    mapping[type].classList.add('active');
  }

  function buildStructure(type) {
    // Remove old group and box
    if (anionsGroup) {
      scene.remove(anionsGroup);
      anionsGroup.traverse(obj => {
        if (obj.isMesh) obj.geometry.dispose();
      });
      // Reuse base geometry instead of disposing, so create new below
    }
    if (bboxLines) {
      scene.remove(bboxLines);
      bboxLines.geometry.dispose();
      bboxLines.material.dispose();
      bboxLines = null;
    }

    // Recreate reusable geometry (prevent disposal of template)
    const localAnionGeometry = anionGeometry.clone();

    anionsGroup = new THREE.Group();
    const positions = getPositionsFor(type, centerDistance);

    for (const p of positions) {
      const mesh = new THREE.Mesh(localAnionGeometry, anionMaterial.clone());
      mesh.position.copy(p);
      mesh.scale.setScalar(anionRadius);
      mesh.material.opacity = anionOpacity;
      mesh.material.transparent = true;
      anionsGroup.add(mesh);
    }
    scene.add(anionsGroup);

    // Bounding box for 4,6,8
    if (type !== 'threefold') {
      const size = centerDistance * 2;
      const boxGeo = new THREE.BoxGeometry(size, size, size);
      const edges = new THREE.EdgesGeometry(boxGeo);
      const lineMat = new THREE.LineBasicMaterial({ color: 0x888888 });
      bboxLines = new THREE.LineSegments(edges, lineMat);
      scene.add(bboxLines);
    }

    // Reset camera to frame the new arrangement
    frameCamera(centerDistance + Math.max(anionRadius, cationRadius) + 0.4);
  }

  function getPositionsFor(type, r) {
    const positions = [];
    if (type === 'threefold') {
      // Equilateral triangle on XY plane
      for (let i = 0; i < 3; i++) {
        const angle = i * (Math.PI * 2 / 3);
        positions.push(new THREE.Vector3(Math.cos(angle) * r, Math.sin(angle) * r, 0));
      }
    } else if (type === 'fourfold') {
      const pts = [
        new THREE.Vector3(1, 1, 1),
        new THREE.Vector3(-1, -1, 1),
        new THREE.Vector3(-1, 1, -1),
        new THREE.Vector3(1, -1, -1)
      ];
      const scale = r / Math.sqrt(3);
      pts.forEach(v => v.multiplyScalar(scale));
      positions.push(...pts);
    } else if (type === 'sixfold') {
      positions.push(
        new THREE.Vector3(r, 0, 0),
        new THREE.Vector3(-r, 0, 0),
        new THREE.Vector3(0, r, 0),
        new THREE.Vector3(0, -r, 0),
        new THREE.Vector3(0, 0, r),
        new THREE.Vector3(0, 0, -r)
      );
    } else if (type === 'eightfold') {
      // Cube vertices at ±1 normalized to distance r (already unit length sqrt(3) -> scale)
      const signs = [-1, 1];
      const scale = r / Math.sqrt(3);
      for (const x of signs) for (const y of signs) for (const z of signs) {
        positions.push(new THREE.Vector3(x, y, z).multiplyScalar(scale));
      }
    }
    return positions;
  }

  function frameCamera(size) {
    const dist = size * 2.6;
    camera.position.set(dist, dist, dist);
    camera.near = Math.max(0.01, dist / 50);
    camera.far = dist * 50;
    camera.updateProjectionMatrix();
    controls.target.set(0, 0, 0);
    controls.update();
  }

  function updateRatioText() {
    const ratio = (cationRadius / anionRadius);
    ratioText.textContent = `The ratio of the cation radius to the anion radius is ${ratio.toFixed(2)}.`;
  }

  function resizeRenderer() {
    const { clientWidth, clientHeight } = canvasContainer;
    renderer.setSize(clientWidth, clientHeight, false);
    camera.aspect = clientWidth / clientHeight;
    camera.updateProjectionMatrix();
  }

  function animate() {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }

  // Utility: Create a simple radial gradient "iridescent" texture
  function createIridescentTexture(size = 512) {
    const canvas = document.createElement('canvas');
    canvas.width = canvas.height = size;
    const ctx = canvas.getContext('2d');

    const cx = size / 2, cy = size / 2, r = size * 0.5;
    const grd = ctx.createRadialGradient(cx, cy, size * 0.05, cx, cy, r);
    grd.addColorStop(0.0, '#2c6be9');  // blue center
    grd.addColorStop(0.35, '#6fa7ff');
    grd.addColorStop(0.6, '#b1e1ff');
    grd.addColorStop(0.85, '#ff9a5a'); // orange edge
    grd.addColorStop(1.0, '#ff6b4a');
    ctx.fillStyle = grd;
    ctx.fillRect(0, 0, size, size);

    // subtle vignette
    const vg = ctx.createRadialGradient(cx, cy, 0, cx, cy, r);
    vg.addColorStop(0.7, 'rgba(0,0,0,0)');
    vg.addColorStop(1.0, 'rgba(0,0,0,0.12)');
    ctx.fillStyle = vg;
    ctx.fillRect(0, 0, size, size);

    const tex = new THREE.CanvasTexture(canvas);
    tex.anisotropy = 8;
    tex.wrapS = THREE.ClampToEdgeWrapping;
    tex.wrapT = THREE.ClampToEdgeWrapping;
    tex.needsUpdate = true;
    return tex;
  }
</script>
</body>
</html>
