<script>
  // ---------- Global UI/physics state ----------
  const slider = document.getElementById('slider-angle');
  const btnPlay = document.getElementById('btn-play');
  const btnPause = document.getElementById('btn-pause');
  const btnReset = document.getElementById('btn-reset');
  const infoDisplay = document.getElementById('info-display');

  const g = 9.81; // m/s^2
  let thetaDeg = parseInt(slider.value, 10); // initial angle
  let isPlaying = false;

  // Kinematics along slope
  let sDown = 0;        // pixels traveled down the slope from the top (0..L)
  let sStart = 0;       // starting sDown when (re)play pressed
  let tStartMs = 0;     // millis() at (re)start time
  const pixPerMeter = 30; // scaling for animation speed

  function updateInfo() {
    const thetaRad = thetaDeg * Math.PI / 180;
    const a = g * Math.sin(thetaRad);
    const accStr = a.toFixed(3);
    infoDisplay.innerHTML = `slope = \\( \\theta \\) = ${thetaDeg}°; &nbsp; acceleration = \\( g \\sin(\\theta) \\) = ${accStr} m/s²`;
    if (window.MathJax && window.MathJax.typesetPromise) {
      MathJax.typesetPromise([infoDisplay]);
    } else if (window.MathJax && window.MathJax.typeset) {
      MathJax.typeset([infoDisplay]);
    }
  }

  // Slider interaction: update angle, stop and reset animation
  slider.addEventListener('input', () => {
    thetaDeg = parseInt(slider.value, 10);
    isPlaying = false;
    sDown = 0;
    sStart = 0;
    tStartMs = 0;
    updateInfo();
  });

  btnPlay.addEventListener('click', () => {
    // If already at bottom, do nothing
    if (sDown >= p5State.slopeLength) return;
    isPlaying = true;
    sStart = sDown;
    tStartMs = p5State.pInst ? p5State.pInst.millis() : performance.now();
  });

  btnPause.addEventListener('click', () => {
    isPlaying = false;
  });

  btnReset.addEventListener('click', () => {
    isPlaying = false;
    sDown = 0;
    sStart = 0;
    tStartMs = 0;
  });

  updateInfo();

  // ---------- p5.js Sketch ----------
  const p5State = {
    pInst: null,
    width: 920,
    height: 560,
    slopeLength: 480, // pixels (hypotenuse length)
    marginLeft: 130,
    marginBottom: 70,
    blockW: 62,
    blockH: 30,
    gravityVecLen: 100 // pixels
  };

  // Remove placeholder canvas and let p5 recreate it with the required id
  (function replaceCanvasForP5() {
    const placeholder = document.getElementById('p5-canvas');
    if (placeholder) placeholder.remove();
  })();

  new p5((p) => {
    p5State.pInst = p;

    function drawArrow(x1, y1, x2, y2, color, label, labelOffset = 12) {
      p.push();
      p.stroke(color);
      p.fill(color);
      p.strokeWeight(2);
      p.line(x1, y1, x2, y2);

      const ang = Math.atan2(y2 - y1, x2 - x1);
      p.translate(x2, y2);
      p.rotate(ang);
      p.triangle(0, 0, -10, 4, -10, -4);
      p.pop();

      if (label) {
        const mx = (x1 + x2) / 2;
        const my = (y1 + y2) / 2;
        const lx = mx + labelOffset * Math.cos(ang - Math.PI / 2);
        const ly = my + labelOffset * Math.sin(ang - Math.PI / 2);
        p.push();
        p.noStroke();
        p.fill(color);
        p.textAlign(p.CENTER, p.CENTER);
        p.text(label, lx, ly);
        p.pop();
      }
    }

    function dashedLine(x1, y1, x2, y2, dash = 6, gap = 6) {
      p.push();
      p.stroke(0);
      p.strokeWeight(2);
      p.drawingContext.setLineDash([dash, gap]);
      p.line(x1, y1, x2, y2);
      p.pop();
    }

    p.setup = function() {
      const c = p.createCanvas(p5State.width, p5State.height);
      c.id('p5-canvas');
      c.parent('visualization-area');

      p.rectMode(p.CENTER);
      p.textSize(14);
      p.strokeCap(p.ROUND);
      p.noLoop(); // will manually redraw when needed; but animation will call loop()
      p.loop();   // keep draw loop active for animation
    };

    p.windowResized = function() {
      // keep static size for simplicity; could be responsive
    };

    p.draw = function() {
      p.background(250);

      // Derived geometry based on theta
      const theta = p.radians(thetaDeg);
      const cosT = Math.cos(theta);
      const sinT = Math.sin(theta);

      // Base triangle points
      const A = { x: p5State.marginLeft, y: p.height - p5State.marginBottom }; // bottom-left vertex
      const L = p5State.slopeLength;
      const base = L * cosT;
      const h = L * sinT;
      const B = { x: A.x + base, y: A.y };
      const C = { x: A.x + base, y: A.y - h };

      // Unit vectors along slope (upwards) and normal (outward)
      const u = { x: cosT, y: -sinT };                 // up the slope from A to C
      const nOut = { x: -sinT, y: -cosT };             // outward (away from plane)

      // Animation update
      const accel = g * sinT; // m/s^2 along slope
      if (isPlaying) {
        const tNow = p.millis();
        const t = (tNow - tStartMs) / 1000;
        const sDelta = 0.5 * accel * t * t * pixPerMeter; // pixels advanced down the slope
        sDown = Math.min(L, sStart + sDelta);
        if (sDown >= L - 0.0001) {
          isPlaying = false;
        }
      }

      // Block position along slope
      const tAlong = L - sDown; // distance from A towards C
      const P = { x: A.x + u.x * tAlong, y: A.y + u.y * tAlong }; // contact point on surface
      const blockCenter = { x: P.x + nOut.x * (p5State.blockH / 2), y: P.y + nOut.y * (p5State.blockH / 2) };

      // Draw inclined plane triangle
      p.push();
      p.fill('#cfe9ff');       // lightblue
      p.stroke('yellow');
      p.strokeWeight(2);
      p.triangle(A.x, A.y, B.x, B.y, C.x, C.y);
      p.pop();

      // Angle marker at A
      p.push();
      p.noFill();
      p.stroke(0);
      p.strokeWeight(2);
      const rAng = 46;
      p.arc(A.x, A.y, 2 * rAng, 2 * rAng, 0, -theta);
      p.noStroke();
      p.fill(0);
      const midAng = -theta / 2;
      p.textAlign(p.CENTER, p.CENTER);
      p.text('θ', A.x + (rAng + 12) * Math.cos(midAng), A.y + (rAng + 12) * Math.sin(midAng));
      p.pop();

      // Local coordinate axes near ramp
      const axisOrigin = { x: A.x + u.x * (L * 0.55), y: A.y + u.y * (L * 0.55) };
      drawArrow(axisOrigin.x, axisOrigin.y, axisOrigin.x + u.x * 55, axisOrigin.y + u.y * 55, '#000', 'x', 8);
      drawArrow(axisOrigin.x, axisOrigin.y, axisOrigin.x + nOut.x * 55, axisOrigin.y + nOut.y * 55, '#000', 'y', 8);

      // Draw block
      p.push();
      p.translate(blockCenter.x, blockCenter.y);
      p.rotate(-theta);
      p.fill('#a9a9a9');
      p.stroke('#111');
      p.strokeWeight(2);
      p.rect(0, 0, p5State.blockW, p5State.blockH);

      // Small right-angle marker between block and normal edge
      p.noFill();
      p.stroke('#555');
      p.strokeWeight(2);
      const m = p5State.blockH / 2 - 6;
      p.rect(-p5State.blockW / 2 + 10, m - 10, 12, 12);
      p.pop();

      // --- Forces ---
      const Fg = p5State.gravityVecLen;     // constant length for mg
      const Fnormal = Fg * Math.cos(theta); // proportional to cos(theta)
      const Fnet = Fg * Math.sin(theta);    // proportional to sin(theta)

      // Gravity (downwards)
      const gravTip = { x: blockCenter.x, y: blockCenter.y + Fg };
      drawArrow(blockCenter.x, blockCenter.y, gravTip.x, gravTip.y, 'red', 'F_grav=mg');

      // Normal (perpendicular outward)
      const nTip = { x: blockCenter.x + nOut.x * Fnormal, y: blockCenter.y + nOut.y * Fnormal };
      drawArrow(blockCenter.x, blockCenter.y, nTip.x, nTip.y, 'deepskyblue', '"normal" force');

      // Net force (parallel down the slope)
      const downSlope = { x: -u.x, y: -u.y };
      const netTip = { x: blockCenter.x + downSlope.x * Fnet, y: blockCenter.y + downSlope.y * Fnet };
      drawArrow(blockCenter.x, blockCenter.y, netTip.x, netTip.y, 'purple', 'F_net = m g sin(θ)');

      // Decomposition of gravity (dashed rectangle)
      const compN_inward = { x: -nOut.x, y: -nOut.y }; // into plane (perpendicular component of gravity)
      const compNPoint = { x: blockCenter.x + compN_inward.x * Fnormal, y: blockCenter.y + compN_inward.y * Fnormal };
      const compTPoint = { x: blockCenter.x + downSlope.x * Fnet, y: blockCenter.y + downSlope.y * Fnet };
      // Two sides
      dashedLine(blockCenter.x, blockCenter.y, compNPoint.x, compNPoint.y);
      dashedLine(compNPoint.x, compNPoint.y, gravTip.x, gravTip.y);
      dashedLine(blockCenter.x, blockCenter.y, compTPoint.x, compTPoint.y);
      dashedLine(compTPoint.x, compTPoint.y, gravTip.x, gravTip.y);

      // Label for m g cos(θ) near the perpendicular component
      p.push();
      p.noStroke();
      p.fill(0);
      p.textAlign(p.LEFT, p.CENTER);
      const labelNpos = {
        x: (blockCenter.x + compNPoint.x) / 2 + 10 * nOut.y, // small offset for readability
        y: (blockCenter.y + compNPoint.y) / 2 - 10 * nOut.x
      };
      p.text('m g cos(θ)', labelNpos.x, labelNpos.y);
      p.pop();

      // Small arc showing θ between gravity and perpendicular component
      p.push();
      const arcR = 22;
      const angleNin = Math.atan2(compN_inward.y, compN_inward.x);
      const angleG = Math.PI / 2; // straight down
      p.noFill();
      p.stroke(0);
      p.strokeWeight(2);
      p.arc(blockCenter.x, blockCenter.y, arcR * 2, arcR * 2, angleNin, angleG);
      p.noStroke();
      p.fill(0);
      const mid = (angleNin + angleG) / 2;
      p.textAlign(p.CENTER, p.CENTER);
      p.text('θ', blockCenter.x + (arcR + 10) * Math.cos(mid), blockCenter.y + (arcR + 10) * Math.sin(mid));
      p.pop();

      // Optional small star point along the slope (purely decorative to mimic snapshots)
      p.push();
      const starPos = { x: A.x + u.x * (L * 0.72), y: A.y + u.y * (L * 0.72) };
      p.translate(starPos.x, starPos.y);
      p.rotate(-theta);
      p.stroke(0);
      p.strokeWeight(2);
      p.line(-6, 0, 6, 0);
      p.line(0, -6, 0, 6);
      p.pop();

      // Thin yellow outline along hypotenuse for clarity
      p.push();
      p.stroke('yellow');
      p.strokeWeight(2);
      p.noFill();
      p.line(A.x, A.y, C.x, C.y);
      p.pop();
    };
  });

</script>
</body>
