  return bits;
}
function padRightToMultiple(bits, blockSize) {
  if (bits.length === 0) return new Array(blockSize).fill(0);
  const rem = bits.length % blockSize;
  if (rem !== 0) {
    const need = blockSize - rem;
    for (let i = 0; i < need; i++) bits.push(0);
  }
  return bits;
}
function ensureExactly(bits, len) {
  if (bits.length > len) return bits.slice(0, len);
  if (bits.length < len) return bits.concat(new Array(len - bits.length).fill(0));
  return bits.slice();
}
function permute(bits, table) {
  const out = new Array(table.length);
  for (let i = 0; i < table.length; i++) {
    out[i] = bits[table[i] - 1];
  }
  return out;
}
function leftRotate(arr, n) {
  const out = arr.slice();
  for (let i = 0; i < n; i++) {
    out.push(out.shift());
  }
  return out;
}
function xorBits(a, b) {
  const out = new Array(a.length);
  for (let i = 0; i < a.length; i++) out[i] = a[i] ^ b[i];
  return out;
}
function sboxSubstitution(bits48) {
  const out32 = [];
  for (let i = 0; i < 8; i++) {
    const start = i * 6;
    const b = bits48.slice(start, start + 6);
    const row = (b[0] << 1) | b[5];
    const col = (b[1] << 3) | (b[2] << 2) | (b[3] << 1) | b[4];
    const val = SBOXES[i][row][col]; // 0..15
    // convert 4-bit value to bits
    out32.push((val >> 3) & 1, (val >> 2) & 1, (val >> 1) & 1, val & 1);
  }
  return out32;
}
function bitsToHex(bits) {
  let hex = "";
  for (let i = 0; i < bits.length; i += 4) {
    const v = (bits[i] << 3) | (bits[i + 1] << 2) | (bits[i + 2] << 1) | bits[i + 3];
    hex += v.toString(16);
  }
  return hex;
}

/* ----------------------------- DES CORE ----------------------------- */
function generateSubkeys(key64bits) {
  // Apply PC-1 to get 56 bits
  const key56 = permute(key64bits, PC1);
  let C = key56.slice(0, 28);
  let D = key56.slice(28, 56);
  const subkeys = [];
  for (let r = 0; r < 16; r++) {
    C = leftRotate(C, SHIFTS[r]);
    D = leftRotate(D, SHIFTS[r]);
    const CD = C.concat(D);
    subkeys.push(permute(CD, PC2)); // 48 bits
  }
  return subkeys;
}
function fFunction(R32, K48) {
  const expanded = permute(R32, E);        // 48
  const xored = xorBits(expanded, K48);    // 48
  const sSub = sboxSubstitution(xored);    // 32
  const permP = permute(sSub, P);          // 32
  return permP;
}
function encryptBlockDES(block64, subkeys, rounds) {
  const ip = permute(block64, IP); // 64
  let L = ip.slice(0, 32);
  let R = ip.slice(32, 64);
  for (let i = 0; i < rounds; i++) {
    const f = fFunction(R, subkeys[i]);
    const newR = xorBits(L, f);
    L = R;
    R = newR;
  }
  // After last round, swap L and R, then FP
  const preoutput = R.concat(L);
  const fp = permute(preoutput, FP);
  return fp;
}

/* ----------------------------- UI & LOGIC ----------------------------- */
function readSteps() {
  const checked = document.querySelector('input[name="steps"]:checked');
  return checked ? parseInt(checked.value, 10) : 16;
}
function updateDisplay() {
  const msg = document.getElementById('input-message').value;
  const keyStr = document.getElementById('input-key').value;
  const steps = readSteps();

  const outputEl = document.getElementById('output-results');
  outputEl.textContent = ""; // clear

  // Message processing: ASCII -> bits, pad to multiple of 64, split
  let msgBits = stringToBits(msg);
  msgBits = padRightToMultiple(msgBits, 64);
  const blocks = [];
  for (let i = 0; i < msgBits.length; i += 64) {
    blocks.push(msgBits.slice(i, i + 64));
  }

  // Key processing: ASCII -> bits, pad/truncate to 64
  let keyBits = ensureExactly(stringToBits(keyStr), 64);
  const subkeys = generateSubkeys(keyBits);

  // Build results lines for even s up to selected steps
  const lines = [];
  for (let s = 2; s <= steps; s += 2) {
    let current_step_ciphertext = "";
    for (const block of blocks) {
      const cipherBits = encryptBlockDES(block, subkeys, s);
      current_step_ciphertext += bitsToHex(cipherBits);
    }
    const sText = String(s).padStart(2, ' ');
    lines.push(`${sText}      ${current_step_ciphertext}`);
  }
  outputEl.textContent = lines.join("\n");
}

// Hook events
document.getElementById('input-message').addEventListener('input', updateDisplay);
document.getElementById('input-key').addEventListener('input', updateDisplay);
document.querySelectorAll('input[name="steps"]').forEach(r => {
  r.addEventListener('change', updateDisplay);
});

// Initialize on load
window.addEventListener('DOMContentLoaded', updateDisplay);
</script>
</body>
</html>
