  <script>
    // Global application state
    const state = {
      currentAlgorithm: 'shellsort',
      numElements: 10,
      sortHistory: [],
      currentStep: 1,
      isPlaying: false,
      playTimer: null,
      baseArray: []
    };

    // DOM references
    const elSliderElements = document.getElementById('slider-elements');
    const elElementsValue = document.getElementById('elements-value');
    const elSliderStep = document.getElementById('slider-step');
    const elStepValue = document.getElementById('step-value');
    const btnPlayPause = document.getElementById('btn-play-pause');
    const btnBack = document.getElementById('btn-step-backward');
    const btnForward = document.getElementById('btn-step-forward');
    const btnResetStep = document.getElementById('btn-reset-step');
    const btnRandomize = document.getElementById('btn-randomize');
    const sortButtons = {
      shellsort: document.getElementById('btn-shellsort'),
      bubblesort: document.getElementById('btn-bubblesort'),
      insertionsort: document.getElementById('btn-insertionsort'),
      cocktail: document.getElementById('btn-cocktail'),
      bogosort: document.getElementById('btn-bogosort')
    };

    // Utility helpers
    function shuffle(arr){
      for(let i=arr.length-1;i>0;i--){
        const j = Math.floor(Math.random()*(i+1));
        [arr[i], arr[j]] = [arr[j], arr[i]];
      }
      return arr;
    }
    function isSorted(arr){
      for(let i=0;i<arr.length-1;i++){
        if(arr[i] > arr[i+1]) return false;
      }
      return true;
    }
    function record(history, arr){
      history.push(arr.slice());
    }

    // Sorting algorithms (each returns full history including initial unsorted state)
    function historyShellSort(input){
      const a = input.slice();
      const history = [a.slice()];
      const n = a.length;
      for(let gap=Math.floor(n/2); gap>0; gap=Math.floor(gap/2)){
        for(let i=gap; i<n; i++){
          const temp = a[i];
          let j = i;
          while(j >= gap && a[j-gap] > temp){
            a[j] = a[j-gap];
            record(history, a);
            j -= gap;
          }
          a[j] = temp;
          record(history, a);
        }
      }
      return history;
    }
    function historyBubbleSort(input){
      const a = input.slice();
      const history = [a.slice()];
      const n = a.length;
      for(let i=0; i<n-1; i++){
        let swapped=false;
        for(let j=0; j<n-1-i; j++){
          if(a[j] > a[j+1]){
            [a[j], a[j+1]] = [a[j+1], a[j]];
            record(history, a);
            swapped=true;
          }
        }
        if(!swapped) break;
      }
      return history;
    }
    function historyInsertionSort(input){
      const a = input.slice();
      const history = [a.slice()];
      const n = a.length;
      for(let i=1;i<n;i++){
        const key = a[i];
        let j = i-1;
        while(j>=0 && a[j] > key){
          a[j+1] = a[j];
          record(history, a);
          j--;
        }
        a[j+1] = key;
        record(history, a);
      }
      return history;
    }
    function historyCocktailSort(input){
      const a = input.slice();
      const history = [a.slice()];
      let n = a.length;
      let start = 0;
      let end = n - 1;
      let swapped = true;
      while(swapped){
        swapped = false;
        for(let i=start; i<end; i++){
          if(a[i] > a[i+1]){
            [a[i], a[i+1]] = [a[i+1], a[i]];
            record(history, a);
            swapped = true;
          }
        }
        if(!swapped) break;
        swapped = false;
        end--;
        for(let i=end-1; i>=start; i--){
          if(a[i] > a[i+1]){
            [a[i], a[i+1]] = [a[i+1], a[i]];
            record(history, a);
            swapped = true;
          }
        }
        start++;
      }
      return history;
    }
    function historyBogoSort(input){
      const a = input.slice();
      const history = [a.slice()];
      const maxShuffles = 800; // safety cap
      let count = 0;
      while(!isSorted(a) && count < maxShuffles){
        shuffle(a);
        record(history, a);
        count++;
      }
      return history;
    }

    function computeHistory(algorithm, arr){
      switch(algorithm){
        case 'shellsort': return historyShellSort(arr);
        case 'bubblesort': return historyBubbleSort(arr);
        case 'insertionsort': return historyInsertionSort(arr);
        case 'cocktail': return historyCocktailSort(arr);
        case 'bogosort': return historyBogoSort(arr);
        default: return [arr.slice()];
      }
    }

    // Control logic
    function setActiveAlgorithm(algo){
      state.currentAlgorithm = algo;
      for(const k in sortButtons){
        sortButtons[k].classList.toggle('active', k===algo);
