        elElementsValue.textContent = '7';
      } else {
        elSliderElements.disabled = false;
      }
    }

    function stopPlaying(){
      if(state.playTimer){
        clearInterval(state.playTimer);
        state.playTimer = null;
      }
      state.isPlaying = false;
      btnPlayPause.textContent = '▶';
    }

    function startPlaying(){
      if(state.sortHistory.length <= 1) return;
      state.isPlaying = true;
      btnPlayPause.textContent = '❚❚';
      state.playTimer = setInterval(()=>{
        if(state.currentStep < state.sortHistory.length){
          elSliderStep.value = String(state.currentStep + 1);
          elSliderStep.dispatchEvent(new Event('input'));
        } else {
          stopPlaying();
        }
      }, 200);
    }

    function randomizeAndCompute(){
      stopPlaying();
      let N = parseInt(elSliderElements.value,10);
      if(state.currentAlgorithm === 'bogosort') N = 7;
      state.numElements = N;

      const arr = Array.from({length:N}, (_,i)=>i+1);
      shuffle(arr);
      state.baseArray = arr.slice();

      state.sortHistory = computeHistory(state.currentAlgorithm, arr);
      // Step slider setup
      elSliderStep.max = String(state.sortHistory.length);
      elSliderStep.value = '1';
      state.currentStep = 1;
      elStepValue.textContent = '1';

      // Redraw
      if (typeof redraw === 'function') redraw();
    }

    // Event listeners
    elSliderElements.addEventListener('input', () => {
      elElementsValue.textContent = elSliderElements.value;
    });

    elSliderStep.addEventListener('input', () => {
      const v = parseInt(elSliderStep.value,10);
      state.currentStep = v;
      elStepValue.textContent = String(v);
      if (typeof redraw === 'function') redraw();
    });

    btnPlayPause.addEventListener('click', () => {
      if(state.isPlaying) stopPlaying();
      else startPlaying();
    });
    btnBack.addEventListener('click', () => {
      const v = Math.max(1, state.currentStep - 1);
      if(v !== state.currentStep){
        elSliderStep.value = String(v);
        elSliderStep.dispatchEvent(new Event('input'));
      }
    });
    btnForward.addEventListener('click', () => {
      const v = Math.min(state.sortHistory.length, state.currentStep + 1);
      if(v !== state.currentStep){
        elSliderStep.value = String(v);
        elSliderStep.dispatchEvent(new Event('input'));
      }
    });
    btnResetStep.addEventListener('click', () => {
      elSliderStep.value = '1';
      elSliderStep.dispatchEvent(new Event('input'));
    });

    btnRandomize.addEventListener('click', randomizeAndCompute);

    // Sort buttons
    for (const [algo, btn] of Object.entries(sortButtons)) {
      btn.addEventListener('click', () => {
        setActiveAlgorithm(algo);
        randomizeAndCompute();
      });
    }

    // Initialize controls on load
    window.addEventListener('DOMContentLoaded', () => {
      // Defaults are already set in HTML; ensure correct state
      setActiveAlgorithm('shellsort');
      elElementsValue.textContent = elSliderElements.value;
      elStepValue.textContent = elSliderStep.value;
      randomizeAndCompute();
    });

    // p5.js sketch
    let chartMargins = {left:50, right:20, top:20, bottom:40};
    function setup(){
      // Replace placeholder canvas with p5 canvas using the same id
      const placeholder = document.getElementById('bar-chart-canvas');
      if(placeholder) placeholder.remove();

      const area = document.getElementById('visualization-area');
      let w = Math.max(320, area.clientWidth - 24);
      let h = 420;
      const cnv = createCanvas(w, h);
      cnv.parent('visualization-area');
      cnv.elt.id = 'bar-chart-canvas';
      colorMode(HSB, 360, 100, 100);
      textFont('Arial, Helvetica, sans-serif');
      textSize(12);
      noLoop();
      redraw();
    }

    function windowResized(){
      const area = document.getElementById('visualization-area');
      let w = Math.max(320, area.clientWidth - 24);
      let h = 420;
      resizeCanvas(w, h);
      if (typeof redraw === 'function') redraw();
    }

    function drawAxis(x, y, w, h){
      stroke(80);
      strokeWeight(1);
      // Y axis
      line(x, y, x, y + h);
      // X axis
      line(x, y + h, x + w, y + h);

      const ticks = [0,5,10,15,20];
      fill(80);
      noStroke();
      textAlign(RIGHT, CENTER);
      for(const t of ticks){
        const ty = y + h - (t/20) * h;
        stroke(80);
        line(x-5, ty, x, ty);
        noStroke();
        text(t.toString(), x-8, ty);
      }
    }

    function draw(){
      background(248);

      const chartX = chartMargins.left;
      const chartY = chartMargins.top;
      const chartW = width - chartMargins.left - chartMargins.right;
      const chartH = height - chartMargins.top - chartMargins.bottom;

      drawAxis(chartX, chartY, chartW, chartH);

      const history = state.sortHistory;
      if(!history || history.length === 0) return;

      const arr = history[Math.max(0, Math.min(history.length-1, state.currentStep - 1))];
      const N = arr.length || 1;

      const stepW = chartW / N;
      const barW = Math.max(2, stepW * 0.8);

      stroke(0);
      strokeWeight(1);
      for(let i=0;i<N;i++){
        const v = arr[i];
        const h = map(v, 1, N, 0, chartH);
        const x = chartX + i * stepW + (stepW - barW)/2;
        const y = chartY + chartH - h;

        const hue = map(v, 1, N, 240, 0); // blue to red
        fill(hue, 70, 90);
        rect(x, y, barW, h);
      }
    }
  </script>
