    if (v1 === v2) {
      return result; // Parallel per spec
    }
    const T = (x1 - x2) / (v2 - v1);
    const X = x1 + v1 * T;

    if (T >= 0 && T <= 1000) {
      result.hasIntersection = true;
      result.T = T;
      result.X = X;
    }
    return result;
  }

  function updateDisplays(x1, v1, x2, v2) {
    valueX1.textContent = Math.round(x1);
    valueV1.textContent = Number(v1).toFixed(2);
    valueX2.textContent = Math.round(x2);
    valueV2.textContent = Number(v2).toFixed(2);
  }

  function updateResult(x1, v1, x2, v2) {
    const res = computeIntersection(x1, v1, x2, v2);
    if (res.hasIntersection) {
      resultText.textContent = `T = ${format2(res.T)} s | X = ${format2(res.X)} m`;
    } else {
      resultText.textContent = "The runners' paths never intersect";
    }
    return res;
  }

  function updatePlot(x1, v1, x2, v2, inter) {
    const t0 = 0, t1 = 1000;
    const y1_0 = x1, y1_1 = x1 + v1 * t1;
    const y2_0 = x2, y2_1 = x2 + v2 * t1;

    const trace1 = {
      type: 'scatter',
      mode: 'lines',
      x: [t0, t1],
      y: [y1_0, y1_1],
      line: { color: 'rgb(70,130,180)', width: 3 },
      name: 'Runner 1'
    };

    const trace2 = {
      type: 'scatter',
      mode: 'lines',
      x: [t0, t1],
      y: [y2_0, y2_1],
      line: { color: 'rgb(255,165,0)', width: 3 },
      name: 'Runner 2'
    };

    const data = [trace1, trace2];

    if (inter.hasIntersection) {
      data.push({
        type: 'scatter',
        mode: 'markers',
        x: [inter.T],
        y: [inter.X],
        marker: {
          color: '#2ecc71',
          line: { color: '#1e8f4d', width: 1.5 },
          size: 10,
          symbol: 'circle'
        },
        name: 'Intersection'
      });
    }

    const layout = {
      margin: { t: 20, r: 20, b: 40, l: 60 },
      showlegend: false,
      xaxis: {
        title: 't',
        range: [0, 1000],
        showgrid: true,
        gridcolor: '#e5e5e5',
        zeroline: false
      },
      yaxis: {
        title: 'distance',
        autorange: true,
        showgrid: true,
        gridcolor: '#eeeeee',
        zeroline: true,
        zerolinecolor: '#888'
      }
    };

    Plotly.react(plotDiv, data, layout, {responsive: true});
  }

  function readValues() {
    return {
      x1: Number(sliderX1.value),
      v1: Number(sliderV1.value),
      x2: Number(sliderX2.value),
      v2: Number(sliderV2.value)
    };
  }

  function updateAll() {
    const { x1, v1, x2, v2 } = readValues();
    updateDisplays(x1, v1, x2, v2);
    const inter = updateResult(x1, v1, x2, v2);
    updatePlot(x1, v1, x2, v2, inter);
  }

  // Event bindings
  [sliderX1, sliderV1, sliderX2, sliderV2].forEach(sl => {
    sl.addEventListener('input', updateAll);
  });

  btnReset.addEventListener('click', () => {
    sliderX1.value = DEFAULTS.x1;
    sliderV1.value = DEFAULTS.v1;
    sliderX2.value = DEFAULTS.x2;
    sliderV2.value = DEFAULTS.v2;
    updateAll();
  });

  // Initial render
  updateAll();
})();
</script>
</body>
</html>
