    });

    updateFromSlider(); // initial render
  }

  function draw() {
    clear();
    drawScene();
    const phase = parseInt(document.getElementById('slider-phase').value, 10);
    drawProcesses(phase);
    drawTooltips();
  }

  function drawScene() {
    labelBoxes = [];

    // Background: sky and ground
    noStroke();
    fill('#CAE9FF'); rect(0, 0, width, height/2);
    fill('#9B7653'); rect(0, height/2, width, height/2);

    // Sun with rays
    push();
    translate(480, 80);
    stroke('#F0C400'); strokeWeight(6);
    fill('#FFDD00'); ellipse(0, 0, 110, 110);
    noStroke(); fill('#FFDD00');
    for (let i = 0; i < 8; i++) {
      const ang = -PI/4 + (i * PI/8);
      const x = cos(ang) * 85;
      const y = sin(ang) * 85;
      push(); translate(x, y); rotate(ang);
      rect(0, -5, 26, 10, 2);
      pop();
    }
    pop();

    // Clouds (top-left)
    push();
    noStroke(); fill('#E0E0E0');
    ellipse(110, 90, 120, 60);
    ellipse(160, 95, 120, 60);
    ellipse(135, 75, 100, 50);
    ellipse(135, 105, 140, 48);
    // Label "Clouds"
    const cloudsLabel = 'Clouds';
    fill('#505050'); textSize(24); textAlign(CENTER, CENTER);
    text(cloudsLabel, 140, 95);
    const cW = textWidth(cloudsLabel);
    addLabelBox(140 - cW/2, 95 - 14, cW, 28, 'Clouds', 'Water vapor condensed in the atmosphere.');
    pop();

    // Rain under clouds
    stroke('#0000FF'); strokeWeight(2);
    const rs = [[95, 135],[120, 140],[150, 145]];
    rs.forEach(([rx, ry]) => {
      line(rx, ry, rx-8, ry+28);
      line(rx-6, ry+36, rx-14, ry+64);
    });

    // Rocks
    noStroke(); fill('#5A5A5A');
    ellipse(175, 255, 64, 64);
    ellipse(205, 265, 52, 52);
    // Rocks label
    const rocksLabel = 'Rocks';
    fill('#E8E8E8'); textSize(18); textAlign(CENTER, TOP);
    text(rocksLabel, 175, 290);
    const rW = textWidth(rocksLabel);
    addLabelBox(175 - rW/2, 290, rW, 22, 'Rocks',
      'A primary reservoir of phosphorus in the form of phosphate.');

    // Water (bottom-left)
    push();
    noStroke();
    fill(0,0,255,150);
    ellipse(80, 420, 120, 100);
    ellipse(125, 450, 120, 100);
    // Water label
    fill('#00008B'); textSize(22); textAlign(LEFT, TOP);
    const waterLabel = 'Water';
    text(waterLabel, 30, 445);
    const wW = textWidth(waterLabel);
    addLabelBox(30, 445, wW, 26, 'Water',
      'Phosphorus is carried into water bodies by runoff and leaching.');
    pop();

    // Animal (stylized cow)
    drawAnimal(325, 275);
    fill(0); textSize(20); textAlign(CENTER, TOP);
    const animalsLabel = 'Animals';
    text(animalsLabel, 325, 318);
    const aW = textWidth(animalsLabel);
    addLabelBox(325 - aW/2, 318, aW, 24, 'Animals',
      'Obtain phosphorus by eating plants or other animals.');

    // Plants group
    drawPlants(440, 360);
    fill('#006400'); textSize(20); textAlign(LEFT, BOTTOM);
    const plantsLabel = 'Plants';
    text(plantsLabel, 465, 347);
    const pW = textWidth(plantsLabel);
    addLabelBox(465, 327, pW, 22, 'Plants',
      'Absorb phosphate from the soil to build organic molecules.');
  }

  function drawAnimal(x, y) {
    push();
    translate(x, y);
    stroke(0); fill(255);
    rect(-28, -20, 72, 42, 5);          // body
    rect(44, -28, 26, 26, 4);           // head
    triangle(68, -28, 60, -40, 55, -28);// ear
    // tail
    fill(255); noStroke();
    quad(-28, -10, -50, -4, -40, 4, -28, 2);
    // legs
    stroke(0); fill(255);
    rect(-10, 20, 12, 18);
    rect(26, 20, 12, 18);
    fill(0); noStroke();
    rect(-10, 36, 12, 6);
    rect(26, 36, 12, 6);
    // spots
    ellipse(10, 2, 7, 7);
    ellipse(3, 10, 9, 9);
    ellipse(0, 0, 5, 5);
    ellipse(17, 12, 6, 6);
    // face
    fill('blue'); ellipse(60, -20, 5, 5); // eye
    fill('orange'); triangle(70, -16, 76, -12, 70, -10); // beak-like mouth
    pop();
  }

  function drawPlants(x, y) {
    push();
    translate(x, y);
    noStroke(); fill('#90EE90');
    triangle(-10, 20,   0, -6,  10, 20);
    triangle( 10, 20,  20, -6,  30, 20);
    triangle( 30, 20,  40, -6,  50, 20);
    pop();
  }

  function drawProcesses(phase) {
    stroke(0); strokeWeight(2); fill(0);
    textSize(14); textAlign(LEFT, BOTTOM);

    // >=1 Erosion: from rain area to rocks
    if (phase >= 1) {
      drawArrow(130, 180, 170, 235);
      addProcessLabelNear(130, 180, 170, 235,
        'Erosion',
        'Weathering of rocks by wind and water releases phosphate.');
    }
    // >=2 Runoff: rocks to water
    if (phase >= 2) {
      drawArrow(180, 290, 110, 400);
      addProcessLabelNear(180, 290, 110, 400,
        'Runoff',
        'Phosphate from fertilizers and rocks is washed into rivers and oceans.');
    }
    // >=3 Soil: water to soil area
    if (phase >= 3) {
      drawArrow(140, 450, 300, 440);
      addProcessLabelNear(140, 450, 300, 440,
        'Soil',
        'Phosphorus in the soil is available for plants.');
    }
    // >=4 Leaching: ground near rocks to water body
    if (phase >= 4) {
      drawArrow(200, 360, 130, 410);
      addProcessLabelNear(200, 360, 130, 410,
        'Leaching',
        'Phosphates in the soil are dissolved in water and carried away.');
    }
    // >=5 Plant uptake: soil to plants
    if (phase >= 5) {
      drawArrow(320, 430, 430, 380);
      addProcessLabelNear(320, 430, 430, 380,
        'Plant uptake',
        'Plants absorb inorganic phosphate from the soil through their roots.');
    }
    // >=6 Animals eat plants: plants to animal
    if (phase >= 6) {
      drawArrow(450, 360, 360, 290);
      addProcessLabelNear(450, 360, 360, 290,
        'Animals eat plants',
        'Transfers phosphorus from plants to animals along the food chain.');
    }
    // >=7 Decomposition: animal to soil
    if (phase >= 7) {
      drawArrow(330, 310, 300, 390);
      addProcessLabelNear(330, 310, 300, 390,
        'Decomposition',
        'Bacteria break down dead organic matter, returning phosphate to the soil and water.');
    }
    // >=8 Geologic uplift: curved arrow from water bottom to rocks
    if (phase >= 8) {
      const p0 = {x: 70, y: 490};
      const p1 = {x: 40, y: 330};
      const p2 = {x: 160, y: 280};
      const p3 = {x: 180, y: 320};
      drawCurvedArrow(p0, p1, p2, p3);

      const midPt = bezierPointVec(p0, p1, p2, p3, 0.55);
      const lab = 'Geologic uplift';
      fill(0); textAlign(LEFT, BOTTOM); text(lab, midPt.x - 36, midPt.y - 8);
      const lw = textWidth(lab);
      addLabelBox(midPt.x - 36, midPt.y - 22, lw, 18, 'Geologic uplift',
        'Over millions of years, ocean sediments containing phosphate are lifted up to form new land.');
    }
  }

  // Draw a straight arrow from (x1,y1) to (x2,y2)
  function drawArrow(x1, y1, x2, y2) {
    push();
    stroke(0); strokeWeight(2); fill(0);
    line(x1, y1, x2, y2);
    const ang = Math.atan2(y2 - y1, x2 - x1);
    const len = 11;
    const x3 = x2 - len * Math.cos(ang - Math.PI / 6);
    const y3 = y2 - len * Math.sin(ang - Math.PI / 6);
    const x4 = x2 - len * Math.cos(ang + Math.PI / 6);
    const y4 = y2 - len * Math.sin(ang + Math.PI / 6);
    triangle(x2, y2, x3, y3, x4, y4);
    pop();
  }

  // Draw a curve with an arrow head at end
  function drawCurvedArrow(p0, p1, p2, p3) {
    noFill();
    stroke(0); strokeWeight(2);
    bezier(p0.x, p0.y, p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);

    const t = 0.98;
    const tx = bezierTangent(p0.x, p1.x, p2.x, p3.x, t);
    const ty = bezierTangent(p0.y, p1.y, p2.y, p3.y, t);
    const ang = Math.atan2(ty, tx);
    const len = 11;
    const x2 = p3.x, y2 = p3.y;
    const x3 = x2 - len * Math.cos(ang - Math.PI / 6);
    const y3 = y2 - len * Math.sin(ang - Math.PI / 6);
    const x4 = x2 - len * Math.cos(ang + Math.PI / 6);
    const y4 = y2 - len * Math.sin(ang + Math.PI / 6);
    fill(0); noStroke();
    triangle(x2, y2, x3, y3, x4, y4);
  }

  function bezierPointVec(p0, p1, p2, p3, t) {
    return {
      x: bezierPoint(p0.x, p1.x, p2.x, p3.x, t),
      y: bezierPoint(p0.y, p1.y, p2.y, p3.y, t),
    };
  }

  // Add a label's bounding box for hover tooltip
  function addLabelBox(x, y, w, h, textKey, tooltip) {
    labelBoxes.push({ x, y, w, h, text: textKey, tip: tooltip });
  }

  // Place process label near its arrow
  function addProcessLabelNear(x1, y1, x2, y2, label, tooltip) {
    const mx = 0.6 * x1 + 0.4 * x2;
    const my = 0.6 * y1 + 0.4 * y2 - 8;
    fill(0); noStroke(); textSize(14); textAlign(LEFT, BOTTOM);
    text(label, mx, my);
    const w = textWidth(label);
    addLabelBox(mx, my - 14, w, 16, label, tooltip);
  }

  // Tooltip rendering with simple word-wrap
  function drawTooltips() {
    let hovered = null;
    for (const b of labelBoxes) {
      if (mouseX >= b.x && mouseX <= b.x + b.w && mouseY >= b.y && mouseY <= b.y + b.h) {
        hovered = b; break;
      }
    }
    if (!hovered) return;

    const padding = 8;
    const maxW = 280;
    const txt = hovered.tip;
    textSize(14); textAlign(LEFT, TOP);

    const wrapped = wrapText(txt, maxW, 16);
    const w = Math.max(Math.min(wrapped.maxWidth, maxW), 120);
    const h = wrapped.totalHeight;

    const bx = constrain(mouseX + 12, 0, width - w - padding * 2);
    const by = constrain(mouseY + 12, 0, height - h - padding * 2);

    noStroke(); fill(255, 255, 240, 235);
    rect(bx, by, w + padding * 2, h + padding * 2, 6);
    fill(0);
    let y = by + padding;
    for (const line of wrapped.lines) {
      text(line, bx + padding, y);
      y += 16;
    }
  }

  function wrapText(str, maxWidth, lineHeight) {
    const words = str.split(' ');
    let line = '';
    const lines = [];
    let maxW = 0;
    textSize(14);
    for (let i = 0; i < words.length; i++) {
      const test = line ? line + ' ' + words[i] : words[i];
      if (textWidth(test) > maxWidth && line) {
        lines.push(line);
        maxW = Math.max(maxW, textWidth(line));
        line = words[i];
      } else {
        line = test;
      }
    }
    if (line) {
      lines.push(line);
      maxW = Math.max(maxW, textWidth(line));
    }
    return { lines, totalHeight: lines.length * lineHeight, maxWidth: maxW };
  }

  // Redraw on hover move for responsive tooltips
  function mouseMoved() { redraw(); }
  function mouseDragged() { redraw(); }
  function mouseOut() { redraw(); }
</script>
</body>
</html>
