  sketchP5 = p;
  p.setup = function() {
    const c = p.createCanvas(600, 450);
    c.parent("canvas-container");
    p.noLoop(); // manual redraws
  };

  function drawEdge(e, colorStr, weight) {
    const V = demoData[currentExample].vertices;
    p.stroke(colorStr);
    p.strokeWeight(weight);
    p.line(V[e.u].x, V[e.u].y, V[e.v].x, V[e.v].y);
