  p.draw = function() {
    p.background(255);

    const example = demoData[currentExample];
    const step = example.steps[currentStep-1];

    // Draw unmatched edges first (thin light gray)
    const matchSet = new Set(step.matching);
    p.stroke('#cccccc');
    p.strokeWeight(2);
    example.edges.forEach((e, idx) => {
      if (!matchSet.has(idx)) {
        drawEdge(e, '#cccccc', 2);
      }
    });

    // Draw matched edges (thick, red during intermediate, blue for final)
    const edgeColor = step.isFinal ? '#4d4dff' : '#ff4d4d';
    step.matching.forEach(idx => {
      drawEdge(example.edges[idx], edgeColor, 6);
    });

    // Draw blossom if present (semi-transparent blue polygon with black border)
    if (step.blossom) {
      const V = example.vertices;
      p.push();
      p.stroke(0);
      p.strokeWeight(1);
      p.fill(0, 0, 255, 80); // rgba(0,0,255,0.3) approx
      p.beginShape();
      step.blossom.vertices.forEach(vi => p.vertex(V[vi].x, V[vi].y));
