      // Super-vertex (yellow circle) at topVertex
      const tv = V[step.blossom.topVertex];
      p.fill('#ffff00');
      p.stroke(0);
      p.strokeWeight(1);
      p.circle(tv.x, tv.y, 16);
      p.fill(0);
      p.noStroke();
      p.textAlign(p.CENTER, p.CENTER);
      p.text('B', tv.x, tv.y-1);
      p.pop();
    }

    // Draw vertices on top
    p.noStroke();
    p.fill(0);
    example.vertices.forEach(v => {
      p.circle(v.x, v.y, 8);
    });
  };
});

// Initialize description text
updateDescription();
