    };
    const labels = {
      cylinderRadius: el('label-cylinder-radius'),
      opacity: el('label-opacity'),
      drillRadius: el('label-drill-radius'),
      inclination: el('label-inclination'),
      rotation: el('label-rotation'),
      offset: el('label-offset'),
    };
    const checkboxBoundaryOnly = el('checkbox-boundary-only');

    // Initialize slider attributes and defaults
    function initControls() {
      sliders.cylinderRadius.min = 0.5; sliders.cylinderRadius.max = 3.0; sliders.cylinderRadius.step = 0.05; sliders.cylinderRadius.value = 2.1;
      sliders.opacity.min = 0.0; sliders.opacity.max = 1.0; sliders.opacity.step = 0.01; sliders.opacity.value = 0.95;
      sliders.drillRadius.min = 0.1; sliders.drillRadius.max = 2.5; sliders.drillRadius.step = 0.05; sliders.drillRadius.value = 1.35;
      sliders.inclination.min = 0.0; sliders.inclination.max = 1.57; sliders.inclination.step = 0.01; sliders.inclination.value = 0.76;
      sliders.rotation.min = -3.14; sliders.rotation.max = 3.14; sliders.rotation.step = 0.01; sliders.rotation.value = 0.9;
      sliders.offset.min = -2.0; sliders.offset.max = 2.0; sliders.offset.step = 0.01; sliders.offset.value = -0.92;
