      const updateLabel = () => {
        labels.cylinderRadius.textContent = toFixed2(sliders.cylinderRadius.value);
        labels.opacity.textContent = toFixed2(sliders.opacity.value);
        labels.drillRadius.textContent = toFixed2(sliders.drillRadius.value);
        labels.inclination.textContent = toFixed2(sliders.inclination.value);
        labels.rotation.textContent = toFixed2(sliders.rotation.value);
        labels.offset.textContent = toFixed2(sliders.offset.value);
      };
      updateLabel();

      Object.values(sliders).forEach(s => {
        s.addEventListener('input', () => { updateLabel(); updateScene(); });
      });
      checkboxBoundaryOnly.addEventListener('change', () => updateScene());
    }

    // Three.js setup
    const canvas = document.getElementById('main-canvas');
    const renderer = new THREE.WebGLRenderer({ antialias: true, canvas });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));

    const scene = new THREE.Scene();
    scene.background = new THREE.Color('#e0e0e0');

    const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 100);
    camera.position.set(9, 7, 10);
    camera.lookAt(0, 0, 0);

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.enablePan = true;
    controls.minDistance = 4;
    controls.maxDistance = 35;

    // Lights
    const ambient = new THREE.AmbientLight(0xffffff, 0.55);
    scene.add(ambient);

    const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.6);
    dirLight1.position.set(5, 8, 6);
    scene.add(dirLight1);

    const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.35);
    dirLight2.position.set(-7, -5, -4);
    scene.add(dirLight2);

    // Resize handling
    function resizeRenderer() {
      const ctn = document.getElementById('visualization-container');
      const w = ctn.clientWidth - 36; // account for inner frame borders
      const h = ctn.clientHeight - 36;
      const width = Math.max(100, w);
      const height = Math.max(100, h);
      renderer.setSize(width, height, false);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    }
    window.addEventListener('resize', () => { resizeRenderer(); });

    // Content group (recreated every update)
    let contentGroup = null;
    const evaluator = new Evaluator();

    // Constants
    const MAIN_HEIGHT = 8.0; // fixed height for main cylinder
    const DRILL_HEIGHT = 12.0; // long enough to fully pass through
    const TUBE_RADIUS = 0.05;

    // Helper: dispose hierarchy
    function disposeObject3D(obj) {
      obj.traverse(child => {
        if (child.geometry) { child.geometry.dispose(); }
        if (child.material) {
          if (Array.isArray(child.material)) {
            child.material.forEach(m => m && m.dispose && m.dispose());
          } else if (child.material.dispose) { child.material.dispose(); }
        }
      });
    }

    // Create Tube objects from segmented points
    function tubesFromSegments(segments, colorHex) {
      const group = new THREE.Group();
      const mat = new THREE.MeshStandardMaterial({
        color: colorHex, roughness: 0.5, metalness: 0.1
      });

      for (const seg of segments) {
        if (!seg || seg.length < 2) continue;
        const curve = new THREE.CatmullRomCurve3(seg, false, 'catmullrom', 0.0);
        const tubularSegments = Math.max(32, seg.length * 4);
        const geom = new THREE.TubeGeometry(curve, tubularSegments, TUBE_RADIUS, 8, false);
        const mesh = new THREE.Mesh(geom, mat);
        group.add(mesh);
      }
      return group;
    }

    // Main update function
    function updateScene() {
      // Remove previous content
      if (contentGroup) {
        scene.remove(contentGroup);
        disposeObject3D(contentGroup);
      }
      contentGroup = new THREE.Group();

      // Read UI values
      const R = parseFloat(sliders.cylinderRadius.value);
      const opacity = parseFloat(sliders.opacity.value);
      const r = parseFloat(sliders.drillRadius.value);
      const inc = parseFloat(sliders.inclination.value);
      const rotY = parseFloat(sliders.rotation.value);
      const offsetY = parseFloat(sliders.offset.value);
      const boundaryOnly = checkboxBoundaryOnly.checked;

      // Materials
      const tanColor = '#D2B48C';
      const tanMat = new THREE.MeshStandardMaterial({
        color: tanColor,
        roughness: 0.7,
        metalness: 0.1,
        transparent: true,
        opacity: boundaryOnly ? 0.15 : opacity
      });

      // Main cylinder (base)
      const mainGeo = new THREE.CylinderGeometry(R, R, MAIN_HEIGHT, 96, 1, false);
      const mainMeshForCSG = new THREE.Mesh(mainGeo);              // neutral for CSG
      const ghostMaterial = new THREE.MeshStandardMaterial({
        color: boundaryOnly ? '#f0f0f0' : tanColor,
        roughness: 0.85,
        metalness: 0.0,
        transparent: true,
        opacity: boundaryOnly ? 0.15 : Math.max(0.1, opacity * 0.7)
      });
      const ghostMesh = new THREE.Mesh(mainGeo.clone(), ghostMaterial);
      ghostMesh.visible = boundaryOnly;
      contentGroup.add(ghostMesh);

      // Drill cylinder transform
      const drillGeo = new THREE.CylinderGeometry(r, r, DRILL_HEIGHT, 96, 1, false);
      const drillMesh = new THREE.Mesh(drillGeo);
      // Compose rotations explicitly: Z 90deg -> X inclination -> Y rotation
      const q = new THREE.Quaternion();
      const qz = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0,0,1), Math.PI / 2);
      const qx = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1,0,0), inc);
      const qy = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0,1,0), rotY);
      q.multiply(qz).multiply(qx).multiply(qy);
      drillMesh.quaternion.copy(q);
      drillMesh.position.set(0, offsetY, 0);
      drillMesh.updateMatrixWorld(true);

      // CSG operation: subtract drill from main
      // Convert meshes to Brushes (the evaluator bakes transforms)
      const brushA = Brush.fromMesh(mainMeshForCSG);
      const brushB = Brush.fromMesh(drillMesh);
      const resultMesh = evaluator.evaluate(brushA, brushB, SUBTRACTION);
      resultMesh.material = tanMat;
      resultMesh.visible = !boundaryOnly;
      contentGroup.add(resultMesh);

      // Boundary curve computation
      // Axis direction a (unit), center point c of drill, and orthonormal basis u, v
      const rotOnly = new THREE.Matrix4().extractRotation(drillMesh.matrixWorld);
      const a = new THREE.Vector3(1, 0, 0).applyMatrix4(rotOnly).normalize(); // local X axis after Z-rotation
      const c = new THREE.Vector3().setFromMatrixPosition(drillMesh.matrixWorld);

      // Choose a vector not parallel to 'a' to build u, v
      let arbitrary = new THREE.Vector3(0, 1, 0);
      if (Math.abs(a.dot(arbitrary)) > 0.95) arbitrary.set(0, 0, 1);

      const u = new THREE.Vector3().crossVectors(a, arbitrary).normalize(); // ⟂ a
      const v = new THREE.Vector3().crossVectors(a, u).normalize();         // ⟂ a and u

      // Iterate angles and solve quadratic to intersect with main cylinder x^2 + z^2 = R^2
      const steps = 256;
      const epsY = 1e-4;

      let seg1 = []; // first solution (smaller s)
      let seg2 = []; // second solution (larger s)
      const segments1 = [];
      const segments2 = [];

      for (let i = 0; i <= steps; i++) {
        const t = (i / steps) * Math.PI * 2.0;
        const cosT = Math.cos(t), sinT = Math.sin(t);
        const circleOffset = new THREE.Vector3()
          .copy(u).multiplyScalar(r * cosT)
          .add(new THREE.Vector3().copy(v).multiplyScalar(r * sinT));

        const p0 = new THREE.Vector3().copy(c).add(circleOffset);

        const ax = a.x, az = a.z;
        const x0 = p0.x, z0 = p0.z;

        const A = ax * ax + az * az;
        const B = 2.0 * (x0 * ax + z0 * az);
        const C = x0 * x0 + z0 * z0 - R * R;

        const disc = B * B - 4 * A * C;

        const pushOrBreak = (ok1, pnt1, ok2, pnt2) => {
          if (ok1) {
            seg1.push(pnt1);
          } else {
            if (seg1.length > 1) segments1.push(seg1);
            seg1 = [];
          }
          if (ok2) {
            seg2.push(pnt2);
          } else {
            if (seg2.length > 1) segments2.push(seg2);
            seg2 = [];
          }
        };

        if (disc < 0 || A < 1e-8) {
          pushOrBreak(false, null, false, null);
          continue;
        }

        const sqrtD = Math.sqrt(disc);
        const s1 = (-B - sqrtD) / (2 * A);
        const s2 = (-B + sqrtD) / (2 * A);

        const p1 = new THREE.Vector3().copy(c).addScaledVector(a, s1).add(circleOffset);
        const p2 = new THREE.Vector3().copy(c).addScaledVector(a, s2).add(circleOffset);

        // Respect finite height of the main cylinder (|y| <= H/2)
        const ok1 = Math.abs(p1.y) <= (MAIN_HEIGHT * 0.5 + epsY);
        const ok2 = Math.abs(p2.y) <= (MAIN_HEIGHT * 0.5 + epsY);

        pushOrBreak(ok1, p1, ok2, p2);
      }

      // finalize any trailing segments
      if (seg1.length > 1) segments1.push(seg1);
      if (seg2.length > 1) segments2.push(seg2);

      // Create tube meshes for the two curves
      const frontColor = '#FFA500';   // orange
      const backColor  = '#4169E1';   // royal blue
      const tubesGroup1 = tubesFromSegments(segments2, frontColor); // use s2 as "front"
      const tubesGroup2 = tubesFromSegments(segments1, backColor);  // use s1 as "back"

      contentGroup.add(tubesGroup1);
      contentGroup.add(tubesGroup2);

      scene.add(contentGroup);
      render();
    }

    // Animation loop
    function render() {
      controls.update();
      renderer.render(scene, camera);
    }
    function animate() {
      requestAnimationFrame(animate);
      render();
    }

    // Start
    initControls();
    resizeRenderer();
    updateScene();
    animate();
  </script>
</body>
</html>
