  </div>

  <div id="visualization-container">
    <div class="bar-wrapper">
      <div id="canvas-hue-parent"></div>
      <div id="arrow-hue" class="arrow">↑</div>
    </div>
    <div class="bar-wrapper">
      <div id="canvas-saturation-parent"></div>
      <div id="arrow-saturation" class="arrow">↑</div>
    </div>
    <div class="bar-wrapper">
      <div id="canvas-brightness-parent"></div>
      <div id="arrow-brightness" class="arrow">↑</div>
    </div>
    <div class="bar-wrapper">
      <div id="canvas-result-parent"></div>
    </div>
  </div>
</main>

<script>
(function(){
  // DOM references
  const sliderH = document.getElementById('slider-hue');
  const sliderS = document.getElementById('slider-saturation');
  const sliderB = document.getElementById('slider-brightness');
  const valueH  = document.getElementById('value-hue');
  const valueS  = document.getElementById('value-saturation');
  const valueB  = document.getElementById('value-brightness');
  const btnReset = document.getElementById('btn-reset');

  const parentHue = document.getElementById('canvas-hue-parent');
  const parentSat = document.getElementById('canvas-saturation-parent');
  const parentBri = document.getElementById('canvas-brightness-parent');
  const parentRes = document.getElementById('canvas-result-parent');

  const arrowHue = document.getElementById('arrow-hue');
  const arrowSat = document.getElementById('arrow-saturation');
  const arrowBri = document.getElementById('arrow-brightness');

  // visual constants
  const innerPad = 8;     // inner padding inside each canvas
  const barH = 56;        // bar height inside canvas
  const radius = 28;      // rounded corner radius
  const arrowOffset = 6;  // gap between bar bottom and arrow

  let gHue, gSat, gBri, gRes; // p5.Graphics instances
  let p5inst = null;

  const initial = { h:0, s:0.5, b:1 };

  function fmt(x){ return (+x).toFixed(3).replace(/\.?0+$/,''); } // trim trailing zeros

  const sketch = function(p){
    p.setup = function(){
      // create graphics based on current layout
      setupCanvases();
      // initialize values and draw once
      sliderH.value = initial.h;
      sliderS.value = initial.s;
      sliderB.value = initial.b;
      updateValueDisplays();
      drawHueBar(); // static spectrum
      updateVisuals(); // dynamic bars + arrows

      // listeners
      sliderH.addEventListener('input', onHueChange);
      sliderS.addEventListener('input', onSatChange);
      sliderB.addEventListener('input', onBriChange);
      btnReset.addEventListener('click', resetAll);
      window.addEventListener('resize', onResize);
    };

    // Build four graphics canvases sized to wrapper width
    function setupCanvases(){
      // clear any old canvases from parents
      [parentHue, parentSat, parentBri, parentRes].forEach(el => el.innerHTML = '');

      // width fits parent (bar-wrapper's content box width)
      const availW = parentHue.getBoundingClientRect().width || 600;
      const canvasW = Math.max(320, Math.round(availW)); // guard min size
      const canvasH = barH + innerPad*2;

      gHue = p.createGraphics(canvasW, canvasH);
      gSat = p.createGraphics(canvasW, canvasH);
      gBri = p.createGraphics(canvasW, canvasH);
      gRes = p.createGraphics(canvasW, canvasH);

      // p5 color mode per buffer
      [gHue,gSat,gBri,gRes].forEach(g => {
        g.colorMode(p.HSB, 1,1,1,1);
        g.pixelDensity(1); // consistent sharpness
        g.noLoop && g.noLoop();
      });

      // attach each canvas to its parent
      parentHue.appendChild(gHue.canvas);
      parentSat.appendChild(gSat.canvas);
      parentBri.appendChild(gBri.canvas);
      parentRes.appendChild(gRes.canvas);

      // ensure canvases scale to parent width via CSS
      [gHue.canvas, gSat.canvas, gBri.canvas, gRes.canvas].forEach(c=>{
        c.style.width = '100%';
        c.style.height = 'auto';
      });

      // position arrows vertically just below the bar
      positionArrowsVertically();
    }

    function positionArrowsVertically(){
      const arrowTop = innerPad + barH + arrowOffset;
      [arrowHue, arrowSat, arrowBri].forEach(a => {
        a.style.top = arrowTop + 'px';
      });
    }

    function roundedClip(g){
      const x = innerPad, y = innerPad, w = g.width - innerPad*2, h = barH;
      const ctx = g.drawingContext;
      ctx.save();
      ctx.beginPath();
      if (typeof ctx.roundRect === 'function'){
        ctx.roundRect(x, y, w, h, radius);
      }else{
        // Fallback rounded rect path
        const r = radius;
        ctx.moveTo(x+r, y);
        ctx.arcTo(x+w, y, x+w, y+h, r);
        ctx.arcTo(x+w, y+h, x, y+h, r);
        ctx.arcTo(x, y+h, x, y, r);
        ctx.arcTo(x, y, x+w, y, r);
        ctx.closePath();
      }
      ctx.clip();
      return {x,y,w,h};
    }

    function strokeBorder(g){
      g.push();
      g.noFill();
      g.stroke(0, 0, 0, 0.25); // in HSB mode: black with some alpha
      g.strokeWeight(1);
      g.rect(innerPad, innerPad, g.width - innerPad*2, barH, radius);
      g.pop();
    }

    // Static hue spectrum bar (draw once or on resize)
    function drawHueBar(){
      gHue.clear();
      const {x, y, w, h} = roundedClip(gHue);
      for(let i=0;i<w;i++){
        const t = i/(w-1);
        gHue.stroke(p.color(t, 1, 1, 1));
        gHue.line(x+i, y, x+i, y+h);
      }
      gHue.drawingContext.restore();
      strokeBorder(gHue);
    }

    // Dynamic saturation bar (depends on h,b)
    function drawSaturationBar(hue, bri){
      gSat.clear();
      const {x, y, w, h} = roundedClip(gSat);
      for(let i=0;i<w;i++){
        const t = i/(w-1);
        gSat.stroke(p.color(hue, t, bri, 1));
        gSat.line(x+i, y, x+i, y+h);
      }
      gSat.drawingContext.restore();
      strokeBorder(gSat);
    }

    // Dynamic brightness bar (depends on h,s)
    function drawBrightnessBar(hue, sat){
      gBri.clear();
      const {x, y, w, h} = roundedClip(gBri);
      for(let i=0;i<w;i++){
        const t = i/(w-1);
        gBri.stroke(p.color(hue, sat, t, 1));
        gBri.line(x+i, y, x+i, y+h);
      }
      gBri.drawingContext.restore();
      strokeBorder(gBri);
    }

    // Solid result bar
    function drawResultBar(hue, sat, bri){
      gRes.clear();
      // clip then fill
      const {x, y, w, h} = roundedClip(gRes);
      gRes.noStroke();
      gRes.fill(p.color(hue, sat, bri, 1));
      gRes.rect(x, y, w, h, radius);
      gRes.drawingContext.restore();
      strokeBorder(gRes);
    }

    function getCurrentVals(){
      return {
        h: parseFloat(sliderH.value),
        s: parseFloat(sliderS.value),
        b: parseFloat(sliderB.value)
      };
    }

    function updateValueDisplays(){
      valueH.textContent = fmt(sliderH.value);
      valueS.textContent = fmt(sliderS.value);
      valueB.textContent = fmt(sliderB.value);
    }

    function positionArrowsHorizontally(){
      const barW = gHue.width - innerPad*2; // same for all
      const leftOffset = innerPad;

      const setArrow = (el, t) => {
        const aw = el.getBoundingClientRect().width || 10;
        const x = leftOffset + t*barW - aw/2;
        el.style.left = Math.round(x) + 'px';
      };

      const v = getCurrentVals();
      setArrow(arrowHue, v.h);
      setArrow(arrowSat, v.s);
      setArrow(arrowBri, v.b);
    }

    // Main redraw function
    function updateVisuals(){
      const v = getCurrentVals();
      updateValueDisplays();

      // redraw dependent bars and result
      drawSaturationBar(v.h, v.b);
      drawBrightnessBar(v.h, v.s);
      drawResultBar(v.h, v.s, v.b);

      // reposition arrows
      positionArrowsHorizontally();
    }

    // Handlers
    function onHueChange(){
      // Hue bar is static; only update dependent bars and arrow
      updateVisuals();
    }
    function onSatChange(){ updateVisuals(); }
    function onBriChange(){ updateVisuals(); }

    function resetAll(){
      sliderH.value = initial.h;
      sliderS.value = initial.s;
      sliderB.value = initial.b;
      updateVisuals();
    }

    function onResize(){
      // recreate canvases to match new width
      setupCanvases();
      drawHueBar();
      updateVisuals();
    }

    // Expose update for potential external call (not required)
    p.updateVisuals = updateVisuals;
  };

  // Create a single p5 instance to manage the four graphics canvases
  p5inst = new p5(sketch);
})();
</script>
</body>
</html>
