<body>
  <div class="main-container">
    <!-- Control Panel Section -->
    <div id="controls-container">
      <div class="control-row">
        <label for="slider-alpha">α</label>
        <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05" />
        <span id="value-alpha" class="value-span">0.05</span>
      </div>
      <div class="control-row">
        <label for="slider-beta">β</label>
        <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5" />
        <span id="value-beta" class="value-span">5</span>
      </div>
      <div class="control-row">
        <label for="slider-gamma">γ</label>
        <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500" />
        <span id="value-gamma" class="value-span">500</span>
      </div>
    </div>

    <!-- Plot Area Section -->
    <div id="plot-container"></div>
  </div>

  <script>
    // Grab elements
    const alphaSlider = document.getElementById('slider-alpha');
    const betaSlider  = document.getElementById('slider-beta');
    const gammaSlider = document.getElementById('slider-gamma');

    const alphaValue = document.getElementById('value-alpha');
    const betaValue  = document.getElementById('value-beta');
    const gammaValue = document.getElementById('value-gamma');

    // Format helpers
    const fmtAlpha = v => Number(v).toFixed(4);
    const fmtBeta  = v => Number(v).toFixed(2);
    const fmtGamma = v => String(Math.round(Number(v)));

    // ODE system derivatives
    function derivatives(t, y, alpha, beta, gamma) {
      const dT = y[0];
      const Cx = y[1];
      const expo = Math.exp(dT / (1 + dT / alpha));
      const dTdt = gamma * Cx * expo - beta * dT;
      const dCxdt = 1 - Cx * expo;
      return [dTdt, dCxdt];
    }

    // 4th-order Runge-Kutta solver for the 2D system
    function solveSystem(alpha, beta, gamma, t0 = 0, tEnd = 20, h = 0.05) {
      const nSteps = Math.round((tEnd - t0) / h);
      const tArr = new Array(nSteps + 1);
      const TArr = new Array(nSteps + 1);
      const CArr = new Array(nSteps + 1);

      let t = t0;
      let y0 = [0, 0]; // ΔT(0) = 0, Cx(0) = 0

      for (let i = 0; i <= nSteps; i++) {
        tArr[i] = t;
        TArr[i] = y0[0];
        CArr[i] = y0[1];

        if (i === nSteps) break;

        const k1 = derivatives(t, y0, alpha, beta, gamma);
        const yk2 = [ y0[0] + 0.5 * h * k1[0], y0[1] + 0.5 * h * k1[1] ];
        const k2 = derivatives(t + 0.5 * h, yk2, alpha, beta, gamma);
        const yk3 = [ y0[0] + 0.5 * h * k2[0], y0[1] + 0.5 * h * k2[1] ];
        const k3 = derivatives(t + 0.5 * h, yk3, alpha, beta, gamma);
        const yk4 = [ y0[0] + h * k3[0], y0[1] + h * k3[1] ];
        const k4 = derivatives(t + h, yk4, alpha, beta, gamma);

        y0 = [
          y0[0] + (h / 6) * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]),
          y0[1] + (h / 6) * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]),
        ];
        t += h;
      }

      // Scale Cx by 1000 for plotting
      const CScaled = CArr.map(v => 1000 * v);
      return { t: tArr, T: TArr, Cscaled: CScaled };
    }

    // Plot using Plotly
    function drawPlot(data) {
      const traceT = {
        x: data.t,
        y: data.T,
        mode: 'lines',
        line: { color: 'orange', width: 2 },
        name: 'ΔT'
      };
      const traceC = {
        x: data.t,
        y: data.Cscaled,
        mode: 'lines',
        line: { color: 'steelblue', width: 2 },
        name: '1000 Cx'
      };

      const layout = {
        xaxis: { title: '', range: [0, 20], zeroline: true },
        yaxis: { title: '', range: [0, 100], zeroline: true },
        showlegend: false,
        margin: { l: 50, r: 20, b: 30, t: 20 },
        plot_bgcolor: '#f0f0f0',
        paper_bgcolor: 'white',
        annotations: [
          {
            text: 'ΔT',
            x: -0.05, y: 100,
            xref: 'paper', yref: 'y',
            xanchor: 'right', yanchor: 'top',
            showarrow: false,
            font: { color: 'purple' }
          },
          {
            text: '1000 C<sub>X</sub>',
            x: -0.05, y: 95,
            xref: 'paper', yref: 'y',
            xanchor: 'right', yanchor: 'top',
            showarrow: false,
            font: { color: 'purple' }
          }
        ]
      };
      const config = { displaylogo: false, responsive: true };

      Plotly.react('plot-container', [traceT, traceC], layout, config);
    }

    // Update values and re-draw plot
    function update() {
      const alpha = parseFloat(alphaSlider.value);
      const beta  = parseFloat(betaSlider.value);
      const gamma = parseFloat(gammaSlider.value);

      alphaValue.textContent = fmtAlpha(alpha);
      betaValue.textContent  = fmtBeta(beta);
      gammaValue.textContent = fmtGamma(gamma);

      const data = solveSystem(alpha, beta, gamma);
      drawPlot(data);
    }

    // Initialize
    window.addEventListener('DOMContentLoaded', () => {
      // Set initial display values
      alphaValue.textContent = fmtAlpha(alphaSlider.value);
      betaValue.textContent  = fmtBeta(betaSlider.value);
      gammaValue.textContent = fmtGamma(gammaSlider.value);
      update();
    });

    // Slider interactions - real-time updates
    [alphaSlider, betaSlider, gammaSlider].forEach(slider => {
      slider.addEventListener('input', update);
    });

    // Keep plot responsive on window resize
    window.addEventListener('resize', () => {
      Plotly.Plots.resize('plot-container');
