<script>
  // Slider elements
  const sliderTime = document.getElementById('slider-time');
  const sliderWavelength = document.getElementById('slider-wavelength');
  const sliderLongAmp = document.getElementById('slider-longitudinal-amplitude');
  const sliderLatAmp = document.getElementById('slider-latitudinal-amplitude');

  // Value spans
  const valueTime = document.getElementById('value-time');
  const valueWavelength = document.getElementById('value-wavelength');
  const valueLongAmp = document.getElementById('value-longitudinal-amplitude');
  const valueLatAmp = document.getElementById('value-latitudinal-amplitude');

  // Helper to format numbers to 2 decimals
  function fmt(v){ return Number(v).toFixed(2); }

  function syncValueDisplays(){
    valueTime.textContent = fmt(sliderTime.value);
    valueWavelength.textContent = fmt(sliderWavelength.value);
    valueLongAmp.textContent = fmt(sliderLongAmp.value);
    valueLatAmp.textContent = fmt(sliderLatAmp.value);
  }

  sliderWavelength.addEventListener('input', syncValueDisplays);
  sliderLongAmp.addEventListener('input', syncValueDisplays);
  sliderLatAmp.addEventListener('input', syncValueDisplays);

  // Time slider interaction: drag pauses animation and sets time
  let userControllingTime = false;
  sliderTime.addEventListener('pointerdown', () => { userControllingTime = true; });
  sliderTime.addEventListener('input', () => {
    // While dragging, the visual time is governed by the slider
    time = parseFloat(sliderTime.value);
    syncValueDisplays();
  });
  function releaseTimeControl(){
    if(userControllingTime){
      // Resume animation from the slider-set time
      time = parseFloat(sliderTime.value);
      userControllingTime = false;
    }
  }
  window.addEventListener('pointerup', releaseTimeControl);
  window.addEventListener('pointercancel', releaseTimeControl);

  // Initialize displays
  syncValueDisplays();

  // p5.js sketch
  let time = parseFloat(sliderTime.value);
  let canvas;
  const gridCols = 10;
  const gridRows = 6;
  const eqPoints = []; // equilibrium positions {x0,y0,row,col}
  let padX = 40, padY = 40;
  const dt = 0.03;

  // Build grid points based on canvas size
  function computeGrid(w,h){
    eqPoints.length = 0;
    const stepX = (w - 2*padX) / (gridCols - 1);
    const stepY = (h - 2*padY) / (gridRows - 1);
    for(let r=0;r<gridRows;r++){
      for(let c=0;c<gridCols;c++){
        const x0 = padX + c*stepX;
        const y0 = padY + r*stepY;
        eqPoints.push({x0,y0,row:r,col:c});
      }
    }
  }

  // p5 instance mode is not strictly necessary; simple global mode:
  function setup(){
    canvas = createCanvas(700, 450);
    canvas.parent('canvas-container');
    pixelDensity(1);
    computeGrid(width,height);
    noLoop(); // We'll manually loop immediately after to ensure setup complete, then redraw each frame
    loop();
  }

  function windowResized(){
    // Keep fixed logical size; visual is responsive via CSS only.
  }

  function draw(){
    // Update animated time unless the user is dragging the time slider
    if(!userControllingTime){
      time += dt;
      sliderTime.value = time; // Keep slider synced to animated time
      valueTime.textContent = fmt(time);
    }

    // Read current slider values
    const wavelength = parseFloat(sliderWavelength.value);
    const longAmp = parseFloat(sliderLongAmp.value);
    const latAmp = parseFloat(sliderLatAmp.value);

    // Clear canvas
    background('#E0F0FF');

    // For phase calculation, scale x so wavelength slider 1..8 gives pleasant cycles across width.
    // Using xScaled = x0 / (width/8) keeps about (8/λ) wavelengths across the canvas.
    const scaleX = width / 8;
    const k = TWO_PI / wavelength;

    // Precompute current positions in a 2D array [row][col]
    const positions = Array.from({length:gridRows}, () => Array(gridCols).fill(null));

    // Drawing styles for ellipses (paths)
    noFill();
    stroke('#8F998F');
    strokeWeight(2);

    for(const p of eqPoints){
      const r = p.row;
      const c = p.col;
      const x0 = p.x0;
      const y0 = p.y0;

      // Depth-dependent exponential decay (r=0 top)
      const amp_decay = Math.exp(- (r / (gridRows - 1)) * 1.5);

      // Scaled amplitudes (pixels)
      const ampX = longAmp * amp_decay * 20;
      const ampY = latAmp  * amp_decay * 20;

      // Phase of a right-moving wave
      const xScaled = x0 / scaleX;
      const phase = k * xScaled - time;

      // Elliptical path
      ellipse(x0, y0, 2*ampX, 2*ampY);

      // Retrograde particle motion (counter-clockwise for rightward wave)
      const dx = ampX * Math.cos(phase);
      const dy = ampY * Math.sin(phase);
      const x = x0 + dx;
      const y = y0 + dy;

      positions[r][c] = {x, y};
    }

    // Draw dashed connecting lines row-by-row
    stroke('#0000FF');
    strokeWeight(2);
    drawingContext.setLineDash([5,5]);
    for(let r=0;r<gridRows;r++){
      for(let c=0;c<gridCols-1;c++){
        const a = positions[r][c];
        const b = positions[r][c+1];
        line(a.x, a.y, b.x, b.y);
      }
    }
    drawingContext.setLineDash([]);

    // Draw particles
    noStroke();
    fill('#FF0000');
    for(let r=0;r<gridRows;r++){
      for(let c=0;c<gridCols;c++){
        const p = positions[r][c];
        circle(p.x, p.y, 8);
      }
    }
  }
</script>
</body>
</html>
