        <input type="range" id="slider-tref" min="0" max="100" step="1" value="35"/>
        <span id="value-tref">35</span>
      </div>

      <div class="control-row">
        <label for="slider-c">c (°C<sup>-1</sup>)</label>
        <input type="range" id="slider-c" min="0.01" max="0.1" step="0.001" value="0.025"/>
        <span id="value-c">0.025</span>
      </div>

      <div class="control-row">
        <label for="slider-b">b (°C)</label>
        <input type="range" id="slider-b" min="200" max="350" step="0.01" value="273.16"/>
        <span id="value-b">273.16</span>
      </div>

      <div class="control-row">
        <label for="slider-a">a (°C)</label>
        <input type="range" id="slider-a" min="1000" max="10000" step="1" value="4000"/>
        <span id="value-a">4000</span>
      </div>
    </div>
  </div>

  <!-- Right Column: Visualization -->
  <div class="right">
    <div id="plot-top"></div>
    <div id="plot-bottom"></div>
  </div>
</div>

<script>
  // Utility helpers
  function toDecimalsFromStep(stepStr) {
    const s = String(stepStr);
    return s.includes('.') ? s.split('.')[1].length : 0;
  }
  function fmtFixed(v, decimals) {
    return Number(v).toLocaleString(undefined, {minimumFractionDigits: decimals, maximumFractionDigits: decimals});
  }
  function linspace(a, b, n) {
    if (n <= 1) return [a];
    const arr = new Array(n);
    const step = (b - a) / (n - 1);
    for (let i = 0; i < n; i++) arr[i] = a + step * i;
    return arr;
  }
  function minmax(arr) {
    let min = Infinity, max = -Infinity;
    for (const v of arr) { if (v < min) min = v; if (v > max) max = v; }
    return [min, max];
  }

  // Linear regression. If throughOrigin = true, forces intercept = 0.
  // Returns {slope, intercept, r2}
  function linearRegression(x, y, throughOrigin=false) {
    if (x.length !== y.length || x.length === 0) return {slope: NaN, intercept: NaN, r2: NaN};

    const n = x.length;
    let sumx = 0, sumy = 0, sumxx = 0, sumxy = 0;
    for (let i = 0; i < n; i++) {
      sumx += x[i];
      sumy += y[i];
      sumxx += x[i] * x[i];
      sumxy += x[i] * y[i];
    }

    let slope, intercept;
    if (throughOrigin) {
      slope = sumxy / sumxx;
      intercept = 0;
    } else {
      const denom = (n * sumxx - sumx * sumx);
      slope = (n * sumxy - sumx * sumy) / denom;
      intercept = (sumy - slope * sumx) / n;
    }

    // R^2
    let sse = 0, sst = 0;
    const meanY = sumy / n;
    for (let i = 0; i < n; i++) {
      const yhat = slope * x[i] + intercept;
      sse += (y[i] - yhat) * (y[i] - yhat);
      sst += (y[i] - meanY) * (y[i] - meanY);
    }
    const r2 = 1 - (sse / sst);
    return {slope, intercept, r2};
  }

  // DOM references
  const btnArrhenius   = document.getElementById('btn-arrhenius');
  const btnExponential = document.getElementById('btn-exponential');

  const sliderN     = document.getElementById('slider-n');
  const sliderTmin  = document.getElementById('slider-tmin');
  const sliderTmax  = document.getElementById('slider-tmax');

  const sliderTref  = document.getElementById('slider-tref');
  const sliderC     = document.getElementById('slider-c');
  const sliderB     = document.getElementById('slider-b');
  const sliderA     = document.getElementById('slider-a');

  const valueN     = document.getElementById('value-n');
  const valueTmin  = document.getElementById('value-tmin');
  const valueTmax  = document.getElementById('value-tmax');

  const valueTref  = document.getElementById('value-tref');
  const valueC     = document.getElementById('value-c');
  const valueB     = document.getElementById('value-b');
  const valueA     = document.getElementById('value-a');

  // State
  let activeModel = 'Arrhenius'; // "Arrhenius" or "exponential"

  // Initialize span displays
  function updateSpanDisplay(input, span) {
    const decimals = toDecimalsFromStep(input.step || "1");
    span.textContent = fmtFixed(Number(input.value), decimals);
  }
  [ [sliderN, valueN], [sliderTmin, valueTmin], [sliderTmax, valueTmax],
    [sliderTref, valueTref], [sliderC, valueC], [sliderB, valueB], [sliderA, valueA]
  ].forEach(([s, v]) => updateSpanDisplay(s, v));

  // Event handlers
  function setActiveButton(model) {
    activeModel = model;
    if (model === 'Arrhenius') {
      btnArrhenius.classList.add('active-button');
      btnExponential.classList.remove('active-button');
    } else {
      btnExponential.classList.add('active-button');
      btnArrhenius.classList.remove('active-button');
    }
    computeAndPlot();
  }
  btnArrhenius.addEventListener('click', () => setActiveButton('Arrhenius'));
  btnExponential.addEventListener('click', () => setActiveButton('exponential'));

  function enforceTmaxConstraint() {
    const tmin = Number(sliderTmin.value);
    const newMin = Math.min(100, tmin + 1);
    sliderTmax.min = String(newMin);
    if (Number(sliderTmax.value) < newMin) {
      sliderTmax.value = String(newMin);
      updateSpanDisplay(sliderTmax, valueTmax);
    }
  }

  const sliders = [sliderN, sliderTmin, sliderTmax, sliderTref, sliderC, sliderB, sliderA];
  sliders.forEach(s => {
    s.addEventListener('input', () => {
      updateSpanDisplay(s, document.getElementById('value-' + s.id.split('-')[1]));
      if (s === sliderTmin) enforceTmaxConstraint();
      computeAndPlot();
    });
  });

  // Computation and plotting
  function getParams() {
    return {
      n: Number(sliderN.value),
      tmin: Number(sliderTmin.value),
      tmax: Number(sliderTmax.value),
      Tref: Number(sliderTref.value),
      c: Number(sliderC.value),
      b: Number(sliderB.value),
      a: Number(sliderA.value)
    };
  }

  function computeAndPlot() {
    const p = getParams();
    // Temperatures
    const T = linspace(p.tmin, p.tmax, p.n);

    // Generate normalized data
    let y = [];
    if (activeModel === 'Arrhenius') {
      // y = exp(-a/(T+b)) / exp(-a/(Tref + b))
      const denom = Math.exp(-p.a / (p.Tref + p.b));
      for (const t of T) y.push(Math.exp(-p.a / (t + p.b)) / denom);
    } else { // exponential
      for (const t of T) y.push(Math.exp(p.c * (t - p.Tref)));
    }

    // Perform fits and assemble plots
    if (activeModel === 'Arrhenius') {
      // Fit exponential: log(y) = c * (T - Tref)
      const xFit = T.map(t => t - p.Tref);
      const yFit = y.map(v => Math.log(v));
      const reg  = linearRegression(xFit, yFit, true); // through origin
      const cHat = reg.slope;
      const r2   = reg.r2;

      // Top plot curve using fitted exponential model
      const Tfine = linspace(p.tmin, p.tmax, 200);
      const yCurve = Tfine.map(t => Math.exp(cHat * (t - p.Tref)));

      // Bottom linear line
      const [xmin, xmax] = minmax(xFit);
      const xLine = [xmin, xmax];
      const yLine = xLine.map(x => cHat * x);

      drawTopPlot(
        "exponential model fit to Arrhenius data<br>c = " + fmtFixed(cHat, 3) + " &nbsp; R² = " + fmtFixed(r2, 3),
        T, y, Tfine, yCurve, '#d62728'
      );

      drawBottomPlot(
        "linear fit of the exponential model<br>R² = " + fmtFixed(r2, 3),
        {xTitle: "T - T_ref", yTitle: "log(k(T)/k(T_ref))"},
        xFit, yFit, xLine, yLine, '#2ca02c', true
      );
    } else {
      // Fit Arrhenius: log(y) = m * (1/(T+b)) + intercept, where a = -m
      const xFit = T.map(t => 1 / (t + p.b));
      const yFit = y.map(v => Math.log(v));
      const reg  = linearRegression(xFit, yFit, false);
      const aHat = -reg.slope;
      const r2   = reg.r2;

      // Top plot curve using fitted Arrhenius model (normalized)
      const denomHat = Math.exp(-aHat / (p.Tref + p.b));
      const Tfine = linspace(p.tmin, p.tmax, 200);
      const yCurve = Tfine.map(t => Math.exp(-aHat / (t + p.b)) / denomHat);

      // Bottom linear line using fitted slope and intercept
      const [xmin, xmax] = minmax(xFit);
      const xLine = [xmin, xmax];
      const yLine = xLine.map(x => reg.slope * x + reg.intercept);

      drawTopPlot(
        "Arrhenius model fit to exponential data<br>a = " + Number(aHat).toLocaleString(undefined, {maximumFractionDigits: 0}) + ", &nbsp; R² = " + fmtFixed(r2, 3),
        T, y, Tfine, yCurve, '#d62728'
      );

      drawBottomPlot(
        "linear fit of the Arrhenius model<br>R² = " + fmtFixed(r2, 3),
        {xTitle: "1/(T + b)", yTitle: "log(k(T)/k(T_ref))"},
        xFit, yFit, xLine, yLine, '#1f77b4', true
      );
    }
  }

  function drawTopPlot(title, xData, yData, xCurve, yCurve, color) {
    const data = [
      {
        x: xData, y: yData,
        mode: 'markers',
        marker: {color: color, size: 8, opacity: 0.9},
        name: 'data'
      },
      {
        x: xCurve, y: yCurve,
        mode: 'lines',
        line: {color: color, width: 3},
        name: 'fit'
      }
    ];
    const layout = {
      title: {text: title, font: {size: 14}},
      margin: {l: 60, r: 15, t: 50, b: 45},
      xaxis: {title: "T (°C)", zeroline: false},
      yaxis: {title: "k(T)/k(T_ref)", rangemode: 'tozero'},
      showlegend: false
    };
    Plotly.react('plot-top', data, layout, {responsive: true, displayModeBar: false});
  }

  function drawBottomPlot(title, axes, xData, yData, xLine, yLine, color, addAxesZeroLines=true) {
    const data = [
      {
        x: xData, y: yData,
        mode: 'markers',
        marker: {color: color, size: 8, opacity: 0.9},
        name: 'linearized data'
      },
      {
        x: xLine, y: yLine,
        mode: 'lines',
        line: {color: color, width: 3},
        name: 'linear fit'
      }
    ];

    // Determine reasonable axis limits and draw zero reference lines
    const yy = yData.concat(yLine);
    const [ymin, ymax] = minmax(yy);
    const xx = xData.concat(xLine);
    const [xmin, xmax] = minmax(xx);

    const shapes = [];
    if (addAxesZeroLines) {
      shapes.push(
        {type:'line', x0:0, x1:0, y0:ymin, y1:ymax, xref:'x', yref:'y',
         line:{color:'#999', width:1, dash:'dot'}},
        {type:'line', x0:xmin, x1:xmax, y0:0, y1:0, xref:'x', yref:'y',
         line:{color:'#999', width:1, dash:'dot'}}
      );
    }

    const layout = {
      title: {text: title, font: {size: 14}},
      margin: {l: 60, r: 15, t: 50, b: 45},
      xaxis: {title: axes.xTitle},
      yaxis: {title: axes.yTitle},
      shapes,
      showlegend: false
    };
    Plotly.react('plot-bottom', data, layout, {responsive: true, displayModeBar: false});
  }

  // Initial constraint and first render
  enforceTmaxConstraint();
  computeAndPlot();

  window.addEventListener('resize', () => {
    Plotly.Plots.resize('plot-top');
    Plotly.Plots.resize('plot-bottom');
  });
</script>
</body>
