    function firstN(raw, n=80) {
      if (!raw) return '';
      const s = raw.trim();
      return s.length > n ? s.slice(0, n) + '…' : s;
    }

    function shiftChar(c, k) {
      const base = 'a'.charCodeAt(0);
      const code = c.charCodeAt(0) - base;
      const shifted = (code + (k % 26) + 26) % 26;
      return String.fromCharCode(base + shifted);
    }

    function caesarEncrypt(plain, offset) {
      let out = '';
      for (let i = 0; i < plain.length; i++) {
        out += shiftChar(plain[i], offset);
      }
      return out;
    }

    function vigenereEncrypt(plain, keyword) {
      let key = (keyword || '').toLowerCase().replace(/[^a-z]/g, '');
      if (key.length === 0) key = 'a';
      let out = '';
      for (let i = 0; i < plain.length; i++) {
        const k = key.charCodeAt(i % key.length) - 97;
        out += shiftChar(plain[i], k);
      }
      return out;
    }

    function autokeyEncrypt(plain, keyword) {
      let key = (keyword || '').toLowerCase().replace(/[^a-z]/g, '');
      if (key.length === 0) key = 'a';
      // extend key with plaintext
      key = (key + plain).slice(0, plain.length);
      let out = '';
      for (let i = 0; i < plain.length; i++) {
        const k = key.charCodeAt(i) - 97;
        out += shiftChar(plain[i], k);
      }
      return out;
    }

    function letterFreqPercentages(text) {
      const counts = new Array(26).fill(0);
      for (let i = 0; i < text.length; i++) {
        const idx = text.charCodeAt(i) - 97;
        if (idx >= 0 && idx < 26) counts[idx]++;
      }
      const total = text.length || 1; // avoid divide by zero
      const freqs = alphabet.map((ch, i) => ({
        letter: ch,
        pct: (counts[i] / total) * 100
      }));
      // sort descending by frequency
      freqs.sort((a, b) => b.pct - a.pct);
      return {
        x: freqs.map(d => d.letter),
        y: freqs.map(d => +d.pct.toFixed(3))
      };
