<div id="app-container">

  <div id="control-panel">
    <div class="row">
      <label for="slider-lambda">permeability ratio, λ</label>
      <input type="range" id="slider-lambda" min="-8" max="-4" step="0.01" value="-5">
      <span id="value-lambda" class="value-span">0.00001</span>
    </div>
    <div class="row">
      <label for="slider-omega">storativity ratio, ω</label>
      <input type="range" id="slider-omega" min="-3" max="-1" step="0.01" value="-2">
      <span id="value-omega" class="value-span">0.01</span>
    </div>
    <div class="row toggle-group">
      <label>option</label>
      <button id="btn-pressure-drop" class="active">pressure drop</button>
      <button id="btn-difference">difference</button>
    </div>
  </div>

  <div id="plot-container"></div>

</div>

<script>
/* -------------------- Utilities -------------------- */
const GAMMA_EULER = 0.577215664901532860606512090082402431;
const LN2 = Math.log(2);

function pow10(x){ return Math.pow(10, x); }

function formatParamValue(v) {
  if (v < 1e-4) return v.toExponential(6);
  // show up to ~7 significant digits without trailing zeros
  return Number(v.toPrecision(7)).toString();
}

function logspace(baseStart, baseEnd, n) {
  const out = new Array(n);
  const logStart = Math.log10(baseStart);
  const logEnd   = Math.log10(baseEnd);
  const step = (logEnd - logStart) / (n - 1);
  for (let i = 0; i < n; i++) {
    out[i] = Math.pow(10, logStart + i * step);
  }
  return out;
}

/* -------------------- Modified Bessel I (series for small x) -------------------- */
function besselI0_series(x) {
  // I0(x) = sum_{k=0}^\infty (x^2/4)^k / (k!)^2
  const z = (x*x)/4;
  let term = 1; // k=0
  let sum = term;
  for (let k = 1; k < 100; k++) {
    term *= z / (k*k);
    sum += term;
    if (Math.abs(term) < 1e-16 * Math.abs(sum)) break;
  }
  return sum;
}
function besselI1_series(x) {
  // I1(x) = sum_{k=0}^\infty (x/2)^{2k+1} / (k!(k+1)!)
  const w = x/2;
  let term = w; // k=0: w^(1)/(0!*1!)
  let sum = term;
  for (let k = 1; k < 100; k++) {
    term *= (w*w) / (k*(k+1));
    sum += term;
    if (Math.abs(term) < 1e-16 * Math.abs(sum)) break;
  }
  return sum;
}

/* -------------------- Modified Bessel K0, K1 -------------------- */
/* Small-x expansions (x <= 2) using convergent series with harmonic numbers.
   For K0:
     K0(x) = -ln(x/2) I0(x) - γ + Σ_{k=1..∞} H_k (x^2/4)^k / (k!)^2
   For K1:
     K1(x) = 1/x + x/2 * (ln(x/2) + γ - 1) + Σ_{k=1..∞} (H_k - 1/(k+1)) (x/2)^{2k+1} / (k!(k+1)!)
*/
function besselK0_small(x) {
  if (x < 1e-14) return -Math.log(x/2) - GAMMA_EULER;
  const I0 = besselI0_series(x);
  const z = (x*x)/4;
  let sumH = 0;
  let term = z; // k=1
  let H = 1;    // H_1
  for (let k = 1; k < 200; k++) {
    sumH += H * term;
    // prepare next k
    const kp1 = k + 1;
    H += 1 / kp1;
    term *= z / (kp1*kp1);
    if (Math.abs(term) < 1e-16 * Math.abs(sumH + 1)) break;
  }
  return -Math.log(x/2) * I0 - GAMMA_EULER + sumH;
}

function besselK1_small(x) {
  if (x < 1e-14) return 1/x;
  const w = x/2;
  // main two leading terms
  let sum = 1/x + (x/2) * (Math.log(x/2) + GAMMA_EULER - 1);
  // series Σ_{k=1..∞} (H_k - 1/(k+1)) (w)^{2k+1} / (k!(k+1)!)
  let H = 1; // H_1
  // k=1 initial term:
  let term = Math.pow(w, 3) / (1 * 2); // w^(2*1+1)/(1!*2!)
  sum += (H - 1/2) * term;
  for (let k = 2; k < 200; k++) {
    H += 1 / k;
    // update term: multiply by w^2 / ((k)*(k+1)) transitioning from k-1 -> k
    term *= (w*w) / (k*(k+1));
    const add = (H - 1/(k+1)) * term;
    sum += add;
    if (Math.abs(add) < 1e-16 * Math.abs(sum + 1)) break;
  }
  return sum;
}

/* Large-x asymptotic (x > 2) for Kν:
   Kν(x) ≈ sqrt(pi/(2x)) e^{-x} Σ_{m=0..M} [ A_m / (8x)^m ],
   with A_0 = 1 and
   A_m = ( Π_{j=1..m} (4ν^2 - (2j-1)^2) ) / m!
*/
function besselK_asym(nu, x, M=8) {
  const pref = Math.sqrt(Math.PI/(2*x)) * Math.exp(-x);
  let sum = 1;
  let prod = 1; // product over (4ν^2 - (2j-1)^2)
  let fact = 1;
  let pow = 1;  // (8x)^m
  const mu = 4 * nu * nu;
  for (let m = 1; m <= M; m++) {
    const a = (2*m - 1);
    prod *= (mu - a*a);
    fact *= m;
    pow *= (8*x);
    sum += (prod / (fact * pow));
  }
  return pref * sum;
}

function besselK0(x) {
  if (x <= 2) return besselK0_small(x);
  return besselK_asym(0, x, 10);
}
function besselK1(x) {
  if (x <= 2) return besselK1_small(x);
  return besselK_asym(1, x, 10);
}

/* -------------------- Stehfest algorithm -------------------- */
function stehfestCoefficients(N) {
  // N must be even
  const V = new Array(N+1).fill(0);
  const N2 = N / 2;
  const fact = (n) => {
    let r = 1;
    for (let i = 2; i <= n; i++) r *= i;
    return r;
  };
  for (let k = 1; k <= N; k++) {
    let sum = 0;
    const jmin = Math.floor((k + 1) / 2);
    const jmax = Math.min(k, N2);
    for (let j = jmin; j <= jmax; j++) {
      const num = Math.pow(j, N2) * fact(2*j);
      const den = fact(N2 - j) * fact(j) * fact(j - 1) * fact(k - j) * fact(2*j - k);
      sum += num / den;
    }
    V[k] = sum * ( ( (N2 + k) % 2 === 0 ) ? 1 : -1 );
  }
  return V;
}

function stehfest(laplaceFunc, t, N=12) {
  const V = stehfestCoefficients(N);
  let sum = 0;
  for (let k = 1; k <= N; k++) {
    const s = k * LN2 / t;
    const F = laplaceFunc(s);
    sum += V[k] * F;
  }
  return (LN2 / t) * sum;
}

/* -------------------- Core model: ΔP(τ; λ, ω) -------------------- */
function calculatePressureDrop(tau, lambda, omega) {
  function laplaceF(s) {
    // f(s) per plan
    const denom = (1 - omega) * s + lambda;
    const f = (omega * (1 - omega) * s + lambda) / denom;
    let g = Math.sqrt(s * f);
    // Protect against numerical issues
    if (!isFinite(g) || g <= 0) g = 1e-30;
    const K0 = besselK0(g);
    const K1 = besselK1(g);
    const Pbar = K0 / (g * K1);
    return Pbar / s;
  }
  const N = 12; // number of terms in Stehfest
  return stehfest(laplaceF, tau, N);
}

/* -------------------- Plotting -------------------- */
const tauArray = logspace(1, 1e10, 200);

function buildTrace(y) {
  return {
    x: tauArray,
    y: y,
    mode: 'lines',
    line: { color: 'red', width: 2 },
    hovertemplate: 'τ=%{x:.3e}<br>y=%{y:.6f}<extra></extra>'
  };
}

function getLayout(option) {
  const common = {
    xaxis: { title: 'dimensionless time, τ', type: 'log', range: [0, 10], dtick: 2, gridcolor: '#ddd' },
    margin: { l: 80, r: 50, b: 60, t: 60 },
    paper_bgcolor: '#fff',
    plot_bgcolor: '#fff'
  };
  if (option === 'difference') {
    return Object.assign({}, common, {
      title: 'deviation from the asymptote',
      yaxis: { title: 'dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)', range: [0, 3.5], gridcolor: '#ddd' }
    });
  } else {
    return Object.assign({}, common, {
      title: 'pressure drawdown at the wellbore',
      yaxis: { title: 'dimensionless pressure drop, ΔP', range: [0, 12], gridcolor: '#ddd' }
    });
  }
}

function computeSeries(lambda, omega, option) {
  const y = new Array(tauArray.length);
  for (let i = 0; i < tauArray.length; i++) {
    const tau = tauArray[i];
    const dP = calculatePressureDrop(tau, lambda, omega);
    if (option === 'difference') {
      y[i] = dP - 0.5 * (Math.log(tau) + 0.8091);
    } else {
      y[i] = dP;
    }
  }
  return y;
}

/* -------------------- UI and interactions -------------------- */
const elSliderLambda = document.getElementById('slider-lambda');
const elSliderOmega = document.getElementById('slider-omega');
const elValueLambda  = document.getElementById('value-lambda');
const elValueOmega   = document.getElementById('value-omega');
const btnPressure    = document.getElementById('btn-pressure-drop');
