    function drawDashedLine(x1, y1, x2, y2, dash = 5, gap = 5) {
      const dx = x2 - x1;
      const dy = y2 - y1;
      const dist = Math.hypot(dx, dy);
      const steps = Math.floor(dist / (dash + gap));
      const ux = dx / dist;
      const uy = dy / dist;

      for (let i = 0; i < steps; i++) {
        const xStart = x1 + (dash + gap) * i * ux;
        const yStart = y1 + (dash + gap) * i * uy;
        const xEnd = xStart + dash * ux;
        const yEnd = yStart + dash * uy;
