      const learnRateP = 1e-5;
      const learnRateU = 1e-5;
      const maxIter = 2000;
      const eps = 1e-6;

      function sse(p, u) {
        let sum = 0;
        for (const d of dataPoints) {
          const f = Math.max(d.age - u, 1e-6);
          const m = p * Math.pow(f, 4/5);
          const e = d.volume - m;
          sum += e * e;
        }
        return sum;
      }

      let prevE = sse(p, u);
      const minAge = Math.min(...dataPoints.map(d => d.age));
      for (let it = 0; it < maxIter; it++) {
        let dEdp = 0;
        let dEdu = 0;

        for (const d of dataPoints) {
          const f = Math.max(d.age - u, 1e-6);
          const f48 = Math.pow(f, 4/5);
          const model = p * f48;
          const e = d.volume - model;
          // dE/dp = -2 * sum(e * f^{4/5})
          dEdp += -2 * e * f48;
          // dE/du = 2 * sum(e * p * (4/5) * f^{-1/5})
          const fneg = Math.pow(f, -1/5);
          dEdu += 2 * e * p * (4/5) * fneg;
        }

        // Update
        p -= learnRateP * dEdp;
        u -= learnRateU * dEdu;

        // Constrain u
        if (u >= minAge) u = minAge - 1e-3;

        // Check convergence
        const E = sse(p, u);
        if (Math.abs(prevE - E) < eps) break;
        prevE = E;
      }

      pEstimate = p;
      uEstimate = u;

      updateUI();
      redraw();
    }

    // Interaction handlers
    function mousePressed() {
      // Only act if inside canvas and inside plot area
      if (!isMouseInCanvas()) return;
      if (!inPlotArea(mouseX, mouseY)) return;

      // Check if near an existing point
      const idx = hitTestPoint(mouseX, mouseY);
      if (idx !== -1) {
        dragIndex = idx;
      } else {
        // Add a new point at clicked location
        const { age, volume } = screenToData(mouseX, mouseY);
        const clamped = {
          age: clamp(age, xMin, xMax),
          volume: clamp(volume, yMin, yMax)
        };
        dataPoints.push(clamped);
        recalcFit();
      }
    }

    function mouseDragged() {
      if (dragIndex === -1) return;
      if (!isMouseInCanvas()) return;

      // Update point during drag
      let mx = mouseX;
      let my = mouseY;
      // Clamp to plot area
      mx = clamp(mx, margin.left, width - margin.right);
      my = clamp(my, margin.top, height - margin.bottom);

      const { age, volume } = screenToData(mx, my);
      dataPoints[dragIndex].age = clamp(age, xMin, xMax);
      dataPoints[dragIndex].volume = clamp(volume, yMin, yMax);

      // Live update drawing + fitting (for dynamic change)
      recalcFit();
    }

    function mouseReleased() {
      if (dragIndex !== -1) {
        dragIndex = -1;
        recalcFit();
      }
    }

    function doubleClicked() {
      if (!isMouseInCanvas()) return;
      const idx = hitTestPoint(mouseX, mouseY);
      if (idx !== -1) {
        dataPoints.splice(idx, 1);
        dragIndex = -1;
        recalcFit();
      }
    }

    function hitTestPoint(mx, my) {
      // Return the index of a point under the mouse, or -1
      let closest = -1;
      let minD = Infinity;
      dataPoints.forEach((pt, idx) => {
        const { x, y } = dataToScreen(pt.age, pt.volume);
        const d = dist(mx, my, x, y);
        if (d <= pointPixelRadius + 4 && d < minD) {
          closest = idx;
          minD = d;
        }
      });
      return closest;
    }

    function isMouseInCanvas() {
      // p5 keeps mouseX/Y as NaN if outside; also we ensure 0..width/height
      return mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height;
    }

    function clamp(v, a, b) {
      return Math.min(Math.max(v, a), b);
    }

    // Initial fit after everything is ready
    // Small timeout ensures spans are present before first update
    window.addEventListener('load', () => {
      // Use initial guesses from plan, then refine
      pEstimate = 58.2729;
      uEstimate = 22.7319;
      updateUI();
      // Fit to make the curve consistent with the points
      recalcFit();
    });
  </script>
