          const a = poly[i];
          const b = poly[(i + 1) % poly.length];
          segs.push({ a: { x: a.x, y: a.y }, b: { x: b.x, y: b.y } });
        }
      };
      addEdges(geom.outer);
      geom.holes.forEach(addEdges);
      return segs;
    }

    function sortVerticesByAngle(verts, S) {
      return verts
        .map(v => {
          const ang = Math.atan2(v.y - S.y, v.x - S.x);
          return { x: v.x, y: v.y, angle: ang };
        })
        .sort((a, b) => a.angle - b.angle);
    }

    function computeRayIntersections(S, sortedVerts, segs) {
      const points = [];
      const hitEdgeIndex = [];

      for (let i = 0; i < sortedVerts.length; i++) {
        const v = sortedVerts[i];
        const dx = v.x - S.x;
        const dy = v.y - S.y;
        const dir = { x: dx, y: dy };
        let bestT = Infinity;
        let bestPoint = null;
        let bestIndex = null;

        for (let ei = 0; ei < segs.length; ei++) {
          const e = segs[ei];
          const res = intersectRaySegment(S, dir, e.a, e.b);
          if (!res || !isFinite(res.t)) continue;
          if (res.t >= 0 && res.t < bestT && res.u >= -EPS && res.u <= 1 + EPS) {
            bestT = res.t;
            bestPoint = res.point;
            bestIndex = ei;
          }
        }
        if (!bestPoint) {
          // Fallback: extend far in the direction
          bestPoint = { x: S.x + dir.x * 1000, y: S.y + dir.y * 1000 };
          bestIndex = null;
        }
        points.push(bestPoint);
        hitEdgeIndex.push(bestIndex);
      }
      return { points, hitEdgeIndex };
    }

    // Intersection of ray S + t*dir (t>=0) with segment AB
    function intersectRaySegment(S, dir, A, B) {
      const v = { x: B.x - A.x, y: B.y - A.y };
      const den = cross(dir, v);
      if (Math.abs(den) < EPS) return null; // parallel
      const AS = { x: A.x - S.x, y: A.y - S.y };
      const t = cross(AS, v) / den;
      const u = cross(AS, dir) / den;
      if (t < -EPS) return null; // intersection behind ray start
      const P = { x: S.x + t * dir.x, y: S.y + t * dir.y };
      return { point: P, t, u };
    }

    function cross(a, b) {
      return a.x * b.y - a.y * b.x;
    }

    // ====== Spatial Queries ======
    function pointInFreeSpace(pt, geom) {
      return pointInPolygon(pt, geom.outer) && geom.holes.every(h => !pointInPolygon(pt, h));
    }

    // Ray casting point-in-polygon (inclusive of boundary)
    function pointInPolygon(pt, poly) {
      let inside = false;
      for (let i = 0, j = poly.length - 1; i < poly.length; j = i++) {
        const xi = poly[i].x, yi = poly[i].y;
        const xj = poly[j].x, yj = poly[j].y;

        const intersect =
          ((yi > pt.y) !== (yj > pt.y)) &&
          (pt.x < (xj - xi) * (pt.y - yi) / ((yj - yi) || 1e-12) + xi);
        if (intersect) inside = !inside;

        // Boundary check (distance to segment)
        const d = distPointToSegment(pt, poly[j], poly[i]);
        if (d < 1e-6) return true;
      }
      return inside;
    }

    function distPointToSegment(P, A, B) {
      const vx = B.x - A.x, vy = B.y - A.y;
      const wx = P.x - A.x, wy = P.y - A.y;
      const c1 = vx * wx + vy * wy;
      if (c1 <= 0) return Math.hypot(P.x - A.x, P.y - A.y);
      const c2 = vx * vx + vy * vy;
      if (c2 <= c1) return Math.hypot(P.x - B.x, P.y - B.y);
      const t = c1 / c2;
      const proj = { x: A.x + t * vx, y: A.y + t * vy };
      return Math.hypot(P.x - proj.x, P.y - proj.y);
    }

    // ====== Drawing Routines ======
    function drawEnvironment(p) {
      p.push();
      p.noStroke();
      p.fill(255);

      p.stroke('#AAAAAA');
      p.strokeWeight(1);
      p.beginShape();
      // Outer boundary (given clockwise order is fine)
      geometry.outer.forEach(v => p.vertex(v.x, v.y));

      // Holes as contours (reverse order)
      geometry.holes.forEach(hole => {
        p.beginContour();
        for (let i = hole.length - 1; i >= 0; i--) {
          p.vertex(hole[i].x, hole[i].y);
        }
        p.endContour();
      });
      p.endShape(p.CLOSE);
      p.pop();
    }

    function drawVertices(p, verts) {
      p.push();
      p.noStroke();
      p.fill('#ff0000');
      verts.forEach(v => {
        p.circle(v.x, v.y, 4);
      });
      p.pop();
    }

    function drawSource(p, S) {
      p.push();
      p.noFill();
      p.stroke('#0057ff');
      p.strokeWeight(1.5);
      p.circle(S.x, S.y, 12); // thin outline
      p.noStroke();
      p.fill('#0057ff');
      p.circle(S.x, S.y, 8); // filled
      p.pop();
    }

    function drawVisibilityFinal(p, pts) {
      p.push();
      p.fill('#ffcc66');
      p.stroke('rgba(170,170,170,0.5)');
      p.strokeWeight(1);
      p.beginShape();
      pts.forEach(pt => p.vertex(pt.x, pt.y));
      p.endShape(p.CLOSE);
      p.pop();
    }

    function drawVisibilityPartial(p, pts, N) {
      const K = Math.max(0, N - 1);

      // Committed fan (light yellow)
      if (K >= 1) {
        p.push();
        p.fill('#ffff99');
        p.stroke('rgba(170,170,170,0.5)');
        p.strokeWeight(1);
        p.beginShape();
        p.vertex(source.x, source.y);
        for (let i = 0; i < K; i++) {
          p.vertex(pts[i].x, pts[i].y);
        }
        p.endShape(p.CLOSE);
        p.pop();
      }

      // Active triangle (orange-yellow)
      if (N >= 2 && N <= pts.length) {
        const prev = pts[N - 2];
        const curr = pts[N - 1];
        p.push();
        p.fill('#ffcc66');
        p.noStroke();
        p.triangle(source.x, source.y, prev.x, prev.y, curr.x, curr.y);
        p.pop();
      }
    }

    function drawHelperRay(p, S, vx, vy) {
      p.push();
      p.stroke('#0057ff');
      p.strokeWeight(1);
      p.line(S.x, S.y, vx, vy);
      p.pop();
    }

    function drawSweepArc(p, S, sorted, N) {
      if (sorted.length === 0 || N === 0) return;
      const start = sorted[0].angle;
      const end = sorted[N - 1].angle;
      let s = start;
      let e = end;
      // Ensure e >= s for arc drawing
      if (e < s) e += Math.PI * 2;

      p.push();
      p.noFill();
      p.stroke('#0057ff');
      p.strokeWeight(2);
      // small radius arc around source
      const r = 22;
      p.arc(S.x, S.y, r, r, s, e);
      p.pop();
    }

    function drawActiveEdge(p, edge) {
      p.push();
      p.stroke('#00cc00');
      p.strokeWeight(3);
      p.line(edge.a.x, edge.a.y, edge.b.x, edge.b.y);
      p.pop();
    }
  </script>
</body>
</html>
