    flex: 1;
    min-height: 300px;
  }
</style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <div>
        <label for="slider-order">expansion order</label>
        <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
        <div class="value-row">
          <span>order</span>
          <span id="value-order">5</span>
        </div>
      </div>

      <div>
        <label for="slider-a">f(0)</label>
        <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
        <div class="value-row">
          <span>a</span>
          <span id="value-a">2.0</span>
        </div>
      </div>

      <div>
        <label for="slider-b">f'(0)</label>
        <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
        <div class="value-row">
          <span>b</span>
          <span id="value-b">0.0</span>
        </div>
      </div>
    </div>

    <div id="visualization-area">
      <div id="plot-container"></div>
    </div>
  </div>

<script>
(function() {
  const plotDivId = 'plot-container';
  const xStart = 0;
  const xEnd = 1.5;
  const nPoints = 200;
  const xs = Array.from({length: nPoints}, (_, i) => xStart + (xEnd - xStart) * i / (nPoints - 1));
  const factorial = [1, 1, 2, 6, 24, 120];

  const sliderOrder = document.getElementById('slider-order');
  const sliderA = document.getElementById('slider-a');
  const sliderB = document.getElementById('slider-b');

  const valueOrder = document.getElementById('value-order');
  const valueA = document.getElementById('value-a');
  const valueB = document.getElementById('value-b');

  function fmt(num) {
    // Round to 5 decimals and strip trailing zeros
    let s = Number(num).toFixed(5);
    s = s.replace(/\.?0+$/, '');
    return s;
  }

  function polyToString(coeffs, order) {
    // coeffs are c_k (k from 0..5), show up to 'order'
    let parts = [];
    for (let k = 0; k <= order; k++) {
      const c = coeffs[k];
      if (Math.abs(c) < 1e-12) continue;
      const sign = c >= 0 ? '+' : '-';
      const mag = Math.abs(c);
      let term;
      if (k === 0) {
        term = fmt(mag);
      } else if (k === 1) {
        term = fmt(mag) + ' x';
      } else {
        term = fmt(mag) + ' x^' + k;
      }
      if (parts.length === 0) {
        parts.push((c < 0 ? '- ' : '') + term);
      } else {
        parts.push((sign === '+' ? '+ ' : '- ') + term);
      }
    }
    if (parts.length === 0) parts.push('0');
    return 'f(x) = ' + parts.join(' ');
  }

  function computeDerivativesAt0(a, b) {
    // f_derivs[k] = f^{(k)}(0), k = 0..5
    const f = new Array(6).fill(0);
    f[0] = a;
    f[1] = b;
    f[2] = -(f[1] + 1.5 * f[0]) / f[0];
    f[3] = -(f[2] * f[1] + f[2] + 1.5 * f[1]) / f[0];
    f[4] = -(2 * f[3] * f[1] + f[2] * f[2] + f[3] + 1.5 * f[2]) / f[0];
    f[5] = -(3 * f[4] * f[1] + 4 * f[3] * f[2] + f[4] + 1.5 * f[3]) / f[0];
    return f;
  }

  function coeffsFromDerivs(fDerivs) {
    const c = new Array(6).fill(0);
    for (let k = 0; k <= 5; k++) {
      c[k] = fDerivs[k] / factorial[k];
    }
    return c;
  }

  function seriesEvaluate(coeffs, order, x) {
    // Evaluate sum_{k=0}^{order} c_k x^k
    let sum = 0;
    for (let k = order; k >= 0; k--) {
      sum = sum * x + coeffs[k];
    }
    return sum;
  }

  function getSeriesCurve(coeffs, order) {
    const ys = xs.map(x => seriesEvaluate(coeffs, order, x));
    return ys;
  }

  function rk4Solve(a, b) {
    const ys = new Array(nPoints);
    // y1 = f(x), y2 = f'(x)
    let y1 = a;
    let y2 = b;
    ys[0] = y1;
    const h = (xEnd - xStart) / (nPoints - 1);

    function denomSafe(v) {
      if (Math.abs(v) < 1e-6) return v >= 0 ? 1e-6 : -1e-6;
      return v;
    }
    function dy2(x, y1_, y2_) {
      const d = denomSafe(y1_);
      return -(y2_ + 1.5 * y1_) / d;
    }

    for (let i = 1; i < nPoints; i++) {
      const x = xs[i - 1];

      const k1_1 = y2;
      const k1_2 = dy2(x, y1, y2);

      const k2_1 = y2 + 0.5 * h * k1_2;
      const k2_2 = dy2(x + 0.5 * h, y1 + 0.5 * h * k1_1, y2 + 0.5 * h * k1_2);

      const k3_1 = y2 + 0.5 * h * k2_2;
      const k3_2 = dy2(x + 0.5 * h, y1 + 0.5 * h * k2_1, y2 + 0.5 * h * k2_2);

      const k4_1 = y2 + h * k3_2;
      const k4_2 = dy2(x + h, y1 + h * k3_1, y2 + h * k3_2);

      y1 = y1 + (h / 6) * (k1_1 + 2 * k2_1 + 2 * k3_1 + k4_1);
      y2 = y2 + (h / 6) * (k1_2 + 2 * k2_2 + 2 * k3_2 + k4_2);

      ys[i] = y1;
    }
    return ys;
  }

  function findAgreementLimit(ySeries, yNumerical) {
    const tol = 0.01;
    for (let i = 0; i < xs.length; i++) {
      const diff = Math.abs(ySeries[i] - yNumerical[i]);
      if (diff >= tol) {
        return xs[i];
      }
    }
    return xEnd;
  }

  function update() {
    const order = parseInt(sliderOrder.value, 10);
    const a = parseFloat(sliderA.value);
    const b = parseFloat(sliderB.value);

    valueOrder.textContent = String(order);
    valueA.textContent = a.toFixed(1);
    valueB.textContent = b.toFixed(1);

    const derivs = computeDerivativesAt0(a, b);
    const coeffs = coeffsFromDerivs(derivs);

    const ySeries = getSeriesCurve(coeffs, order);
    const yNumerical = rk4Solve(a, b);
    const xLimit = findAgreementLimit(ySeries, yNumerical);

    const polyTitle = polyToString(coeffs, order);

    const traceRed = {
      type: 'scatter',
      mode: 'lines',
      x: xs,
      y: ySeries,
      line: { color: 'red', width: 2 },
      name: 'Power series'
    };

    const traceGreen = {
      type: 'scatter',
      mode: 'lines',
      x: xs,
      y: yNumerical,
      line: { color: 'green', width: 3 },
      name: 'Numerical (RK4)'
    };

    const layout = {
      title: { text: polyTitle, font: { size: 16 } },
      xaxis: { title: 'x', range: [0, 1.5], zeroline: false, showgrid: true },
      yaxis: { title: 'f(x)', range: [0, 4], zeroline: false, showgrid: true },
      margin: { l: 50, r: 20, t: 50, b: 40 },
      shapes: [
        {
          type: 'rect',
          xref: 'x',
          yref: 'paper',
          x0: 0,
          y0: 0,
          x1: xLimit,
          y1: 1,
          fillcolor: 'lightblue',
          opacity: 0.4,
          layer: 'below',
          line: { width: 0 }
        }
      ],
      showlegend: false
    };

    Plotly.react(plotDivId, [traceRed, traceGreen], layout, {responsive: true});
  }

  // Initialize plot once DOM is ready
  window.addEventListener('load', function() {
    Plotly.newPlot(plotDivId, [{
      x: [0, 0],
      y: [0, 0],
      type: 'scatter'
    }], {
      title: 'Initializing...',
      xaxis: { title: 'x', range: [0, 1.5] },
      yaxis: { title: 'f(x)', range: [0, 4] }
    }, {responsive: true});

    sliderOrder.addEventListener('input', update);
    sliderA.addEventListener('input', update);
    sliderB.addEventListener('input', update);

    update();
  });
})();
</script>
</body>
</html>
