    alpha: false
  });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
  renderer.setClearColor(0xFFFFFF, 1.0);

  let scene = new THREE.Scene();
  let camera = null;
  let clock = new THREE.Clock();

  function sizeRendererToContainer() {
    const {clientWidth, clientHeight} = ui.canvasContainer;
    renderer.setSize(clientWidth, clientHeight, false);
    return { width: clientWidth, height: clientHeight };
  }

  function addLights() {
    const amb = new THREE.AmbientLight(0xffffff, 0.55);
    const dir = new THREE.DirectionalLight(0xffffff, 0.9);
    dir.position.set(8, 18, 14);
    dir.castShadow = false;
    scene.add(amb);
    scene.add(dir);
  }

  function clearScene() {
    while (scene.children.length > 0) {
      const obj = scene.children.pop();
      if (obj.geometry) obj.geometry.dispose?.();
      if (obj.material) {
        if (Array.isArray(obj.material)) obj.material.forEach(m=>m.dispose?.());
        else obj.material.dispose?.();
      }
    }
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xFFFFFF);
    addLights();
  }

  // ======= Data / color utilities =======
  function countFrequencies(text) {
    const counts = {};
    letters.forEach(l => counts[l] = 0);
    const A = 'A'.charCodeAt(0);
    for (const ch of text.toUpperCase()) {
      const code = ch.charCodeAt(0);
      if (code >= A && code < A + 26) {
        const letter = String.fromCharCode(code);
        counts[letter] = (counts[letter] || 0) + 1;
      }
    }
    return counts;
  }

  function roundUpToNearest50(n) {
    if (n <= 0) return 50;
    return Math.ceil(n / 50) * 50;
  }

  // Frequency gradient: blue -> purple -> orange -> red
  const gradStops = [
    {p:0.00, c:new THREE.Color(0x3b82f6)}, // blue
    {p:0.50, c:new THREE.Color(0x7e57c2)}, // purple
    {p:0.75, c:new THREE.Color(0xf39c12)}, // orange
    {p:1.00, c:new THREE.Color(0xe74c3c)}  // red
  ];
  function colorMap01(t) {
    t = Math.min(1, Math.max(0, t));
    let i = 0;
    while (i < gradStops.length-1 && t > gradStops[i+1].p) i++;
    const a = gradStops[i], b = gradStops[i+1];
    const local = (t - a.p) / (b.p - a.p);
    return a.c.clone().lerp(b.c, local);
  }

  function lightenHSL(color, amount=0.25) {
    const c = color.clone();
    const hsl = { h:0, s:0, l:0 };
    c.getHSL(hsl);
    hsl.l = Math.min(1, hsl.l + amount);
    return new THREE.Color().setHSL(hsl.h, hsl.s, hsl.l);
  }

  // ======= Text sprite / label utilities =======
  function makeTextTexture(text, {
    fontSize = 48,
    fontFamily = '600 48px "Inter", system-ui, -apple-system, "Segoe UI", Roboto, Helvetica, Arial',
    color = '#111',
    padding = 12,
    bg = null, // e.g., 'rgba(255,255,255,0.0)'
    stroke = null
  } = {}) {
    const dpr = Math.min(window.devicePixelRatio || 1, 2);
    const ctx = document.createElement('canvas').getContext('2d');
    ctx.font = fontFamily.replace('48', String(fontSize));
    const metrics = ctx.measureText(text);
    const w = Math.ceil(metrics.width + padding*2);
    const h = Math.ceil(fontSize + padding*2);
    ctx.canvas.width = Math.max(2, Math.pow(2, Math.ceil(Math.log2(w*dpr))));
    ctx.canvas.height = Math.max(2, Math.pow(2, Math.ceil(Math.log2(h*dpr))));
    ctx.scale(dpr, dpr);

    // Background
    if (bg) {
      ctx.fillStyle = bg;
      ctx.fillRect(0, 0, w, h);
    }
    ctx.font = fontFamily.replace('48', String(fontSize));
    ctx.textBaseline = 'middle';
    ctx.textAlign = 'left';
    if (stroke) {
      ctx.lineWidth = 4;
      ctx.strokeStyle = stroke;
      ctx.strokeText(text, padding, h/2);
    }
    ctx.fillStyle = color;
    ctx.fillText(text, padding, h/2);

    const tex = new THREE.CanvasTexture(ctx.canvas);
    tex.anisotropy = 8;
    tex.needsUpdate = true;
    return { texture: tex, pxWidth: w, pxHeight: h, canvas: ctx.canvas };
  }

  function makeTextSprite(text, opts = {}) {
    const {texture, pxWidth, pxHeight} = makeTextTexture(text, opts);
    const aspect = pxWidth / pxHeight;
    const height = (opts.spriteHeight || 2.5);
    const material = new THREE.SpriteMaterial({ map: texture, transparent: true });
    const sprite = new THREE.Sprite(material);
    sprite.scale.set(height * aspect, height, 1);
    return sprite;
  }

  function makeCircularLabel(letter, colorHex) {
    const size = 256;
    const dpr = Math.min(window.devicePixelRatio || 1, 2);
    const ctx = document.createElement('canvas').getContext('2d');
    ctx.canvas.width = size * dpr;
    ctx.canvas.height = size * dpr;
    ctx.scale(dpr, dpr);
    const r = size/2 - 4;
    ctx.clearRect(0,0,size,size);

    // circle background
    const bg = lightenHSL(new THREE.Color(colorHex), 0.3).getStyle();
    ctx.fillStyle = bg;
    ctx.beginPath();
    ctx.arc(size/2, size/2, r, 0, Math.PI*2);
    ctx.closePath();
    ctx.fill();

    // subtle rim
    ctx.lineWidth = 6;
    ctx.strokeStyle = 'rgba(0,0,0,0.15)';
    ctx.stroke();

    // letter
    ctx.fillStyle = '#111';
    ctx.font = '700 140px system-ui, -apple-system, "Segoe UI", Roboto, Helvetica, Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(letter, size/2, size/2 + 6);

    const tex = new THREE.CanvasTexture(ctx.canvas);
    tex.needsUpdate = true;
    const mat = new THREE.MeshBasicMaterial({ map: tex, transparent: true });
    const geom = new THREE.CircleGeometry(0.9, 48);
    return new THREE.Mesh(geom, mat);
  }

  // ======= Axes / grid helpers =======
  function add2DAxes({xMin, xMax, yMax, ticks = 4}) {
    const grp = new THREE.Group();
    const mat = new THREE.LineBasicMaterial({ color: 0xA0A0A0 });
    const geom = new THREE.BufferGeometry();

    const verts = [];
    // y-axis line
    verts.push(xMin, 0, 0,  xMin, yMax, 0);

    // baseline x-axis
    verts.push(xMin, 0, 0, xMax, 0, 0);

    // grid/ticks
    const step = yMax / (ticks - 1);
    for (let i = 0; i < ticks; i++) {
      const y = i * step;
      verts.push(xMin, y, 0, xMax, y, 0);
    }

    geom.setAttribute('position', new THREE.Float32BufferAttribute(verts, 3));
    const lines = new THREE.LineSegments(geom, mat);
    grp.add(lines);

    // tick labels
    for (let i = 0; i < ticks; i++) {
      const yVal = Math.round(i * step);
      const label = makeTextSprite(String(yVal), { fontSize: 42, color: '#6b7280', spriteHeight: 1.8 });
      label.position.set(xMin - 1.8, i * step, 0);
      grp.add(label);
    }
    return grp;
  }

  function add3DGridAndYAxis(bounds, yMax, ticks=4) {
    const {minX, maxX, minZ, maxZ} = bounds;
    const xMargin = 1.0;
    const zMargin = 1.0;
    const axisX = minX - xMargin;
    const axisZ = maxZ + zMargin;

    const grp = new THREE.Group();
    const mat = new THREE.LineBasicMaterial({ color: 0x9E9E9E });
    const geom = new THREE.BufferGeometry();
    const verts = [];

    // Y-axis
    verts.push(axisX, 0, axisZ, axisX, yMax, axisZ);

    // Horizontal grid lines across X at each tick (z fixed at axisZ)
    const step = yMax / (ticks - 1);
    for (let i = 0; i < ticks; i++) {
      const y = i * step;
      verts.push(minX - xMargin, y, axisZ, maxX + xMargin, y, axisZ);
    }

    // Add a few floor lines in Z to suggest depth
    const floorLines = 4;
    for (let j = 0; j <= floorLines; j++) {
      const z = maxZ + zMargin - (j * (maxZ - minZ + 2*zMargin) / floorLines);
      verts.push(minX - xMargin, 0, z, maxX + xMargin, 0, z);
    }

    geom.setAttribute('position', new THREE.Float32BufferAttribute(verts, 3));
    const lines = new THREE.LineSegments(geom, mat);
    grp.add(lines);

    // Tick labels
    for (let i = 0; i < ticks; i++) {
      const yVal = Math.round(i * step);
      const label = makeTextSprite(String(yVal), { fontSize: 42, color: '#6b7280', spriteHeight: 1.8 });
      label.position.set(axisX - 1.2, i * step, axisZ);
      grp.add(label);
    }

    return grp;
  }

  // ======= Layouts =======
  function layoutPositions(layoutName) {
    const s = 2.2;   // horizontal key spacing
    const d = 3.0;   // row spacing (Z)
    let rows = [];
    if (layoutName === 'qwerty') {
      rows = ['QWERTYUIOP', 'ASDFGHJKL', 'ZXCVBNM'];
    } else { // dvorak
      rows = ['PYFGCRL', 'AOEUIDHTNS', 'QJKXBMWVZ'];
    }
    const map = {};
    let minX = Infinity, maxX = -Infinity;
    let minZ = Infinity, maxZ = -Infinity;

    rows.forEach((row, r) => {
      const n = row.length;
      const startX = -((n - 1) * s) / 2;
      const z = -(r * d);
      for (let i = 0; i < n; i++) {
        const ch = row[i];
        const x = startX + i * s;
        map[ch] = { x, z, row: r };
        minX = Math.min(minX, x);
        maxX = Math.max(maxX, x);
        minZ = Math.min(minZ, z);
        maxZ = Math.max(maxZ, z);
      }
    });

    // Some layouts may omit a letter in rows - ensure all letters have a position (fallback grid)
    letters.forEach((L, idx) => {
      if (!map[L]) {
        const x = (idx % 10) - 5;
        const z = - (2 + Math.floor(idx / 10)) * d;
        map[L] = { x, z, row: 1 };
        minX = Math.min(minX, x);
        maxX = Math.max(maxX, x);
        minZ = Math.min(minZ, z);
        maxZ = Math.max(maxZ, z);
      }
    });

    return { positions: map, bounds: { minX, maxX, minZ, maxZ } };
  }

  // ======= Draw 2D =======
  function draw2D(freqs) {
    clearScene();

    const values = letters.map(L => freqs[L] || 0);
    const maxVal = Math.max(0, ...values);
    const yMax = roundUpToNearest50(maxVal);
    const {width, height} = sizeRendererToContainer();

    // layout
    const spacing = 1.5;
    const barWidth = 1.0;
    const totalWidth = (letters.length-1) * spacing + barWidth;
    const xLeft = -2.0;  // axis will be slightly left of 0th bar
    const xMin = xLeft - 0.5;
    const xMax = xLeft + totalWidth + 1.5;

    // Ortho camera
    const top = yMax * 1.25;
    const bottom = -yMax * 0.1;
    const left = xMin - 1.0;
    const right = xMax + 1.0;
    camera = new THREE.OrthographicCamera(left, right, top, bottom, -100, 1000);
    camera.position.set(0, 0, 100);
    camera.lookAt(new THREE.Vector3((xMin+xMax)/2, yMax/2, 0));
    scene.add(camera);

    // axes
    const axes = add2DAxes({ xMin, xMax, yMax, ticks: 4 });
    scene.add(axes);

    // bars
    const group = new THREE.Group();
    const barGeom = new THREE.BoxGeometry(barWidth, 1, 0.9);
    const maxFreq = maxVal || 1;

    letters.forEach((L, idx) => {
      const f = freqs[L] || 0;
      const t = f / maxFreq; // normalize for color
      const color = colorMap01(t);
      const mat = new THREE.MeshStandardMaterial({ color, roughness: 0.45, metalness: 0.0 });
      const mesh = new THREE.Mesh(barGeom, mat);
      const heightY = Math.max(0.001, f); // avoid zero-height
      mesh.scale.y = heightY;
      const x = xLeft + idx * spacing;
      mesh.position.set(x, heightY/2, 0);
      mesh.castShadow = false; mesh.receiveShadow = false;

      // letter label above each bar
      const lbl = makeTextSprite(L, { fontSize: 44, color: '#374151', spriteHeight: 1.8 });
      lbl.position.set(x, heightY + (yMax*0.05), 0.01);

      group.add(mesh);
      group.add(lbl);
    });

    scene.add(group);
  }

  // ======= Draw 3D keyboard =======
  function draw3D(freqs, layoutName) {
    clearScene();
    const values = letters.map(L => freqs[L] || 0);
    const maxVal = Math.max(0, ...values);
    const yMax = roundUpToNearest50(maxVal);
    const { positions, bounds } = layoutPositions(layoutName);
    sizeRendererToContainer();

    // Perspective camera
    const spanX = bounds.maxX - bounds.minX;
    const spanZ = bounds.maxZ - bounds.minZ;
    const diag = Math.sqrt(spanX*spanX + spanZ*spanZ);
    camera = new THREE.PerspectiveCamera(38, ui.canvasContainer.clientWidth / ui.canvasContainer.clientHeight, 0.1, 1000);
    camera.position.set(0, yMax * 0.9 + 10, diag * 1.1 + 14);
    camera.lookAt(new THREE.Vector3(0, Math.max(6, yMax * 0.35), (bounds.minZ + bounds.maxZ)/2));
    scene.add(camera);

    // Axes + grid
    const axes = add3DGridAndYAxis(bounds, yMax, 4);
    scene.add(axes);

    // Row colors
    const rowColors = [
      0x9BB53C, // top row: olive/yellow-green
      0x3BC3D6, // home row: blue/cyan
      0x8E44AD  // bottom row: purple
    ];

    const group = new THREE.Group();
    const cylGeom = new THREE.CylinderGeometry(0.9, 0.9, 1, 32);
    const maxFreq = maxVal || 1;
    const minVisible = 0.22;

    letters.forEach(letter => {
      const pos = positions[letter];
      const rowIdx = pos.row;
      const color = new THREE.Color(rowColors[rowIdx]);
      const mat = new THREE.MeshStandardMaterial({ color, roughness: 0.45, metalness: 0.05 });

      const mesh = new THREE.Mesh(cylGeom, mat);
      let h = freqs[letter] || 0;
      h = Math.max(minVisible, h);
      mesh.scale.y = h;           // scale the unit-height cylinder
      mesh.position.set(pos.x, h/2, pos.z);
      mesh.castShadow = false; mesh.receiveShadow = false;

      // circular plane label on top
      const disc = makeCircularLabel(letter, rowColors[rowIdx]);
      disc.rotation.x = -Math.PI / 2;
      disc.position.set(pos.x, h + 0.05, pos.z);

      group.add(mesh);
      group.add(disc);
    });

    scene.add(group);
  }

  // ======= Interaction wiring =======
  function setActive(btn, active) {
    if (active) btn.classList.add('active');
    else btn.classList.remove('active');
  }

  function refresh() {
    const freqs = countFrequencies(state.text);
    if (state.plot === '2d') draw2D(freqs);
    else draw3D(freqs, state.keyboard);
  }

  // Initial UI state and listeners
  ui.selectText.value = state.text;
  ui.selectText.addEventListener('change', () => {
    state.text = ui.selectText.value;
    refresh();
  });

  ui.btnPlot2d.addEventListener('click', () => {
    state.plot = '2d';
    setActive(ui.btnPlot2d, true);
    setActive(ui.btnPlot3d, false);
    refresh();
  });
  ui.btnPlot3d.addEventListener('click', () => {
    state.plot = '3d';
    setActive(ui.btnPlot3d, true);
    setActive(ui.btnPlot2d, false);
    refresh();
  });

  ui.btnKbQwerty.addEventListener('click', () => {
    state.keyboard = 'qwerty';
    setActive(ui.btnKbQwerty, true);
    setActive(ui.btnKbDvorak, false);
    if (state.plot === '3d') refresh();
  });
  ui.btnKbDvorak.addEventListener('click', () => {
    state.keyboard = 'dvorak';
    setActive(ui.btnKbDvorak, true);
    setActive(ui.btnKbQwerty, false);
    if (state.plot === '3d') refresh();
  });

  window.addEventListener('resize', () => {
    // For simplicity, recompute view on resize to maintain correct camera framing.
    refresh();
  });

  // ======= Animation loop =======
  function animate() {
    requestAnimationFrame(animate);
    renderer.render(scene, camera);
  }

  // ======= Initialize =======
  addLights();
  refresh();
  animate();
})();
</script>
</body>
</html>
