      labelPAmp.textContent   = fmt(sliderPAmp.value, 3);
      labelPWL.textContent    = fmt(sliderPWL.value, 2);
      labelSAmp.textContent   = fmt(sliderSAmp.value, 3);
      labelSWL.textContent    = fmt(sliderSWL.value, 2);
    }
    updateAllLabels();

    // p5 sketch
    let isPlaying = false;
    let particles = [];
    let logical = { xmin:0, xmax:5, ymin:-0.5, ymax:0.5 };
    let plot = { left:50, right:16, top:16, bottom:40 }; // margins in pixels
    let lastCanvasW = 0;

    function initParticles(n){
      particles = [];
      const N = Math.max(2, Math.floor(n));
      const L = logical.xmax - logical.xmin;
      const dx = (N>1) ? L/(N-1) : 0;
      for(let i=0;i<N;i++){
        const x0 = logical.xmin + i*dx;
        particles.push({ x0, history: [] });
      }
    }

    function clearHistories(){
      for(const p of particles){ p.history = []; }
    }

    // Reset function
    function resetAll(){
      isPlaying = false;
      btnPlayPause.textContent = 'Play';

      sliderTime.value   = defaults.time;
      sliderTrail.value  = defaults.trail;
      sliderPoints.value = defaults.points;
      sliderPAmp.value   = defaults.pAmp;
      sliderPWL.value    = defaults.pWl;
      sliderSAmp.value   = defaults.sAmp;
      sliderSWL.value    = defaults.sWl;

      updateAllLabels();
      initParticles(Number(sliderPoints.value));
    }

    // Event listeners for controls
    sliderTime.addEventListener('input', ()=>{ labelTime.textContent = fmt(sliderTime.value,2); });
    sliderTrail.addEventListener('input', ()=>{ labelTrail.textContent = fmt(sliderTrail.value,0); });
    sliderPoints.addEventListener('input', ()=>{
      labelPoints.textContent = fmt(sliderPoints.value,0);
      initParticles(Number(sliderPoints.value));
    });
    sliderPAmp.addEventListener('input', ()=>{ labelPAmp.textContent = fmt(sliderPAmp.value,3); });
    sliderPWL.addEventListener('input', ()=>{ labelPWL.textContent = fmt(sliderPWL.value,2); });
    sliderSAmp.addEventListener('input', ()=>{ labelSAmp.textContent = fmt(sliderSAmp.value,3); });
    sliderSWL.addEventListener('input', ()=>{ labelSWL.textContent = fmt(sliderSWL.value,2); });

    btnPlayPause.addEventListener('click', ()=>{
      isPlaying = !isPlaying;
      btnPlayPause.textContent = isPlaying ? 'Pause' : 'Play';
    });

    btnReset.addEventListener('click', ()=>{
      resetAll();
      clearHistories();
    });

    // p5 sketch in global mode (but will manage canvas placement and id)
    let cnv;
    function setup(){
      // Replace placeholder canvas by p5 canvas
      const placeholder = document.getElementById('p5-canvas');
      const parentNode = document.getElementById('canvas-container');
      if (placeholder) {
        // Remove the placeholder; p5 will create its own canvas
        placeholder.remove();
      }

      // Create responsive canvas
      const w = parentNode.clientWidth || 800;
      const h = 320;
      cnv = createCanvas(w, h);
      cnv.parent('canvas-container');
      cnv.id('p5-canvas');

      initParticles(Number(sliderPoints.value));
      updateAllLabels();
      lastCanvasW = w;
      frameRate(60);
      textFont('Arial, Helvetica, sans-serif');
    }

    function windowResized(){
      const parentNode = document.getElementById('canvas-container');
      const w = parentNode.clientWidth || 800;
      const h = 320;
      if (w !== lastCanvasW){
        resizeCanvas(w, h);
        lastCanvasW = w;
      }
    }

    function mapX(x){
      const left = plot.left;
      const right = width - plot.right;
      return map(x, logical.xmin, logical.xmax, left, right);
    }
    function mapY(y){
      const top = plot.top;
      const bottom = height - plot.bottom;
      return map(y, logical.ymax, logical.ymin, top, bottom);
    }

    function drawAxes(){
      const left = plot.left;
      const right = width - plot.right;
      const top = plot.top;
      const bottom = height - plot.bottom;

      // Border
      noFill();
      stroke(230);
      rect(left, top, right-left, bottom-top);

      // X-axis at y=0
      stroke(160);
      const y0 = mapY(0);
      line(left, y0, right, y0);

      // X ticks 0..5 step 1
      textSize(11);
      fill(80);
      noStroke();
      for (let x=logical.xmin; x<=logical.xmax+1e-6; x+=1){
        const px = mapX(x);
        stroke(210);
        line(px, bottom, px, top);
        noStroke();
        fill(80);
        textAlign(CENTER, TOP);
        text(fmt(x,0), px, bottom+4);
      }

      // Y ticks -0.4..0.4 step 0.2
      for (let y=-0.4; y<=0.4001; y+=0.2){
        const py = mapY(y);
        stroke(228);
        line(left, py, right, py);
        noStroke();
        fill(80);
        textAlign(RIGHT, CENTER);
        text(fmt(y,1), left-6, py);
      }
    }

    function draw(){
      background(255);

      // Auto-advance time if playing
      if (isPlaying){
        let t = parseFloat(sliderTime.value);
        const dt = 0.01; // time step per frame
        t += dt;
        if (t > parseFloat(sliderTime.max)) t = parseFloat(sliderTime.min);
        sliderTime.value = t;
        labelTime.textContent = fmt(t,2);
      }

      drawAxes();

      const t = parseFloat(sliderTime.value);
      const pAmp = parseFloat(sliderPAmp.value);
      const pWl  = parseFloat(sliderPWL.value);
      const sAmp = parseFloat(sliderSAmp.value);
      const sWl  = parseFloat(sliderSWL.value);
      const trailLen = parseInt(sliderTrail.value);

      // Iterate particles and draw
      for (let i=0; i<particles.length; i++){
        const part = particles[i];
        const x0 = part.x0;

        // Wave displacements
        const dx = pAmp * Math.sin(TWO_PI * (x0 / pWl - t));
        const dy = sAmp * Math.sin(TWO_PI * (x0 / sWl - t));

        const xFinal = x0 + dx;
        const yFinal = dy;

        // Save history for trails
        part.history.push({x:xFinal, y:yFinal});
        if (part.history.length > trailLen){
          part.history.splice(0, part.history.length - trailLen);
        }

        // Vertical line at current xFinal
        stroke(176,196,222); // lightsteelblue
        strokeWeight(1);
        const xPix = mapX(xFinal);
        const yTop = mapY(logical.ymax);
        const yBot = mapY(logical.ymin);
        line(xPix, yTop, xPix, yBot);

        // Draw trail (older points more transparent)
        if (trailLen > 0){
          noStroke();
          for (let j=0; j<part.history.length; j++){
            const hpos = part.history[j];
            const alpha = map(j, 0, part.history.length-1, 30, 120);
            fill(106,90,205, alpha); // slateblue with alpha
            ellipse(mapX(hpos.x), mapY(hpos.y), 3.2, 3.2);
          }
        }

        // Current particle
        noStroke();
        fill(106,90,205);
        ellipse(mapX(xFinal), mapY(yFinal), 6, 6);
      }
    }

    // Initialize after page loads
    window.addEventListener('load', ()=>{
      resetAll(); // sets initial state and particles

      // Ensure labels stay in sync on any change
      const inputs = [
        sliderTime, sliderTrail, sliderPoints,
        sliderPAmp, sliderPWL, sliderSAmp, sliderSWL
      ];
      inputs.forEach(el=>{
        el.addEventListener('change', ()=>{ /* draw loop updates continuously */ });
      });
    });
  </script>
</body>
</html>
