<body>
  <div id="app-container">
    <div id="control-panel">
      <div class="row">
        <label for="slider-dip">fault dip</label>
        <input type="range" id="slider-dip" min="0" max="90" step="1" value="0" />
        <span id="value-dip" class="value">0</span>
      </div>
      <div class="row">
        <label for="slider-depth">fault depth</label>
        <input type="range" id="slider-depth" min="100" max="1000" step="10" value="500" />
        <span id="value-depth" class="value">500</span>
      </div>
      <div class="row">
        <label for="slider-limit">x and y plot limit</label>
        <input type="range" id="slider-limit" min="500" max="2000" step="50" value="1000" />
        <span id="value-limit" class="value">1000</span>
      </div>

      <div class="group-row">
        <label>displacement component</label>
        <div id="btn-group-component" class="segmented">
          <button id="btn-component-x" aria-pressed="false">X</button>
          <button id="btn-component-y" aria-pressed="false">Y</button>
          <button id="btn-component-z" aria-pressed="true"  class="active">Z</button>
        </div>

        <label style="margin-left:6px;">fault type</label>
        <div id="btn-group-fault-type" class="segmented">
          <button id="btn-fault-tensile" class="active" aria-pressed="true">tensile</button>
          <button id="btn-fault-strike-slip" aria-pressed="false">strike-slip</button>
          <button id="btn-fault-normal" aria-pressed="false">normal</button>
        </div>
      </div>
    </div>

    <div id="plot-container">
      <div id="plot"></div>
    </div>
