<script>
(function(){
  // Constants
  const nu = 0.25; // Poisson's ratio
  const b  = 1.0;  // dislocation magnitude (meters)

  // Elements
  const dipSlider   = document.getElementById('slider-dip');
  const depthSlider = document.getElementById('slider-depth');
  const limitSlider = document.getElementById('slider-limit');

  const dipValue    = document.getElementById('value-dip');
  const depthValue  = document.getElementById('value-depth');
  const limitValue  = document.getElementById('value-limit');

  const btnGroupComponent = document.getElementById('btn-group-component');
  const btnGroupFault     = document.getElementById('btn-group-fault-type');

  // State
  let state = {
    component: 'Z',        // 'X' | 'Y' | 'Z'
    faultType: 'tensile',  // 'tensile' | 'strike-slip' | 'normal'
    dipDeg: +dipSlider.value,
    depth: +depthSlider.value,
    limit: +limitSlider.value
  };

  // Helpers for UI
  function setActiveInGroup(groupEl, targetBtn){
    [...groupEl.querySelectorAll('button')].forEach(btn=>{
      btn.classList.toggle('active', btn === targetBtn);
      btn.setAttribute('aria-pressed', btn === targetBtn ? 'true' : 'false');
    });
  }

  // Displacement model:
  // A compact, stable, closed-form approximation using Green's-function-inspired fields.
  // It captures qualitative patterns of tensile opening, strike-slip shear, and normal dip-slip
  // for a buried point dislocation in an elastic half-space.
  function calculateDisplacements(x, y, dipRad, depth, faultType, nuConst){
    // radial distance to the buried source
    const R = Math.sqrt(x*x + y*y + depth*depth);
    const invR3 = 1.0 / (R*R*R);

    // Base coefficients (scaled for reasonable magnitudes)
    const C = (1 - nuConst) / Math.PI * b;

    let ux=0, uy=0, uz=0;

    if (faultType === 'tensile'){
      // Isotropic opening (Mogi-like) with dip influencing anisotropy/orientation
      // Horizontal components diminish with larger dip; vertical uplifts reduce slightly with dip
      const aH = Math.cos(dipRad);                // horizontal weighting
      const aV = 0.6 + 0.4 * Math.cos(dipRad);    // vertical weighting
      ux = C * aH * x * invR3;
      uy = C * aH * y * invR3;
      uz = C * aV * depth * invR3;
    } else if (faultType === 'strike-slip'){
      // Anti-plane shear producing a horizontal swirl around the source.
      // A small vertical term depends on dip to introduce slight asymmetry.
      const S = 0.8 * C;
      ux = -S * y * depth * invR3;
      uy =  S * x * depth * invR3;
      uz =  0.25 * S * Math.sin(dipRad) * (x) * invR3;
    } else if (faultType === 'normal'){
      // Dip-slip on a plane striking along y, dipping in +x direction.
      // Slip direction has horizontal and vertical components depending on dip.
      const N = C;
      const sh = Math.sin(dipRad);
      const ch = Math.cos(dipRad);
      // Horizontal displacement along down-dip direction
      ux =  N * sh * x * depth * invR3 + 0.15*N*ch * x * invR3;
      // Modest transverse component to shape the saddle
      uy = -0.2 * N * sh * y * depth * invR3;
      // Vertical motion tied to down-dip slip; sign changes across x=0
      uz = -N * sh * x * invR3 + 0.3 * N * ch * depth * invR3;
    }

    return { ux, uy, uz };
  }

  // Build grid and compute Z values in mm for selected component
  function computeGridAndZ(){
    const dipRad = state.dipDeg * Math.PI / 180.0;
    const n = 50; // resolution
    const lim = state.limit;
    const xs = new Array(n);
    const ys = new Array(n);

    // Evenly spaced coordinates from -limit to +limit
    for (let i=0; i<n; i++){
      xs[i] = -lim + (2*lim) * (i/(n-1));
      ys[i] = -lim + (2*lim) * (i/(n-1));
    }

    const z = new Array(n);
    for (let j=0; j<n; j++){
      z[j] = new Array(n);
      const y = ys[j];
      for (let i=0; i<n; i++){
        const x = xs[i];
        const d = calculateDisplacements(x, y, dipRad, state.depth, state.faultType, nu);
        let comp = 0;
        if (state.component === 'X') comp = d.ux;
        else if (state.component === 'Y') comp = d.uy;
        else comp = d.uz;
        z[j][i] = comp * 1000.0; // meters -> millimeters
      }
    }
    return { xs, ys, z };
  }

  // Plotly update
  function updatePlot(){
    // Update numeric labels
    dipValue.textContent   = String(state.dipDeg);
    depthValue.textContent = String(state.depth);
    limitValue.textContent = String(state.limit);

    const { xs, ys, z } = computeGridAndZ();

    const trace = {
      type: 'surface',
      x: xs,
      y: ys,
      z: z,
      colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
      showscale: false,
      contours: {
        x: { show: true, color: 'black', width: 0.5 },
        y: { show: true, color: 'black', width: 0.5 },
        z: { show: false }
      },
      lighting: {
        ambient: 0.8,
        diffuse: 0.8,
        specular: 0.2
      }
    };

    const layout = {
      title: '',
      scene: {
        xaxis: { title: 'x (m)', range: [-state.limit, state.limit], zeroline: false },
        yaxis: { title: 'y (m)', range: [-state.limit, state.limit], zeroline: false },
        zaxis: { title: 'z (mm)', autorange: true },
        camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
      },
      margin: { l: 20, r: 20, b: 20, t: 20 }
    };

    Plotly.react('plot', [trace], layout, {responsive: true});
  }

  // Event handlers
  function onSliderInput(){
    state.dipDeg = +dipSlider.value;
    state.depth  = +depthSlider.value;
    state.limit  = +limitSlider.value;
    updatePlot();
  }

  dipSlider.addEventListener('input', onSliderInput);
  depthSlider.addEventListener('input', onSliderInput);
  limitSlider.addEventListener('input', onSliderInput);

  btnGroupComponent.addEventListener('click', (e)=>{
    if (e.target.tagName !== 'BUTTON') return;
    setActiveInGroup(btnGroupComponent, e.target);
    const id = e.target.id;
    if (id === 'btn-component-x') state.component = 'X';
    else if (id === 'btn-component-y') state.component = 'Y';
    else state.component = 'Z';
    updatePlot();
  });

  btnGroupFault.addEventListener('click', (e)=>{
    if (e.target.tagName !== 'BUTTON') return;
    setActiveInGroup(btnGroupFault, e.target);
    const id = e.target.id;
    if (id === 'btn-fault-tensile') state.faultType = 'tensile';
    else if (id === 'btn-fault-strike-slip') state.faultType = 'strike-slip';
    else state.faultType = 'normal';
    updatePlot();
  });

  // Initial render
  updatePlot();

  // Resize handling for responsiveness
  window.addEventListener('resize', () => Plotly.Plots.resize('plot'));
})();
</script>
</body>
</html>
