  <!-- p5.js for drawing -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // State and DOM references
    const state = {
      t: 0.1,
      k: 2,
      showAxes: true
    };

    const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

    const sliderT = document.getElementById('slider-t');
    const sliderK = document.getElementById('slider-k');
    const tValueDisplay = document.getElementById('t-value-display');
    const kValueDisplay = document.getElementById('k-value-display');
    const checkboxAxes = document.getElementById('checkbox-axes');
    const formulaDiv = document.getElementById('formula-display');

    function updateFormula() {
      const z_k = zeros[state.k - 1];
      const omega_k = z_k / 2;
      const tex = `\\(\\omega_{${state.k}} = ${omega_k.toFixed(2)}\\, \\sqrt{g/l}\\)`;
      formulaDiv.innerHTML = tex;
      if (window.MathJax && MathJax.typesetPromise) {
        MathJax.typesetPromise([formulaDiv]);
      } else if (window.MathJax && MathJax.typeset) {
        MathJax.typeset([formulaDiv]);
      } else {
        // Try again a bit later if MathJax isn't ready yet
        setTimeout(() => {
          if (window.MathJax && MathJax.typesetPromise) MathJax.typesetPromise([formulaDiv]);
        }, 200);
      }
    }

    // Initialize displays and formula
    function syncDisplays() {
      tValueDisplay.textContent = Number(state.t).toFixed(2);
      kValueDisplay.textContent = String(state.k);
      updateFormula();
    }

    sliderT.addEventListener('input', () => {
      state.t = parseFloat(sliderT.value);
      tValueDisplay.textContent = state.t.toFixed(2);
    });

    sliderK.addEventListener('input', () => {
      state.k = parseInt(sliderK.value, 10);
      kValueDisplay.textContent = String(state.k);
      updateFormula();
    });

    checkboxAxes.addEventListener('change', () => {
      state.showAxes = checkboxAxes.checked;
    });

    syncDisplays();

    // Bessel function J0 approximation (Abramowitz & Stegun 9.1.21, 9.1.24)
    function besselJ0(x) {
      const ax = Math.abs(x);
      let y, ans1, ans2, z, xx;
      if (ax < 8.0) {
        y = x * x;
        ans1 = 57568490574.0 + y * (-13362590354.0 + y * (651619640.7 +
              y * (-11214424.18 + y * (77392.33017 + y * (-184.9052456)))));
        ans2 = 57568490411.0 + y * (1029532985.0 + y * (9494680.718 +
              y * (59272.64853 + y * (267.8532712 + y))));
        return ans1 / ans2;
      } else {
        z = 8.0 / ax;
        y = z * z;
        xx = ax - 0.25 * Math.PI;
        ans1 = 1.0 + y * (-0.001098628627 + y * (0.00002734510407 +
              y * (-0.000002073370639 + y * 0.0000002093887211)));
        ans2 = -0.01562499995 + y * (0.0001430488765 +
              y * (-0.000006911147651 + y * (0.0000007621095161 - 0.0000000934935152)));
        const res = Math.sqrt(0.636619772 / ax) * (Math.cos(xx) * ans1 - z * Math.sin(xx) * ans2);
        return res;
      }
    }

    // p5.js sketch in instance mode
    const sketch = (p) => {
      let w, h;
      const xMin = -2, xMax = 2;
      const yTop = 0, yBottom = -1.2;
      const amplitude = 0.8;

      function mapX(x) {
        return p.map(x, xMin, xMax, 0, w);
      }
      function mapY(y) {
        // yTop (0) at top -> yBottom negative at bottom
        return p.map(y, yTop, yBottom, 0, h);
      }

      p.setup = function() {
        // Choose a responsive size
        const container = document.getElementById('canvas-container');
        w = container.clientWidth - 2; // account for borders
        h = container.clientHeight - 2;
        // If container has no height yet (first load), set a default
        if (h < 100) h = 520;
        if (w < 200) w = 620;
        const cnv = p.createCanvas(w, h);
        cnv.parent('canvas-container');
        p.frameRate(60);
        p.strokeCap(p.ROUND);
      };

      p.windowResized = function() {
        // Keep canvas responsive to container size changes
        const container = document.getElementById('canvas-container');
        const newW = container.clientWidth - 2;
        const newH = container.clientHeight - 2;
        if (newW > 0 && newH > 0) {
          w = newW;
          h = newH;
          p.resizeCanvas(w, h);
        }
      };

      function drawAxes() {
        const ctx = p.drawingContext;
        // Dashed axes
        ctx.save();
        ctx.setLineDash([6, 6]);
        p.stroke(160);
        p.strokeWeight(1);

        // Horizontal axis at y = 0 from xMin to xMax
        p.line(mapX(xMin), mapY(0), mapX(xMax), mapY(0));
        // Vertical axis at x = 0 from yTop to yBottom
        p.line(mapX(0), mapY(yTop), mapX(0), mapY(yBottom));
        ctx.restore();

        // Ticks and labels
        p.fill(90);
        p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        p.textSize(12);

        // x-axis ticks at -2, -1, 1, 2
        const xTicks = [-2, -1, 1, 2];
        xTicks.forEach((xt) => {
          const px = mapX(xt);
          const py = mapY(0);
          p.stroke(160);
          p.strokeWeight(1);
          p.line(px, py - 6, px, py + 6);
          p.noStroke();
          p.text(xt.toString(), px, py + 8);
        });

        // y-axis ticks at -0.2, -0.4, ..., -1.0
        p.textAlign(p.RIGHT, p.CENTER);
        for (let y = -0.2; y >= -1.0 - 1e-9; y -= 0.2) {
          const py = mapY(y);
          const px = mapX(0);
          p.stroke(160);
          p.line(px - 6, py, px + 6, py);
          p.noStroke();
          p.text(y.toFixed(1), px - 8, py);
        }
      }

      function drawString() {
        const k = state.k;
        const z_k = zeros[k - 1];
        const omega_k = z_k / 2;
        const timeFactor = Math.cos(omega_k * state.t);

        p.noFill();
        p.stroke(0x3f, 0x6e, 0xa8);
        p.strokeWeight(4);

        p.beginShape();
        for (let yNorm = 0; yNorm <= 1.0001; yNorm += 0.005) {
          const bessel_arg = z_k * Math.sqrt(yNorm);
          const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
          const y_pos = -yNorm;
          const px = mapX(x_pos);
          const py = mapY(y_pos);
          p.vertex(px, py);
        }
        p.endShape();
      }

      p.draw = function() {
        p.background('#f0f0f0');

        if (state.showAxes) {
          drawAxes();
        }

        drawString();
      };
    };

    new p5(sketch);

    // Ensure formula renders after MathJax loads
    if (document.readyState === 'complete') {
      updateFormula();
    } else {
      window.addEventListener('load', updateFormula);
    }
  </script>
</body>
</html>
