<script>
(function() {
  // Constants for Propylene
  const Tc = 365.57;       // K
  const Pc = 46.65;        // bar
  const omega = 0.144;     // acentric factor
  const R = 0.0831446;     // L·bar/(mol·K)

  // DOM elements
  const btnSRK = document.getElementById('btn-srk');
  const btnPR = document.getElementById('btn-pr');
  const checkboxAspen = document.getElementById('checkbox-aspen');
  const sliderPressure = document.getElementById('slider-pressure');
  const displayPressure = document.getElementById('display-pressure');
  const plotEl = document.getElementById('plot-container');

  // State
  let activeEOS = 'SRK'; // default
  displayPressure.textContent = sliderPressure.value;

  // Aspen Plus comparison data (fixed)
  const aspenX = [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400];
  const aspenY = [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946];

  // Styling helpers
  function updateButtonStyles() {
    btnSRK.classList.toggle('selected', activeEOS === 'SRK');
    btnPR.classList.toggle('selected', activeEOS === 'PR');
  }

  // Numerics: cubic solver for x^3 + a x^2 + b x + c = 0
  function cubicRealRoots(a, b, c) {
    const a1 = a, b1 = b, c1 = c;
    const p = b1 - (a1 * a1) / 3;
    const q = (2 * a1 * a1 * a1) / 27 - (a1 * b1) / 3 + c1;
    const disc = (q * q) / 4 + (p * p * p) / 27;
    const shift = -a1 / 3;
    const roots = [];

    if (disc > 1e-14) {
      // One real root
      const sqrtDisc = Math.sqrt(disc);
      const u = Math.cbrt(-q / 2 + sqrtDisc);
      const v = Math.cbrt(-q / 2 - sqrtDisc);
      roots.push(u + v + shift);
    } else if (Math.abs(disc) <= 1e-14) {
      // Multiple real roots
      const u = Math.cbrt(-q / 2);
      roots.push(2 * u + shift);
      roots.push(-u + shift);
    } else {
      // Three real roots
      const r = Math.sqrt(-p / 3);
      let phi = Math.acos(Math.max(-1, Math.min(1, -q / (2 * r * r * r))));
      for (let k = 0; k < 3; k++) {
        const y = 2 * r * Math.cos((phi + 2 * Math.PI * k) / 3);
        roots.push(y + shift);
      }
    }
    // Filter numerical noise (all are real by construction)
    return roots;
  }

  function largestRealRoot(a, b, c) {
    const roots = cubicRealRoots(a, b, c);
    // select the largest real root
    return Math.max.apply(null, roots);
  }

  function computeEOSData(eos, Pbar) {
    const Tvals = [];
    const phivals = [];

    for (let T = 300; T <= 500; T += 5) {
      const Tr = T / Tc;

      let a, b, m, alpha;
      if (eos === 'SRK') {
        a = 0.42748 * R * R * Tc * Tc / Pc;
        b = 0.08664 * R * Tc / Pc;
        m = 0.480 + 1.574 * omega - 0.176 * omega * omega;
        alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
      } else {
        a = 0.45724 * R * R * Tc * Tc / Pc;
        b = 0.07780 * R * Tc / Pc;
        m = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
        alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
      }

      const a_eff = a * alpha;
      const A = a_eff * Pbar / (R * T * R * T);
      const B = b * Pbar / (R * T);

      let Z;
      if (eos === 'SRK') {
        // Z^3 - Z^2 + (A - B - B^2) Z - A B = 0  -> a=-1, b=(A-B-B^2), c=-AB
        Z = largestRealRoot(-1, (A - B - B * B), -A * B);
      } else {
        // Z^3 - (1 - B) Z^2 + (A - 3B^2 - 2B) Z - (A B - B^2 - B^3) = 0
        // -> a = B-1, b = A - 3B^2 - 2B, c = -A B + B^2 + B^3
        Z = largestRealRoot(B - 1, (A - 3 * B * B - 2 * B), (-A * B + B * B + B * B * B));
      }

      let lnphi;
      if (eos === 'SRK') {
        lnphi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);
      } else {
        const sqrt2 = Math.SQRT2;
        const term1 = Z + (1 + sqrt2) * B;
        const term2 = Z + (1 - sqrt2) * B;
        lnphi = Z - 1 - Math.log(Z - B) - (A / (2 * sqrt2 * B)) * Math.log(term1 / term2);
      }

      const phi = Math.exp(lnphi);
      Tvals.push(T);
      phivals.push(phi);
    }
    return { x: Tvals, y: phivals };
  }

  const layout = {
    margin: { l: 80, r: 20, t: 10, b: 70 },
    xaxis: {
      title: 'temperature (K)',
      range: [295, 505],
      showgrid: true,
      gridcolor: '#ffffff',
      gridwidth: 1,
      zeroline: false,
      minor: { showgrid: true, gridcolor: '#e9e9e9' }
    },
    yaxis: {
      title: 'gas-phase fugacity coefficient',
      range: [0.68, 1.02],
      showgrid: true,
      gridcolor: '#ffffff',
      gridwidth: 1,
      zeroline: false,
      minor: { showgrid: true, gridcolor: '#e9e9e9' }
    },
    plot_bgcolor: '#f0f0f0',
    paper_bgcolor: '#f0f0f0',
    showlegend: false
  };

  function updatePlot() {
    const P = parseInt(sliderPressure.value, 10);
    const eos = activeEOS;
    const eosColor = eos === 'SRK' ? 'red' : 'blue';

    const dataEOS = computeEOSData(eos, P);

    const eosTrace = {
      x: dataEOS.x,
      y: dataEOS.y,
      type: 'scatter',
      mode: 'lines',
      line: { color: eosColor, width: 2 },
      name: eos
    };

    const showAspen = (P === 10 && checkboxAspen.checked);
    const aspenTrace = {
      x: aspenX,
      y: aspenY,
      type: 'scatter',
      mode: 'markers',
      marker: { color: 'green', symbol: 'square', size: 8 },
      name: 'Aspen Plus',
      visible: showAspen
    };

    Plotly.react(plotEl, [eosTrace, aspenTrace], layout, { displayModeBar: false, responsive: true });
  }

  // Event listeners
  btnSRK.addEventListener('click', () => {
    activeEOS = 'SRK';
    updateButtonStyles();
    updatePlot();
  });
  btnPR.addEventListener('click', () => {
    activeEOS = 'PR';
    updateButtonStyles();
    updatePlot();
  });
  sliderPressure.addEventListener('input', (e) => {
    displayPressure.textContent = e.target.value;
    updatePlot();
  });
  checkboxAspen.addEventListener('change', updatePlot);

  // Initialize
  updateButtonStyles();
  updatePlot();
})();
</script>
</body>
