<script>
(() => {
  // Global state for the application
  const state = {
    mode: 'da',           // 'da' | 'regio' | 'stereo-phile' | 'stereo-diene' | 'homo'
    inverse: false,
    focus: false,
    reaction: 0,          // 0-100
    ergEwg: 0             // 0-100
  };

  // Cache DOM controls
  const btns = {
    da: document.getElementById('btn-mode-da'),
    regio: document.getElementById('btn-mode-regio'),
    stereoPhile: document.getElementById('btn-mode-stereo-phile'),
    stereoDiene: document.getElementById('btn-mode-stereo-diene'),
    homo: document.getElementById('btn-mode-homo'),
  };

  const controls = {
    inverseContainer: document.getElementById('inverse-container'),
    focusContainer: document.getElementById('focus-container'),
    reactionContainer: document.getElementById('reaction-slider-container'),
    ergEwgContainer: document.getElementById('erg-ewg-slider-container'),
    inverse: document.getElementById('check-inverse'),
    focus: document.getElementById('check-focus'),
    reaction: document.getElementById('slider-reaction'),
    ergEwg: document.getElementById('slider-erg-ewg'),
    reactionMinus: document.getElementById('btn-reaction-minus'),
    reactionPlus: document.getElementById('btn-reaction-plus'),
    ergMinus: document.getElementById('btn-erg-ewg-minus'),
    ergPlus: document.getElementById('btn-erg-ewg-plus'),
  };

  // Attach mode button events
  btns.da.addEventListener('click', () => setMode('da'));
  btns.regio.addEventListener('click', () => setMode('regio'));
  btns.stereoPhile.addEventListener('click', () => setMode('stereo-phile'));
  btns.stereoDiene.addEventListener('click', () => setMode('stereo-diene'));
  btns.homo.addEventListener('click', () => setMode('homo'));

  // Control events
  controls.inverse.addEventListener('change', (e) => { state.inverse = e.target.checked; });
  controls.focus.addEventListener('change', (e) => { state.focus = e.target.checked; });

  controls.reaction.addEventListener('input', (e) => { state.reaction = +e.target.value; });
  controls.ergEwg.addEventListener('input', (e) => { state.ergEwg = +e.target.value; });

  controls.reactionMinus.addEventListener('click', () => stepSlider(controls.reaction, -1));
  controls.reactionPlus.addEventListener('click', () => stepSlider(controls.reaction, +1));
  controls.ergMinus.addEventListener('click', () => stepSlider(controls.ergEwg, -1));
  controls.ergPlus.addEventListener('click', () => stepSlider(controls.ergEwg, +1));

  function stepSlider(slider, delta){
    const min = +slider.min, max = +slider.max, step = +slider.step || 1;
    let v = +slider.value + delta*step;
    v = Math.max(min, Math.min(max, v));
    slider.value = v;
    slider.dispatchEvent(new Event('input', {bubbles:true}));
  }

  function setMode(mode){
    state.mode = mode;
    // Visual active class
    document.querySelectorAll('.mode-button').forEach(b => b.classList.remove('active'));
    switch(mode){
      case 'da': btns.da.classList.add('active'); break;
      case 'regio': btns.regio.classList.add('active'); break;
      case 'stereo-phile': btns.stereoPhile.classList.add('active'); break;
      case 'stereo-diene': btns.stereoDiene.classList.add('active'); break;
      case 'homo': btns.homo.classList.add('active'); break;
    }

    // Reset controls to defaults for new mode (as "initial state")
    controls.inverse.checked = false;
    controls.focus.checked = false;
    controls.reaction.value = 0;
    controls.ergEwg.value = 0;

    state.inverse = false;
    state.focus = false;
    state.reaction = 0;
    state.ergEwg = 0;

    // Update visibility according to the plan
    updateControlVisibility();
  }

  function updateControlVisibility(){
    const hide = (el, yes) => el.classList.toggle('hidden', yes);
    const m = state.mode;

    // Defaults: hide all, then enable per mode
    let showInverse=false, showFocus=false, showReaction=false, showErg=false;

    if(m === 'da'){
      showFocus = true;
      showReaction = true;
    } else if(m === 'regio'){
      showInverse = true;
      showFocus = true;
      showReaction = true;
      showErg = true;
    } else if(m === 'stereo-phile' || m === 'stereo-diene'){
      showFocus = true;
    } else if(m === 'homo'){
      showInverse = true;
      showErg = true;
    }

    hide(controls.inverseContainer, !showInverse);
    hide(controls.focusContainer, !showFocus);
    hide(controls.reactionContainer, !showReaction);
    hide(controls.ergEwgContainer, !showErg);
  }

  // Initialize
  updateControlVisibility();

  // p5 sketch (instance mode), mounted into canvas-container
  const sketch = (p) => {
    let renderer; // p5.Renderer
    function createOrResizeCanvas(){
      const parent = document.getElementById('canvas-container');
      const w = parent.clientWidth;
      const h = parent.clientHeight;
      if(!renderer){
        renderer = p.createCanvas(w, h);
        // Give this canvas the required id, remove placeholder if present
        renderer.elt.id = 'viz-canvas';
        // Remove the initial placeholder canvas if there were two
        const placeholders = parent.querySelectorAll('canvas');
        if(placeholders.length > 1){
          placeholders.forEach(c => { if(c !== renderer.elt) c.remove(); });
        }
      }else{
        p.resizeCanvas(w, h);
      }
    }

    p.setup = function(){
      createOrResizeCanvas();
      p.pixelDensity(1);
      p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif');
    };

    p.windowResized = function(){
      createOrResizeCanvas();
    };

    p.draw = function(){
      p.background(255);
      p.push();
      p.stroke(0);
      p.fill(0);

      switch(state.mode){
        case 'da': drawDielsAlder(p); break;
        case 'regio': drawRegio(p); break;
        case 'stereo-phile': drawStereo(p, 'phile'); break;
        case 'stereo-diene': drawStereo(p, 'diene'); break;
        case 'homo': drawHomoLumo(p); break;
      }

      p.pop();
    };

    // ---------- Utility drawing helpers ----------
    function drawDoubleBond(x1,y1,x2,y2, weight=2, gap=3, alpha=255, col=[0,0,0]){
      const dx = x2-x1, dy=y2-y1;
      const len = Math.hypot(dx,dy) || 1;
      const nx = -dy/len, ny = dx/len;
      p.strokeWeight(weight);
      p.stroke(col[0], col[1], col[2], alpha);
      p.line(x1 + nx*gap, y1 + ny*gap, x2 + nx*gap, y2 + ny*gap);
      p.line(x1 - nx*gap, y1 - ny*gap, x2 - nx*gap, y2 - ny*gap);
    }
    function drawSingleBond(x1,y1,x2,y2, weight=2, alpha=255, col=[0,0,0]){
      p.strokeWeight(weight);
      p.stroke(col[0], col[1], col[2], alpha);
      p.line(x1, y1, x2, y2);
    }
    function drawHighlight(x1,y1,x2,y2, strength=1, alpha=160){
      // Bold semi-transparent red underlay to highlight bonds
      p.stroke(255,0,0, alpha);
      p.strokeWeight(4*strength);
      p.line(x1,y1,x2,y2);
    }
    function drawDashedArrow(x1,y1,x2,y2, dash=8, gap=6, weight=2, color=[0,0,0,255]){
      const dx=x2-x1, dy=y2-y1, dist = Math.hypot(dx,dy), ang=Math.atan2(dy,dx);
      p.push();
      p.translate(x1,y1);
      p.rotate(ang);
      p.stroke(color[0], color[1], color[2], color[3]);
      p.strokeWeight(weight);
      let x=0;
      while(x < dist-10){
        p.line(x,0, Math.min(x+dash, dist-10), 0);
        x += dash + gap;
      }
      // arrow head
      p.translate(dist,0);
      p.line(0,0,-10, -5);
      p.line(0,0,-10, +5);
      p.pop();
    }
    function lerpPt(a,b,t){ return [p.lerp(a[0],b[0],t), p.lerp(a[1],b[1],t)]; }

    // ---------- Mode renderers ----------
    function drawDielsAlder(){
      const w = p.width, h = p.height;
      const t = state.reaction/100; // 0..1

      // Positions for approach (0..0.5)
      const leftStart = [w*0.28, h*0.55];
      const leftEnd   = [w*0.40, h*0.55];
      const rightStart= [w*0.72, h*0.55];
      const rightEnd  = [w*0.60, h*0.55];

      const tApproach = p.constrain(p.map(t,0,0.5,0,1),0,1);
      const L = lerpPt(leftStart, leftEnd, tApproach);
      const R = lerpPt(rightStart, rightEnd, tApproach);

      // Stage 1: reactants
      const alphaReact = (t < 0.5) ? 255 : p.map(t,0.5,1,255,40);
      drawButadiene(L[0], L[1], 1.0, -25*Math.PI/180, alphaReact);
      drawEthylene(R[0], R[1], 1.0, 90*Math.PI/180, alphaReact);

      if(state.focus){
        // Highlight reactive π bonds during approach
        highlightButadiene(L[0], L[1], 1.0, -25*Math.PI/180, (t<0.5)? 180: p.map(t,0.5,1,180,30), true);
        highlightEthylene(R[0], R[1], 1.0, 90*Math.PI/180, (t<0.5)? 180: p.map(t,0.5,1,180,30), true);
      }

      // Stage 2: product appears (0.5..1)
      if(t >= 0.5){
        const tf = p.constrain(p.map(t,0.5,1,0,1),0,1);
        const alphaProd = p.map(tf,0,1,40,255);
        drawCyclohexene(w*0.50, h*0.55, 120, alphaProd, tf);
        if(state.focus){
          // two forming sigma bonds + one forming pi bond in red strengthening
          drawCyclohexeneFocus(w*0.50, h*0.55, 120, p.map(tf,0,1,40,230));
        }
      }
    }

    function drawButadiene(cx, cy, scale=1, angle=0, alpha=255){
      // Simple s-cis butadiene representation
      const len = 60*scale, kink = 26*scale;
      const p1 = [cx-len, cy-kink];
      const p2 = [cx-10,  cy-8];
      const p3 = [cx-10,  cy+8];
      const p4 = [cx-len, cy+kink];
      const q1 = [cx+18,  cy-8];
      const q2 = [cx+18,  cy+8];

      p.push();
      p.translate(cx, cy);
      p.rotate(angle);
      p.translate(-cx, -cy);

      // double bonds (p1-p2) and (p3-p4)
      drawDoubleBond(p1[0],p1[1], p2[0],p2[1], 2, 3, alpha);
      drawSingleBond(p2[0],p2[1], q1[0],q1[1], 2, alpha); // central single bond (upper)
      drawSingleBond(p3[0],p3[1], q2[0],q2[1], 2, alpha); // central single bond (lower)
      drawDoubleBond(p3[0],p3[1], p4[0],p4[1], 2, 3, alpha);
      p.pop();
    }
    function highlightButadiene(cx, cy, scale=1, angle=0, alpha=160, onlyPi=false){
      const len = 60*scale, kink = 26*scale;
      const p1 = [cx-len, cy-kink];
      const p2 = [cx-10,  cy-8];
      const p3 = [cx-10,  cy+8];
      const p4 = [cx-len, cy+kink];
      const q1 = [cx+18,  cy-8];
      const q2 = [cx+18,  cy+8];

      p.push();
      p.translate(cx, cy);
      p.rotate(angle);
      p.translate(-cx, -cy);
      // highlight the two π bonds (p1-p2 and p3-p4) that break
      drawHighlight(p1[0],p1[1], p2[0],p2[1], 1, alpha);
      drawHighlight(p3[0],p3[1], p4[0],p4[1], 1, alpha);
      if(!onlyPi){
        drawHighlight(p2[0],p2[1], q1[0],q1[1], 1, alpha*0.6);
        drawHighlight(p3[0],p3[1], q2[0],q2[1], 1, alpha*0.6);
      }
      p.pop();
    }
    function drawEthylene(cx, cy, scale=1, angle=0, alpha=255){
      const len=22*scale;
      const p1=[cx-len, cy], p2=[cx+len, cy];
      p.push();
      p.translate(cx,cy);
      p.rotate(angle);
      p.translate(-cx,-cy);
      drawDoubleBond(p1[0],p1[1], p2[0],p2[1], 2, 3, alpha);
      p.pop();
    }
    function highlightEthylene(cx,cy,scale=1,angle=0,alpha=160){
      const len=22*scale;
      const p1=[cx-len, cy], p2=[cx+len, cy];
      p.push();
      p.translate(cx,cy);
      p.rotate(angle);
      p.translate(-cx,-cy);
      drawHighlight(p1[0],p1[1], p2[0],p2[1], 1, alpha);
      p.pop();
    }
    function drawCyclohexene(cx, cy, size=120, alpha=255, tf=1){
      // Draw a hexagon ring with a double bond on the right; lightly morph using tf if desired
      const r = size/2;
      const pts = [];
      for(let i=0;i<6;i++){
        const a = -Math.PI/2 + i*Math.PI/3;
        pts.push([cx + r*Math.cos(a), cy + r*Math.sin(a)]);
      }
      // ring edges
      for(let i=0;i<6;i++){
        const a = pts[i], b = pts[(i+1)%6];
        drawSingleBond(a[0],a[1], b[0],b[1], 2, alpha);
      }
      // right side double bond (between pts[1]-pts[2])
      drawDoubleBond(pts[1][0],pts[1][1], pts[2][0],pts[2][1], 2, 3, alpha);
    }
    function drawCyclohexeneFocus(cx, cy, size=120, alpha=200){
      const r = size/2;
      const pts = [];
      for(let i=0;i<6;i++){
        const a = -Math.PI/2 + i*Math.PI/3;
        pts.push([cx + r*Math.cos(a), cy + r*Math.sin(a)]);
      }
      // Highlight: two new σ bonds (connecting 1-6 and 3-4 if we consider DA formation),
      // and the new π bond (right side).
      drawHighlight(pts[0][0],pts[0][1], pts[1][0],pts[1][1], 1.2, alpha); // sigma
      drawHighlight(pts[2][0],pts[2][1], pts[3][0],pts[3][1], 1.2, alpha); // sigma
      drawHighlight(pts[1][0],pts[1][1], pts[2][0],pts[2][1], 1.2, alpha); // pi
    }

    function drawRegio(){
      const w=p.width, h=p.height;
      const t = state.reaction/100;

      // Reactants at center fading out
      const alphaReact = p.map(t,0,1,255,40);
      drawButadiene(w*0.36, h*0.55, 1.0, -20*Math.PI/180, alphaReact);
      drawEthylene(w*0.56, h*0.55, 1.0, 90*Math.PI/180, alphaReact);
      if(state.focus){
        highlightButadiene(w*0.36, h*0.55, 1.0, -20*Math.PI/180, (1-t)*160, true);
        highlightEthylene(w*0.56, h*0.55, 1.0, 90*Math.PI/180, (1-t)*160);
      }

      // Two products appear simultaneously on left and right paths
      const prodAlpha = p.map(t,0,1,40,255);
      const leftPos = [w*0.28, h*0.55];
      const rightPos = [w*0.72, h*0.55];

      // ERG/EWG slider adjusts prominence (dominant product stronger)
      const e = state.ergEwg/100;
      let leftStrength = p.map(e,0,1,0.7,1.0);
      let rightStrength = p.map(e,0,1,0.7,0.4);
      if(state.inverse){ // swap dominance
        [leftStrength, rightStrength] = [rightStrength, leftStrength];
      }

      drawCyclohexene(leftPos[0], leftPos[1], 110, prodAlpha*leftStrength);
      drawCyclohexene(rightPos[0], rightPos[1], 110, prodAlpha*rightStrength);

      if(state.focus){
        drawCyclohexeneFocus(leftPos[0], leftPos[1], 110, prodAlpha*leftStrength);
        drawCyclohexeneFocus(rightPos[0], rightPos[1], 110, prodAlpha*rightStrength);
      }

      // Substituent labels to hint regio outcome
      p.noStroke();
      p.fill(90,90,90, 160*leftStrength);
      p.textAlign(p.CENTER, p.BOTTOM);
      p.textSize(14);
      p.text("major (ERG on diene)", leftPos[0], leftPos[1]-90);
      p.fill(90,90,90, 160*rightStrength);
      p.text("minor", rightPos[0], rightPos[1]-90);
    }

    function drawStereo(perspective){
      // Static two panels: cis (meso) top, trans (racemic) bottom
      const w=p.width, h=p.height;
      const pad=24;
      const bw = w - 2*pad, bh = h - 2*pad;
      const midY = pad + bh/2;

      p.stroke(0); p.strokeWeight(1.5); p.noFill();
      p.rect(pad, pad, bw, bh);
      p.line(pad, midY, pad+bw, midY);

      // Titles
      p.textAlign(p.CENTER, p.CENTER);
      p.textSize(14); p.noStroke(); p.fill(0);
      p.text("cis", w*0.5, pad+14);
      p.text("trans", w*0.5, midY+14);
      p.textSize(13); p.fill(70);
      p.text("meso compound", w*0.5, midY-18);
      p.text("racemic mixture", w*0.5, h-pad-14);

      // Top: single meso product, highlight two new σ bonds
      const topCenter = [w*0.5, pad + (midY-pad)/2 + 10];
      drawCyclohexene(topCenter[0], topCenter[1], 160, 255);
      // dashed vertical symmetry plane
      p.stroke(255,0,0,110); p.strokeWeight(1); drawDashedLine(topCenter[0], topCenter[1]-90, topCenter[0], topCenter[1]+90, 6,6);
      // substituents (draw as short red bonds)
      p.stroke(180); p.strokeWeight(2);
      const r=160/2;
      const v1=[topCenter[0]+r*Math.cos(-Math.PI/2 + Math.PI/3), topCenter[1]+r*Math.sin(-Math.PI/2 + Math.PI/3)]; // right-top vertex (approx DB end)
      const v2=[topCenter[0]+r*Math.cos(-Math.PI/2 + 2*Math.PI/3), topCenter[1]+r*Math.sin(-Math.PI/2 + 2*Math.PI/3)];
      if(state.focus){ p.stroke(255,0,0); }
      p.line(v1[0],v1[1], v1[0]+30, v1[1]-20);
      p.line(v2[0],v2[1], v2[0]+30, v2[1]+20);
      p.noStroke(); p.fill(120);
      p.textAlign(p.LEFT, p.CENTER);
      p.textSize(16);
      p.text("COOH", v1[0]+34, v1[1]-22);
      p.text("COOH", v2[0]+34, v2[1]+20);

      // Bottom: two enantiomers side-by-side
      const bottomY = midY + (h-midY)/2 + 10;
      const leftC = [w*0.33, bottomY], rightC=[w*0.67, bottomY];
      drawCyclohexene(leftC[0], leftC[1], 160, 255);
      drawCyclohexene(rightC[0], rightC[1], 160, 255);
      // dashed vertical reflection hints
      p.stroke(255,0,0,110); drawDashedLine(leftC[0], leftC[1]-90, leftC[0], leftC[1]+90, 6,6);
      p.stroke(255,0,0,110); drawDashedLine(rightC[0], rightC[1]-90, rightC[0], rightC[1]+90, 6,6);
      // substituents mirrored (simple lines)
      p.stroke(state.focus? [255,0,0] : [180]); p.strokeWeight(2);
      const r2=160/2;
      // left molecule
      const lv1=[leftC[0]+r2*Math.cos(-Math.PI/2 + Math.PI/3), leftC[1]+r2*Math.sin(-Math.PI/2 + Math.PI/3)];
      const lv2=[leftC[0]+r2*Math.cos(-Math.PI/2 + 2*Math.PI/3), leftC[1]+r2*Math.sin(-Math.PI/2 + 2*Math.PI/3)];
      p.line(lv1[0],lv1[1], lv1[0]-30, lv1[1]-20);
      p.line(lv2[0],lv2[1], lv2[0]-20, lv2[1]+26);
      p.noStroke(); p.fill(120); p.textSize(16); p.textAlign(p.LEFT,p.CENTER);
      p.text("COOH", lv1[0]-28, lv1[1]-26);
      p.text("HOOC", lv2[0]-18, lv2[1]+26);

      // right molecule (mirror)
      const rv1=[rightC[0]+r2*Math.cos(-Math.PI/2 + Math.PI/3), rightC[1]+r2*Math.sin(-Math.PI/2 + Math.PI/3)];
      const rv2=[rightC[0]+r2*Math.cos(-Math.PI/2 + 2*Math.PI/3), rightC[1]+r2*Math.sin(-Math.PI/2 + 2*Math.PI/3)];
      p.stroke(state.focus? [255,0,0] : [180]); p.strokeWeight(2);
      p.line(rv1[0],rv1[1], rv1[0]+30, rv1[1]+20);
      p.line(rv2[0],rv2[1], rv2[0]+20, rv2[1]-26);
      p.noStroke(); p.fill(120); p.textAlign(p.LEFT,p.CENTER);
      p.text("COOH", rv1[0]+34, rv1[1]+20);
      p.text("HOOC", rv2[0]+22, rv2[1]-26);

      function drawDashedLine(x1,y1,x2,y2,dash=6,gap=6){
        const dx=x2-x1, dy=y2-y1, dist=Math.hypot(dx,dy), ang=Math.atan2(dy,dx);
        p.push(); p.translate(x1,y1); p.rotate(ang); let x=0; p.strokeWeight(1.2);
        while(x<dist){ p.line(x,0, Math.min(x+dash,dist), 0); x += dash+gap; }
        p.pop();
      }
    }

    function drawHomoLumo(){
      const w=p.width, h=p.height;
      p.textAlign(p.CENTER, p.CENTER);
      p.textSize(16);

      const leftX = w*0.33, rightX = w*0.67;
      const baseY = h*0.62;
      const spacing = 60;

      // ERG/EWG shifting
      const shift = p.map(state.ergEwg, 0,100, 0, 70);
      let dieneShiftY = -shift, dienophileShiftY = +shift;
      let leftLabel = "diene", rightLabel = "dienophile";
      let extraLeft = "", extraRight = "";
      if(state.inverse){
        // Swap effects: diene EWG (lower), dienophile ERG (higher)
        dieneShiftY = +shift;
        dienophileShiftY = -shift;
        extraLeft = " EWG";
        extraRight = " ERG";
      }

      // Draw labels
      p.noStroke(); p.fill(0);
      p.text(leftLabel + extraLeft, leftX, h*0.25);
      p.text(rightLabel + extraRight, rightX, h*0.25);

      // Energy levels: LUMO above HOMO
      const leftHOMO = baseY + dieneShiftY;
      const leftLUMO = baseY - spacing + dieneShiftY;
      const rightHOMO = baseY + dienophileShiftY - spacing;
      const rightLUMO = baseY - 2*spacing + dienophileShiftY;

      // axes invisible; just horizontal lines
      p.stroke(0); p.strokeWeight(2);
      drawLevel(leftX, leftHOMO, "HOMO", true);
      drawLevel(leftX, leftLUMO, "LUMO", false);

      drawLevel(rightX, rightHOMO, "HOMO", true);
      drawLevel(rightX, rightLUMO, "LUMO", false);

      // Interactions: arrow from left HOMO to right LUMO and from right HOMO to left LUMO
      const gap1 = Math.abs(leftHOMO - rightLUMO);
      const gap2 = Math.abs(rightHOMO - leftLUMO);
      // Dominant interaction has smaller gap
      const dom1 = gap1 < gap2; // if true, left->right is dominant
      const thick1 = p.map(gap1, 20, 180, 4, 1.2);
      const thick2 = p.map(gap2, 20, 180, 4, 1.2);
      if(dom1){
        drawDashedArrow(leftX+40, leftHOMO, rightX-40, rightLUMO, 10,6, thick1, [255,0,0,220]);
        drawDashedArrow(rightX-40, rightHOMO, leftX+40, leftLUMO, 10,6, 1.2, [0,0,0,150]);
      }else{
        drawDashedArrow(rightX-40, rightHOMO, leftX+40, leftLUMO, 10,6, thick2, [255,0,0,220]);
        drawDashedArrow(leftX+40, leftHOMO, rightX-40, rightLUMO, 10,6, 1.2, [0,0,0,150]);
      }

      function drawLevel(x, y, label, electrons){
        p.line(x-60, y, x+60, y);
        p.noStroke(); p.fill(0);
        p.textSize(13); p.text(label, x-80, y-2);
        // electrons as short "up-down" pair
        if(electrons){
          p.stroke(0); p.strokeWeight(2);
          p.line(x-10, y-10, x-10, y+10);
          p.line(x+10, y-10, x+10, y+10);
        }
      }
    }
  };

  new p5(sketch, document.getElementById('canvas-container'));
})();
</script>
</body>
</html>
