<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>A Visual Proof of Viviani's Theorem</title>
    <!-- Include p5.js from CDN -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        :root{
            --panel-bg:#f7f7f7;
            --border:#d9d9d9;
        }
        body{
            margin:0;
            font-family:system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
            background:#ffffff;
            color:#111;
        }
        #demo-container{
            width: 660px;
            margin: 16px auto;
            border:1px solid var(--border);
            border-radius:8px;
            box-shadow:0 1px 2px rgba(0,0,0,0.05);
            overflow:hidden;
        }
        #control-panel{
            background:var(--panel-bg);
            padding:10px 14px;
            border-bottom:1px solid var(--border);
            display:flex;
            align-items:center;
            gap:6px;
            font-size:14px;
        }
        #canvas-container{
            padding:8px;
        }
        #control-panel input[type="radio"]{
            transform: translateY(1px);
        }
        canvas{
            display:block;
            border:1px solid #e5e5e5;
            border-radius:6px;
            background:#fff;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        // --- Global geometry state ---
        let W = 600, H = 520;
        let A, B, C, M; // vertices and midpoint of AB
        let P;          // draggable point
        let currentStep = "1";
        let dragging = false;
        const PR = 10; // radius for dragging
        const BLUE = [30, 120, 255];
        const RED  = [255, 0, 0];
        const GREEN= [0, 180, 0];

        // --- Utility: vectors ---
        function vec(x, y){ return new p5.Vector(x, y); }
        function clone(v){ return createVector(v.x, v.y); }

        function unit(v){
            let m = v.mag();
            if(m === 0) return createVector(0,0);
            return createVector(v.x/m, v.y/m);
        }
        function add(a,b){ return createVector(a.x+b.x, a.y+b.y); }
        function sub(a,b){ return createVector(a.x-b.x, a.y-b.y); }
        function mul(v, s){ return createVector(v.x*s, v.y*s); }

        // Signed area helper for point-in-triangle
        function triSign(p1, p2, p3){
            return (p1.x - p3.x)*(p2.y - p3.y) - (p2.x - p3.x)*(p1.y - p3.y);
        }
        function pointInTriangle(p, a, b, c){
            const d1 = triSign(p, a, b);
            const d2 = triSign(p, b, c);
            const d3 = triSign(p, c, a);
            const has_neg = (d1 < 0) || (d2 < 0) || (d3 < 0);
            const has_pos = (d1 > 0) || (d2 > 0) || (d3 > 0);
            return !(has_neg && has_pos); // inside or on edge
        }

        // Projection (nearest point) of P onto segment AB
        function projectToSegment(p, a, b){
            const ab = sub(b, a);
            const ap = sub(p, a);
            const ab2 = ab.x*ab.x + ab.y*ab.y;
            let t = (ap.x*ab.x + ap.y*ab.y) / ab2;
            t = constrain(t, 0, 1);
            return add(a, mul(ab, t));
        }

        // Clamp point inside triangle by snapping to closest point on perimeter
        function clampToTriangle(p, a, b, c){
            if(pointInTriangle(p, a, b, c)) return clone(p);
            const f1 = projectToSegment(p, a, b);
            const f2 = projectToSegment(p, b, c);
            const f3 = projectToSegment(p, c, a);
            let d1 = p5.Vector.dist(p, f1);
            let d2 = p5.Vector.dist(p, f2);
            let d3 = p5.Vector.dist(p, f3);
            let m = Math.min(d1, d2, d3);
            if(m === d1) return f1;
            if(m === d2) return f2;
            return f3;
        }

        // Draw dashed line helper
        function dashedLine(x1,y1,x2,y2, dash=[6,6], col=[120,170,255], sw=1.5){
            push();
            drawingContext.setLineDash(dash);
            stroke(col[0], col[1], col[2], 170);
            strokeWeight(sw);
            line(x1,y1,x2,y2);
            drawingContext.setLineDash([]);
            pop();
        }

        // Draw the main triangle and labels
        function drawMainTriangle(){
            stroke(0);
            strokeWeight(2);
            noFill();
            line(A.x,A.y,B.x,B.y);
            line(B.x,B.y,C.x,C.y);
            line(C.x,C.y,A.x,A.y);

            // Labels
            noStroke(); fill(0); textSize(14);
            textAlign(CENTER, BOTTOM); text("C", C.x, C.y - 8);
            textAlign(RIGHT, TOP); text("A", A.x - 6, A.y + 4);
            textAlign(LEFT, TOP); text("B", B.x + 6, B.y + 4);

            // Main altitude (dashed)
            dashedLine(M.x, A.y, M.x, C.y, [6,6], [120,170,255], 1.5);
        }

        // Draw draggable point marker
        function drawPMarker(greyed=false){
            push();
            const baseAlpha = greyed ? 120 : 255;
            const edgeAlpha = greyed ? 120 : 220;
            stroke(20,20,20, edgeAlpha);
            strokeWeight(1.5);
            fill(220,220,220, baseAlpha);
            circle(P.x, P.y, PR*2);
            // cross
            line(P.x-6, P.y-0, P.x+6, P.y-0);
            line(P.x, P.y-6, P.x, P.y+6);
            // label P
            noStroke(); fill(200,30,30, baseAlpha);
            textSize(14); textAlign(LEFT, CENTER);
            text("P", P.x - 14, P.y + 12);
            pop();
        }

        // Compute feet and distances from P to each side
        function perpendicularInfo(){
            const fAB = projectToSegment(P, A, B);
            const fBC = projectToSegment(P, B, C);
            const fAC = projectToSegment(P, C, A);
            const dAB = p5.Vector.dist(P, fAB);
            const dBC = p5.Vector.dist(P, fBC);
            const dAC = p5.Vector.dist(P, fAC);
            return { fAB, fBC, fAC, dAB, dBC, dAC };
        }

        // Draw small filled equilateral triangle given apex, base center, base direction
        function drawSmallEqTriangle(apex, baseCenter, baseDirUnit, col){
            const d = p5.Vector.dist(apex, baseCenter); // altitude
            const side = 2 * d / Math.sqrt(3);
            const half = side/2;
            const p1 = add(baseCenter, mul(baseDirUnit,  half));
            const p2 = add(baseCenter, mul(baseDirUnit, -half));

            // fill and outline
            push();
            stroke(0);
            strokeWeight(1.5);
            fill(col[0], col[1], col[2], 120);
            triangle(apex.x, apex.y, p1.x, p1.y, p2.x, p2.y);
            pop();

            // altitude inside
            push();
            stroke(0,0,0,150);
            strokeWeight(1);
            line(apex.x, apex.y, baseCenter.x, baseCenter.y);
            pop();

            return {p1, p2, baseCenter, altitude:d};
        }

        // Stack triangles along the main altitude (Step 3)
        function drawStackedTriangles(info){
            const {dAB, dBC, dAC} = info;
            const axis = unit(sub(C, M));       // direction from base midpoint to C
            const uAB  = unit(sub(B, A));       // base direction (horizontal)
            const baseY = A.y;                  // AB line
            // Red (base on AB)
            const baseCenterRed = clone(M);
            const apexRed = add(baseCenterRed, mul(axis, dAB));
            drawSmallEqTriangle(apexRed, baseCenterRed, uAB, RED);

            // Blue stacked on top of red
            const baseCenterBlue = add(M, mul(axis, dAB));
            const apexBlue = add(M, mul(axis, dAB + dBC));
            drawSmallEqTriangle(apexBlue, baseCenterBlue, uAB, [0,0,255]);

            // Label 'y' at blue apex
            noStroke(); fill(0); textSize(14); textAlign(LEFT, CENTER);
            text("y", apexBlue.x + 6, apexBlue.y - 8);

            // Green stacked on top of blue
            const baseCenterGreen = add(M, mul(axis, dAB + dBC));
            const apexGreen = add(M, mul(axis, dAB + dBC + dAC));
            drawSmallEqTriangle(apexGreen, baseCenterGreen, uAB, [0,200,0]);

            // Expect apexGreen to coincide with C; draw subtle dashed to show alignment
            dashedLine(apexGreen.x, apexGreen.y, C.x, C.y, [2,4], [0,180,0], 1);
        }

        // Step renderers
        function renderStep1(info){
            // Draw perpendiculars and feet
            push();
            stroke(0); strokeWeight(2);
            line(P.x, P.y, info.fAB.x, info.fAB.y);
            line(P.x, P.y, info.fBC.x, info.fBC.y);
            line(P.x, P.y, info.fAC.x, info.fAC.y);
            pop();

            // feet dots
            push();
            noStroke(); fill(BLUE[0],BLUE[1],BLUE[2]);
            circle(info.fAB.x, info.fAB.y, 8);
            circle(info.fBC.x, info.fBC.y, 8);
            circle(info.fAC.x, info.fAC.y, 8);
            pop();

            // draggable marker
            drawPMarker(false);
        }

        function renderStep2(info){
            // Base directions along triangle sides
            const uAB = unit(sub(B, A));
            const uBC = unit(sub(C, B));
            const uAC = unit(sub(A, C));

            // Base centers are feet
            drawSmallEqTriangle(P, info.fAB, uAB, RED);
            const triGreen = drawSmallEqTriangle(P, info.fAC, uAC, [0,200,0]);
            const triBlue  = drawSmallEqTriangle(P, info.fBC, uBC, [0,0,255]);

            // label 'x' and 'y' near the feet on AC and BC
            noStroke(); fill(0); textSize(14);
            textAlign(RIGHT, BOTTOM);
            text("x", info.fAC.x - 6, info.fAC.y - 6);
            textAlign(LEFT, TOP);
            text("y", info.fBC.x + 6, info.fBC.y + 6);

            // Also show horizontal through P for reference (like snapshot)
            push();
            stroke(0,0,0,120); strokeWeight(1);
            line(A.x+1, P.y, B.x-1, P.y);
            pop();

            drawPMarker(false);
        }

        function renderStep3(info){
            drawStackedTriangles(info);
            drawPMarker(true); // ghosted original location
        }

        // --- p5.js lifecycle ---
        function setup(){
            const c = createCanvas(W, H);
            c.parent('canvas-container');

            // Define main equilateral triangle inside the canvas
            const margin = 80;
            const baseLen = 460;
            const baseY = H - 60;
            const h = baseLen * Math.sqrt(3) / 2;

            A = vec(margin, baseY);
            B = vec(margin + baseLen, baseY);
            M = vec((A.x + B.x)/2, (A.y + B.y)/2);
            C = vec((A.x + B.x)/2, baseY - h);

            // Initial P
            P = vec(width*0.4, height*0.6);
            P = clampToTriangle(P, A, B, C);

            // Radio listeners
            document.querySelectorAll('input[name="step-selector"]').forEach(r=>{
                r.addEventListener('change', (e)=>{
                    currentStep = e.target.value;
                });
            });
        }

        function draw(){
            background(255);

            const info = perpendicularInfo();

            // Common: Main triangle and altitude
            drawMainTriangle();

            // Step-specific drawing
            if(currentStep === "1"){
                renderStep1(info);
            }else if(currentStep === "2"){
                renderStep2(info);
            }else{
                renderStep3(info);
            }
        }

        function mousePressed(){
            if(mouseX < 0 || mouseX > width || mouseY < 0 || mouseY > height) return;
            const d = dist(mouseX, mouseY, P.x, P.y);
            if(d <= PR + 4) dragging = true;
        }
        function mouseDragged(){
            if(!dragging) return;
            const candidate = vec(mouseX, mouseY);
            P = clampToTriangle(candidate, A, B, C);
        }
        function mouseReleased(){
            dragging = false;
        }
    </script>
</body>
</html>
