<script>
(() => {
  // -----------------------
  // Globals and constants
  // -----------------------
  const DAY_MS = 24 * 60 * 60 * 1000;
  const slider = document.getElementById('time-slider');
  const dateDisplay = document.getElementById('date-display');
  const playPauseBtn = document.getElementById('play-pause-button');
  const selector = document.getElementById('dwarf-planet-selector');

  // Base dates
  const baseStartUTC = new Date(Date.UTC(2000, 0, 1, 0, 0, 0)); // for slider display
  const j2000UTC = new Date(Date.UTC(2000, 0, 1, 12, 0, 0));    // J2000 epoch

  let isAnimating = false;
  let rafId = null;

  let selectedDwarfPlanet = 'Pluto';

  // Orbital elements (at J2000). Degrees where specified.
  const orbitalElements = {
    'Mercury': { a: 0.3871, e: 0.2056, i: 7.005,  Omega: 48.331,  omega: 29.124,  M0: 174.796 },
    'Venus'  : { a: 0.7233, e: 0.0068, i: 3.395,  Omega: 76.680,  omega: 54.884,  M0: 50.416  },
    'Earth'  : { a: 1.0000, e: 0.0167, i: 0.000,  Omega: -11.260, omega: 114.208, M0: 358.617 },
    'Mars'   : { a: 1.5237, e: 0.0934, i: 1.850,  Omega: 49.562,  omega: 286.502, M0: 19.412  },
    'Ceres'  : { a: 2.7691, e: 0.0760, i: 10.593, Omega: 80.393,  omega: 73.598,  M0: 77.372  },
    'Pluto'  : { a: 39.482, e: 0.2488, i: 17.160, Omega: 110.299, omega: 113.834, M0: 14.882  },
    'Haumea' : { a: 43.132, e: 0.1912, i: 28.220, Omega: 122.155, omega: 239.544, M0: 204.03  },
    'Makemake':{ a: 45.791, e: 0.1559, i: 29.006, Omega: 79.623,  omega: 294.821, M0: 95.895  },
    'Eris'   : { a: 67.668, e: 0.4418, i: 44.187, Omega: 35.951,  omega: 151.291, M0: 206.51  }
  };

  // planet colors
  const colors = {
    Sun: 0xffdd55,
    Mercury: 0xaaaaaa,
    Venus: 0xe0c39d,
    Earth: 0x2b6cff,
    Mars: 0xd2553d,
    Dwarf: 0x5b2a86
  };

  // -----------------------
  // three.js setup
  // -----------------------
  const canvas = document.getElementById('3d-canvas');
  const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));

  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xf3f3f6);

  const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 2000);
  camera.position.set(0, 15, 30);

  const controls = new THREE.OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.dampingFactor = 0.08;
  controls.minDistance = 1;
  controls.maxDistance = 800;

  // Lighting
  const ambient = new THREE.AmbientLight(0xffffff, 0.55);
  scene.add(ambient);
  const sunLight = new THREE.PointLight(0xffffff, 1.2, 0, 2);
  sunLight.position.set(0, 0, 0);
  scene.add(sunLight);

  // Sun
  const sunGeom = new THREE.SphereGeometry(0.4, 32, 32); // ~0.5 AU visibility
  const sunMat = new THREE.MeshBasicMaterial({ color: colors.Sun });
  const sunMesh = new THREE.Mesh(sunGeom, sunMat);
  sunMesh.position.set(0, 0, 0);
  scene.add(sunMesh);

  // Data structures for bodies
  const innerPlanets = ['Mercury', 'Venus', 'Earth', 'Mars'];
  const planetMeshes = {};
  const planetOrbits = {};

  // Create inner planet orbits and meshes
  const planetRadii = { Mercury: 0.06, Venus: 0.08, Earth: 0.09, Mars: 0.08 };

  innerPlanets.forEach(name => {
    const mesh = new THREE.Mesh(
      new THREE.SphereGeometry(planetRadii[name], 24, 24),
      new THREE.MeshLambertMaterial({ color: colors[name] })
    );
    planetMeshes[name] = mesh;
    scene.add(mesh);

    const orbitLine = createOrbitLine(orbitalElements[name], 0x2952a3); // blue
    planetOrbits[name] = orbitLine;
    scene.add(orbitLine);
  });

  // Dwarf planet current objects
  let dwarfMesh = null;
  let dwarfOrbitLine = null;

  function initDwarfObjects(name) {
    if (dwarfMesh) scene.remove(dwarfMesh);
    if (dwarfOrbitLine) scene.remove(dwarfOrbitLine);

    const mesh = new THREE.Mesh(
      new THREE.SphereGeometry(0.12, 24, 24),
      new THREE.MeshLambertMaterial({ color: colors.Dwarf })
    );
    dwarfMesh = mesh;
    scene.add(dwarfMesh);

    dwarfOrbitLine = createOrbitLine(orbitalElements[name], 0xd22e2e); // red
    scene.add(dwarfOrbitLine);
  }

  // -----------------------
  // Orbit rendering helper
  // -----------------------
  function createOrbitLine(elements, colorHex) {
    const a = elements.a;
    const e = elements.e;
    const i = THREE.MathUtils.degToRad(elements.i);
    const Omega = THREE.MathUtils.degToRad(elements.Omega);
    const omega = THREE.MathUtils.degToRad(elements.omega);

    // Construct 2D ellipse in orbital plane (focus at origin):
    // Use EllipseCurve centered at (-a e, 0) so that one focus lies at (0, 0).
    const b = a * Math.sqrt(1 - e * e);
    const ellipse = new THREE.EllipseCurve(-a * e, 0, a, b, 0, Math.PI * 2, false, 0);
    const pts2 = ellipse.getPoints(400);

    // Convert to Vector3 list
    const pts3 = pts2.map(p => new THREE.Vector3(p.x, p.y, 0));
    const geom = new THREE.BufferGeometry().setFromPoints(pts3);

    // Rotate from orbital plane to ecliptic: Rz(Omega) * Rx(i) * Rz(omega)
    geom.applyMatrix4(new THREE.Matrix4().makeRotationZ(omega));
    geom.applyMatrix4(new THREE.Matrix4().makeRotationX(i));
    geom.applyMatrix4(new THREE.Matrix4().makeRotationZ(Omega));

    const mat = new THREE.LineBasicMaterial({ color: colorHex, transparent: true, opacity: 0.9 });
    const line = new THREE.Line(geom, mat);
    return line;
  }

  // -----------------------
  // Orbital mechanics
  // -----------------------
  function calculatePosition(dateUTC, elems) {
    // Days since J2000 (2000-01-01 12:00 UTC)
    const d = (dateUTC - j2000UTC) / DAY_MS;

    const a = elems.a;
    const e = elems.e;
    const i = THREE.MathUtils.degToRad(elems.i);
    const Omega = THREE.MathUtils.degToRad(elems.Omega);
    const omega = THREE.MathUtils.degToRad(elems.omega);

    // Orbital period (in days) using Kepler's third law (P in years ~ a^(3/2))
    const P = 365.25 * Math.pow(a, 1.5);

    // Mean anomaly in radians
    let Mdeg = elems.M0 + (360 / P) * d;
    Mdeg = ((Mdeg % 360) + 360) % 360; // normalize
    const M = THREE.MathUtils.degToRad(Mdeg);

    // Solve Kepler's equation for E (radians) using Newton-Raphson
    let E = M + e * Math.sin(M) + 0.5 * e * e * Math.sin(2 * M); // good initial guess
    for (let k = 0; k < 12; k++) {
      const f = E - e * Math.sin(E) - M;
      const fp = 1 - e * Math.cos(E);
      E = E - f / fp;
    }

    // True anomaly ν and radius r
    const cosE = Math.cos(E);
    const sinE = Math.sin(E);
    const r = a * (1 - e * cosE);
    const nu = 2 * Math.atan2(Math.sqrt(1 + e) * Math.sin(E / 2), Math.sqrt(1 - e) * Math.cos(E / 2));

    // Position in ecliptic coordinates (rotation by Ω, i, ω)
    const cosO = Math.cos(Omega), sinO = Math.sin(Omega);
    const cosi = Math.cos(i), sini = Math.sin(i);
    const coswNu = Math.cos(omega + nu);
    const sinwNu = Math.sin(omega + nu);

    const x = r * (cosO * coswNu - sinO * sinwNu * cosi);
    const y = r * (sinO * coswNu + cosO * sinwNu * cosi);
    const z = r * (sinwNu * sini);

    return new THREE.Vector3(x, y, z);
  }

  // -----------------------
  // Date / time helpers
  // -----------------------
  const DAY_NAMES = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];
  const MONTH_NAMES = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];

  function dateFromSliderValue(valueDays) {
    return new Date(baseStartUTC.getTime() + valueDays * DAY_MS); // UTC midnight offset
    // Note: display uses this date at 00:00:00 UTC.
  }

  function formatUTCDate(d) {
    const dow = DAY_NAMES[d.getUTCDay()];
    const day = d.getUTCDate();
    const mon = MONTH_NAMES[d.getUTCMonth()];
    const yr = d.getUTCFullYear();
    return `${dow} ${day} ${mon} ${yr} 00:00:00`;
  }

  function updateDateDisplay() {
    const d = dateFromSliderValue(Number(slider.value));
    dateDisplay.textContent = formatUTCDate(d);
  }

  // -----------------------
  // Positions update and rendering
  // -----------------------
  function updateAllPositions() {
    const currentDateUTC = dateFromSliderValue(Number(slider.value)); // 00:00:00 UTC

    innerPlanets.forEach(name => {
      const pos = calculatePosition(currentDateUTC, orbitalElements[name]);
      planetMeshes[name].position.copy(pos);
    });

    if (dwarfMesh && selectedDwarfPlanet) {
      const posD = calculatePosition(currentDateUTC, orbitalElements[selectedDwarfPlanet]);
      dwarfMesh.position.copy(posD);
    }
  }

  function render() {
    controls.update();
    renderer.render(scene, camera);
  }

  function refresh() {
    updateDateDisplay();
    updateAllPositions();
    render();
  }

  // -----------------------
  // Event wiring
  // -----------------------
  selector.addEventListener('click', (e) => {
    const b = e.target.closest('button[data-planet]');
    if (!b) return;

    // Toggle active class
    selector.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
    b.classList.add('active');

    // Update selected and rebuild dwarf orbit/body
    selectedDwarfPlanet = b.getAttribute('data-planet');
    initDwarfObjects(selectedDwarfPlanet);

    // Set position for current date
    updateAllPositions();
    render();
  });

  slider.addEventListener('input', () => {
    refresh();
  });

  playPauseBtn.addEventListener('click', () => {
    isAnimating = !isAnimating;
    if (isAnimating) {
      playPauseBtn.textContent = '⏸';
      startAnimation();
    } else {
      playPauseBtn.textContent = '▶';
      if (rafId) cancelAnimationFrame(rafId);
      rafId = null;
    }
  });

  function startAnimation() {
    let lastTime = performance.now();
    const stepPerFrame = 2; // days per animation frame step

    function tick(now) {
      if (!isAnimating) return;
      // simple frame-capped increment (independent of dt for steadier visuals)
      let v = Number(slider.value);
      v += stepPerFrame;
      if (v > Number(slider.max)) v = Number(slider.min);
      slider.value = String(v);
      refresh();

      lastTime = now;
      rafId = requestAnimationFrame(tick);
    }
    rafId = requestAnimationFrame(tick);
  }

  // Resize handling
  function onResize() {
    const rect = document.getElementById('visualization-container').getBoundingClientRect();
    const w = Math.max(300, rect.width);
    const h = Math.max(260, rect.height);
    renderer.setSize(w, h, false);
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
    render();
  }
  window.addEventListener('resize', onResize);

  // -----------------------
  // Initialization
  // -----------------------
  initDwarfObjects(selectedDwarfPlanet);
  onResize();
  refresh(); // set initial date text and positions

})();
</script>
